// ==================== MODAL DE PESO DOMINICAL ====================

function checkSundayWeightModal() {
  const today = new Date();
  const dayOfWeek = today.getDay(); // 0 = Domingo
  
  // Só executa aos domingos
  if (dayOfWeek !== 0) return;
  
  const todayStr = today.toISOString().split('T')[0]; // Formato: YYYY-MM-DD
  
  // Verifica se o usuário já clicou em "Não marcar hoje"
  const skippedDate = localStorage.getItem('sundayWeightSkipped');
  if (skippedDate === todayStr) return;
  
  // Verifica se já registrou peso hoje
  const alreadyRegistered = weightHistory.some(record => {
    const recordDate = new Date(record.date).toISOString().split('T')[0];
    return recordDate === todayStr;
  });
  
  if (alreadyRegistered) return;
  
  // Se passou por todas as verificações, abre o modal
  openSundayWeightModal();
}

function openSundayWeightModal() {
  const modal = document.getElementById('sundayWeightModal');
  if (!modal) return;
  
  // Preenche com os últimos valores salvos
  const lastWeight = localStorage.getItem('lastWeight') || '';
  const lastChest = localStorage.getItem('lastChest') || '';
  const lastAbs = localStorage.getItem('lastAbs') || '';
  const lastThigh = localStorage.getItem('lastThigh') || '';
  const lastAge = localStorage.getItem('userAge') || '';
  
  document.getElementById('sundayWeight').value = lastWeight;
  document.getElementById('sundayChest').value = lastChest;
  document.getElementById('sundayAbs').value = lastAbs;
  document.getElementById('sundayThigh').value = lastThigh;
  document.getElementById('sundayAge').value = lastAge;
  
  // Abre o modal
  modal.classList.add('active');
}

function closeSundayWeightModal() {
  const modal = document.getElementById('sundayWeightModal');
  if (modal) modal.classList.remove('active');
}

function saveSundayWeight() {
  const weight = parseFloat(document.getElementById('sundayWeight').value.replace(',', '.'));
  const chest = parseFloat(document.getElementById('sundayChest').value) || null;
  const abs = parseFloat(document.getElementById('sundayAbs').value) || null;
  const thigh = parseFloat(document.getElementById('sundayThigh').value) || null;
  const age = parseInt(document.getElementById('sundayAge').value) || 25;
  
  // Validação
  if (!weight || weight <= 0) {
    showToast('❌ Digite um peso válido!');
    return;
  }
  
  // Cria o registro
  const record = {
    id: Date.now(),
    date: new Date().toISOString(),
    weight: weight,
    bf: null,
    folds: null
  };
  
  // Calcula BF se tiver as 3 dobras
  if (chest && abs && thigh) {
    record.bf = calculateBF(chest, abs, thigh, age);
    record.folds = { chest: chest, abs: abs, thigh: thigh, age: age };
    
    // Salva no localStorage para preenchimento futuro
    localStorage.setItem('lastChest', chest);
    localStorage.setItem('lastAbs', abs);
    localStorage.setItem('lastThigh', thigh);
  }
  
  // Salva peso e idade
  localStorage.setItem('lastWeight', weight);
  localStorage.setItem('userAge', age);
  
  // Adiciona ao histórico
  weightHistory.unshift(record);
  saveData();
  
  // Atualiza a aba de peso
  renderWeightHistory();
  renderWeightChart();
  updateWeightGoalProgress();
  
  // Atualiza o input da aba peso também (para manter sincronizado)
  const weightInput = document.getElementById('weightInput');
  if (weightInput) weightInput.value = weight;
  
  calculateIMC(weight);
  
  // Fecha o modal
  closeSundayWeightModal();
  
  // Feedback
  let msg = '✅ Peso registrado!';
  if (record.bf) msg += ` (BF: ${record.bf}%)`;
  showToast(msg);
}

function postponeSundayWeight() {
  // Apenas fecha o modal, não salva nada
  // Na próxima vez que abrir o app, vai aparecer de novo
  closeSundayWeightModal();
  showToast('⏰ Ok! Vou lembrar mais tarde.');
}

function skipSundayWeight() {
  // Marca que o usuário não quer registrar hoje
  const todayStr = new Date().toISOString().split('T')[0];
  localStorage.setItem('sundayWeightSkipped', todayStr);
  
  closeSundayWeightModal();
  showToast('👍 Tudo bem! Não vou perguntar mais hoje.');
}

function loadData() {
  try {
    const savedHistory = localStorage.getItem('workoutHistory');
    const savedWeight = localStorage.getItem('weightHistory');
    const savedCounter = localStorage.getItem('counterHistory');
    const savedAbault = localStorage.getItem('abaultData'); // ADICIONAR
    
    if (savedHistory) workoutHistory = JSON.parse(savedHistory);
    if (savedWeight) weightHistory = JSON.parse(savedWeight);
    if (savedCounter) counterHistory = JSON.parse(savedCounter);
    if (savedAbault) abaultData = JSON.parse(savedAbault); // ADICIONAR
    
    activeProgram = localStorage.getItem('activeProgram') || null;
  } catch (e) {
    console.error('Erro ao carregar dados:', e);
  }
}

function saveData() {
  try {
    localStorage.setItem('workoutHistory', JSON.stringify(workoutHistory));
    localStorage.setItem('weightHistory', JSON.stringify(weightHistory));
  } catch (e) {
    console.error('Erro ao salvar dados:', e);
  }
}

function saveCounterData() {
  localStorage.setItem('counterHistory', JSON.stringify(counterHistory));
}

