function updateDateDisplay() {
  const now = new Date();
  const options = { weekday: 'long', year: 'numeric', month: 'long', day: 'numeric' };
  
  const dateEl = document.getElementById('currentDate');
  const dayEl = document.getElementById('currentDay');
  
  if (dateEl) {
    // Capitaliza a primeira letra da data
    let dateStr = now.toLocaleDateString('pt-BR', options);
    dateStr = dateStr.charAt(0).toUpperCase() + dateStr.slice(1);
    dateEl.textContent = dateStr;
  }
  
if (dayEl) {
  const workoutData = getWorkoutForDay(currentDayIndex);
  // USA O NOME SUBSTITUÍDO SE EXISTIR
  const workoutName = overriddenWorkoutName || (workoutData ? workoutData.name : "Treino");
    
    const todayIndex = now.getDay(); // 0-6 (Domingo-Sábado)
    
    // Se o dia selecionado na tela for igual ao dia real de hoje
    if (currentDayIndex === todayIndex) {
      dayEl.innerHTML = workoutName + ' <span style="font-size: 0.7em; opacity: 0.9; background: var(--success); color: white; padding: 2px 8px; border-radius: 12px; margin-left: 6px; vertical-align: middle;">Hoje</span>';
      dayEl.style.color = 'var(--success)';
    } else {
      dayEl.textContent = workoutName;
      dayEl.style.color = 'var(--primary)';
    }
  }

  // (Opcional) Lembrete de Backup aos Domingos
  const existingReminder = document.getElementById('backupReminder');
  if (existingReminder) existingReminder.remove();

  const lastBackup = localStorage.getItem('lastBackupDate');
  const todayStr = new Date().toLocaleDateString();

  if (currentDayIndex === 0 && lastBackup !== todayStr) { 
    const reminderDiv = document.createElement('div');
    reminderDiv.id = 'backupReminder';
    reminderDiv.innerHTML = '⚠️ LEMBRETE: FAZER BACKUP';
    reminderDiv.style.cssText = 'color: #ef4444; font-weight: 800; font-size: 12px; margin-top: 5px; border: 1px dashed #ef4444; padding: 4px 8px; border-radius: 6px; display: inline-block;';
    
    const header = document.querySelector('.header');
    if (header) header.appendChild(reminderDiv);
  }
}

