// --- Lógica do Timer ---
let timerInterval = null;
let timerEndTime = null;

function startTimer(seconds) {
  stopTimer(); // Limpa timer anterior se houver
  
  const now = Date.now();
  timerEndTime = now + (seconds * 1000);
  
  updateTimerDisplay(seconds);
  
  // Feedback visual inicial
  document.getElementById('timerDisplay').style.color = 'var(--primary)';

  timerInterval = setInterval(() => {
    const timeLeft = Math.ceil((timerEndTime - Date.now()) / 1000);
    
    if (timeLeft <= 0) {
      stopTimer();
      finishTimer();
    } else {
      updateTimerDisplay(timeLeft);
    }
  }, 100);
}

function stopTimer() {
  if (timerInterval) clearInterval(timerInterval);
  timerInterval = null;
  updateTimerDisplay(0);
  document.getElementById('timerDisplay').style.color = 'var(--text)';
}

function finishTimer() {
  updateTimerDisplay(0);
  document.getElementById('timerDisplay').style.color = 'var(--success)';
  
  // Vibrar padrão de alarme (vibra, pausa, vibra...)
  if (navigator.vibrate) {
    navigator.vibrate([500, 200, 500, 200, 500]); 
  }
  
  // Inicia a sequência de bipes sonoros
  playAlarmSequence();
}

function playAlarmSequence() {
  // Toca 4 bipes seguidos com intervalo
  let count = 0;
  playBeep(); // Toca o primeiro imediatamente
  
  const interval = setInterval(() => {
    count++;
    if (count >= 4) { // Define quantos bipes vai dar (aqui são 1+4 = 5 bipes)
      clearInterval(interval);
    } else {
      playBeep();
    }
  }, 800); // Intervalo de 800ms entre cada bipe
}

function playBeep() {
  try {
    const AudioContext = window.AudioContext || window.webkitAudioContext;
    if (AudioContext) {
      const ctx = new AudioContext();
      const osc = ctx.createOscillator();
      const gain = ctx.createGain();

      osc.connect(gain);
      gain.connect(ctx.destination);

      // Mudei para 'square' que é um som mais "digital" e percebido como mais alto
      osc.type = 'square'; 
      osc.frequency.value = 880; // Frequência aguda
      gain.gain.value = 0.2; // Volume ajustado

      osc.start();
      setTimeout(() => osc.stop(), 400); // Duração do bipe aumentada para 400ms
    }
  } catch (e) { console.log('Audio error', e); }
}

function updateTimerDisplay(seconds) {
  const m = Math.floor(seconds / 60).toString().padStart(2, '0');
  const s = (seconds % 60).toString().padStart(2, '0');
  const display = document.getElementById('timerDisplay');
  if(display) display.textContent = `${m}:${s}`;
}



