function updateWeightGoalProgress() {
  const container = document.getElementById('weightGoalProgress');
  if (!container) return;
  
  if (!weightGoalValue || !weightGoalType || weightHistory.length === 0) {
    container.style.display = 'none';
    return;
  }
  
  container.style.display = 'block';
  
  const currentWeight = weightHistory[0].weight;
  const startWeight = weightHistory[weightHistory.length - 1].weight;
  
  // Calcula progresso
  let totalToLose, alreadyLost, remaining, pct;
  
  if (weightGoalType === 'lose') {
    totalToLose = startWeight - weightGoalValue;
    alreadyLost = startWeight - currentWeight;
    remaining = currentWeight - weightGoalValue;
  } else {
    totalToLose = weightGoalValue - startWeight;
    alreadyLost = currentWeight - startWeight;
    remaining = weightGoalValue - currentWeight;
  }
  
  // Evita divisão por zero e valores negativos
  if (totalToLose <= 0) {
    pct = remaining <= 0 ? 100 : 0;
  } else {
    pct = Math.max(0, Math.min(100, (alreadyLost / totalToLose) * 100));
  }
  
  // Atualiza UI
  const remainingText = remaining > 0 
    ? `${Math.abs(remaining).toFixed(1)} kg restantes`
    : `🎉 Meta atingida! (${Math.abs(remaining).toFixed(1)} kg além)`;
  
  document.getElementById('weightGoalRemaining').textContent = remainingText;
  document.getElementById('weightGoalRemaining').style.color = remaining <= 0 ? 'var(--success)' : 'var(--primary)';
  document.getElementById('weightGoalBar').style.width = pct + '%';
  document.getElementById('weightGoalStart').textContent = `Início: ${startWeight} kg`;
  document.getElementById('weightGoalTarget').textContent = `Meta: ${weightGoalValue} kg`;
  
  // Muda cor da barra se atingiu
  if (remaining <= 0) {
    document.getElementById('weightGoalBar').style.background = 'var(--success)';
  }
}

