// Ciclo de carboidratos
function renderCarbCycle() {
  const container = document.getElementById('carbCycleContent');
  const weeklyContainer = document.getElementById('weeklyPlanContent');
  
  if (!container || !calculatedDiet) return;
  
  const { carbs, fat, targetCalories, protein } = calculatedDiet;
  
  // Alto carb = +30%, Baixo carb = -30%
  const highCarb = Math.round(carbs * 1.3);
  const lowCarb = Math.round(carbs * 0.7);
  
  // Ajusta gordura inversamente
  const highCarbFat = Math.round(fat * 0.85);
  const lowCarbFat = Math.round(fat * 1.15);
  
  // Calorias aproximadas
  const highCarbCal = (protein * 4) + (highCarb * 4) + (highCarbFat * 9);
  const lowCarbCal = (protein * 4) + (lowCarb * 4) + (lowCarbFat * 9);
  
  container.innerHTML = `
    <div class="carb-cycle-container">
      <div class="carb-cycle-day high-carb">
        <div style="font-size:24px; margin-bottom:5px;">💪</div>
        <div style="font-size:14px; font-weight:700; color:var(--success);">Dia de Treino</div>
        <div style="font-size:11px; color:var(--text-muted); margin-bottom:10px;">Alto Carboidrato</div>
        <div style="font-size:20px; font-weight:700; color:var(--success);">${highCarb}g</div>
        <div style="font-size:10px; color:var(--text-muted);">carbs</div>
        <div style="margin-top:8px; font-size:11px; color:var(--text-muted);">
          P:${protein}g | G:${highCarbFat}g<br/>
          ~${Math.round(highCarbCal)} kcal
        </div>
      </div>
      <div class="carb-cycle-day low-carb">
        <div style="font-size:24px; margin-bottom:5px;">😴</div>
        <div style="font-size:14px; font-weight:700; color:var(--warning);">Dia de Descanso</div>
        <div style="font-size:11px; color:var(--text-muted); margin-bottom:10px;">Baixo Carboidrato</div>
        <div style="font-size:20px; font-weight:700; color:var(--warning);">${lowCarb}g</div>
        <div style="font-size:10px; color:var(--text-muted);">carbs</div>
        <div style="margin-top:8px; font-size:11px; color:var(--text-muted);">
          P:${protein}g | G:${lowCarbFat}g<br/>
          ~${Math.round(lowCarbCal)} kcal
        </div>
      </div>
    </div>
    
    <div style="margin-top:15px; padding:12px; background:var(--bg-input); border-radius:10px; font-size:11px; color:var(--text-muted);">
      💡 <strong>Por que ciclar?</strong> Dias de treino exigem mais energia (glicogênio). Em dias de descanso, o corpo precisa de menos carboidratos. A gordura extra ajuda na saciedade e produção hormonal.
    </div>
  `;
  
  // Plano semanal
  const days = ['Dom', 'Seg', 'Ter', 'Qua', 'Qui', 'Sex', 'Sáb'];
  const trainDays = [1, 2, 3, 5, 6]; // Exemplo: Seg, Ter, Qua, Sex, Sab
  
  weeklyContainer.innerHTML = `
    <div style="display:grid; grid-template-columns:repeat(7, 1fr); gap:5px; text-align:center;">
      ${days.map((day, idx) => {
        const isTrain = trainDays.includes(idx);
        const bg = isTrain ? 'rgba(34,197,94,0.2)' : 'rgba(245,158,11,0.2)';
        const color = isTrain ? 'var(--success)' : 'var(--warning)';
        const icon = isTrain ? '🏋️' : '😴';
        
        return `
          <div style="background:${bg}; padding:8px 4px; border-radius:6px;">
            <div style="font-size:10px; color:var(--text-muted);">${day}</div>
            <div style="font-size:14px;">${icon}</div>
            <div style="font-size:9px; color:${color}; font-weight:600;">${isTrain ? highCarb : lowCarb}g</div>
          </div>
        `;
      }).join('')}
    </div>
    <div style="margin-top:10px; text-align:center; font-size:10px; color:var(--text-muted);">
      Ajuste os dias de treino conforme sua rotina
    </div>
  `;
}

// Calculadora de água
function renderWaterCalculator() {
  const weight = parseFloat(document.getElementById('dietWeight').value) || 
                 parseFloat(localStorage.getItem('lastWeight')) || 70;
  const activity = parseFloat(document.getElementById('activityLevel').value) || 1.55;
  
  // Fórmula: 35ml por kg * fator de atividade
  let waterMl = weight * 35;
  if (activity >= 1.55) waterMl *= 1.2;
  if (activity >= 1.725) waterMl *= 1.1;
  
  const waterLiters = (waterMl / 1000).toFixed(1);
  const glasses = Math.ceil(waterMl / 250); // Copos de 250ml
  
  const litersEl = document.getElementById('waterGoalLiters');
  const glassesEl = document.getElementById('waterGlasses');
  
  if (litersEl) litersEl.textContent = waterLiters + ' L';
  
  if (glassesEl) {
    glassesEl.innerHTML = Array(Math.min(glasses, 12)).fill(0).map((_, i) => 
      `<span class="water-glass filled">💧</span>`
    ).join('');
    
    if (glasses > 12) {
      glassesEl.innerHTML += `<span style="font-size:12px; color:rgba(255,255,255,0.8);"> +${glasses - 12}</span>`;
    }
  }
}

// Salvar e compartilhar dieta
function saveDietResults() {
  if (!calculatedDiet) {
    showToast('❌ Calcule os macros primeiro!');
    return;
  }
  
  localStorage.setItem('savedDiet', JSON.stringify(calculatedDiet));
  loadSavedDiet();
  showToast('💾 Dieta salva!');
}

function loadSavedDiet() {
  const saved = localStorage.getItem('savedDiet');
  const card = document.getElementById('savedDietCard');
  
  if (saved && card) {
    const diet = JSON.parse(saved);
    
    document.getElementById('savedCalories').textContent = diet.targetCalories;
    document.getElementById('savedProtein').textContent = diet.protein + 'g';
    document.getElementById('savedCarbs').textContent = diet.carbs + 'g';
    document.getElementById('savedFat').textContent = diet.fat + 'g';
    
    card.style.display = 'block';
    
    // Também define como dieta calculada atual
    calculatedDiet = diet;
    
    // Se for uma dieta pré-pronta, marca ela como selecionada
    const presetId = localStorage.getItem('currentDietPreset');
    if (presetId && DIET_PRESETS[presetId]) {
      selectedDietPreset = presetId;
    }
  }
}

function clearSavedDiet() {
  if (confirm('Limpar dieta salva?')) {
    localStorage.removeItem('savedDiet');
    document.getElementById('savedDietCard').style.display = 'none';
    showToast('🗑️ Dieta removida');
  }
}

function shareDietResults() {
  if (!calculatedDiet) {
    showToast('❌ Calcule os macros primeiro!');
    return;
  }
  
  const d = calculatedDiet;
  const goalLabels = { cutting: '🔥 Cutting', manutencao: '⚖️ Manutenção', bulking: '💪 Bulking' };
  
  let text = `🥗 *MINHA DIETA*\n`;
  text += `━━━━━━━━━━━━━━━━━\n\n`;
  text += `📊 Objetivo: ${goalLabels[d.goal] || 'Personalizado'}\n`;
  text += `⚖️ Peso: ${d.weight}kg\n\n`;
  text += `🎯 *Meta Calórica: ${d.targetCalories} kcal*\n\n`;
  text += `🥩 Proteína: ${d.protein}g (${d.proteinPct}%)\n`;
  text += `🍚 Carboidrato: ${d.carbs}g (${d.carbsPct}%)\n`;
  text += `🥑 Gordura: ${d.fat}g (${d.fatPct}%)\n\n`;
  text += `━━━━━━━━━━━━━━━━━\n`;
  text += `📱 _Exercise Tracker_`;
  
  if (navigator.share) {
    navigator.share({ title: 'Minha Dieta', text: text })
      .catch(() => copyToClipboard(text));
  } else {
    copyToClipboard(text);
  }
}

// Inicialização
document.addEventListener('DOMContentLoaded', function() {
  // Carrega dieta salva
  setTimeout(loadSavedDiet, 500);
  
  // Observer para carregar dados quando entrar na aba
  const dietaSection = document.getElementById('dieta');
  if (dietaSection) {
    const observer = new MutationObserver(function(mutations) {
      mutations.forEach(function(mutation) {
        if (mutation.target.classList.contains('active')) {
          loadDietData();
          renderWaterCalculator();
        }
      });
    });
    observer.observe(dietaSection, { attributes: true, attributeFilter: ['class'] });
  }
});


