function loadFoodLog() {
    const date = document.getElementById('foodDate').value;
    const listDiv = document.getElementById('foodLogList');
    const countSpan = document.getElementById('foodLogCount');
    
    const dayData = foodHistory[date] || [];
    
    let totalKcal = 0, totalProt = 0, totalFat = 0, totalCarb = 0;
    
    if (countSpan) {
        countSpan.textContent = `${dayData.length} ${dayData.length === 1 ? 'item' : 'itens'}`;
    }
    
    if (dayData.length === 0) {
        listDiv.innerHTML = `
            <div style="text-align:center; padding:30px; color:var(--text-muted);">
                <div style="font-size:40px; margin-bottom:10px;">🍽️</div>
                <div style="font-size:13px;">Nenhum registro nesta data.</div>
                <div style="font-size:11px; margin-top:5px;">Use a busca ou seleção rápida acima.</div>
            </div>
        `;
    } else {
        listDiv.innerHTML = dayData.map((item) => {
            totalKcal += item.calorias;
            totalProt += item.proteina;
            totalFat += item.gordura;
            totalCarb += item.carboidrato;
            
            const isEditing = editingFoodLogItem && editingFoodLogItem.date === date && editingFoodLogItem.timestamp === item.timestamp;
            
            return `
                <div class="food-log-item ${isEditing ? 'editing' : ''} ${item.isCustomMeal ? 'customMeal-item' : ''}" id="food-log-${item.timestamp}">
                    <div class="food-log-header">
                        <div class="food-log-info">
                            <div class="food-log-name">${item.nome}</div>
                            <div class="food-log-macros">
                                P:${Math.round(item.proteina)}g • C:${Math.round(item.carboidrato)}g • G:${Math.round(item.gordura)}g
                            </div>
                        </div>
                        <div class="food-log-qty-display">
                            <span class="food-log-qty-text">${item.quantidade}${item.unidade} • ${Math.round(item.calorias)} kcal</span>
                        </div>
                        <div class="food-log-actions">
                            <button class="food-log-btn edit" onclick="toggleEditFoodLog('${date}', ${item.timestamp})" title="Editar">✏️</button>
                            <button class="food-log-btn delete" onclick="removeFoodLog('${date}', ${item.timestamp})" title="Remover">🗑️</button>
                        </div>
                    </div>
                    
                    <div class="food-log-edit-area">
                        <div style="font-size:11px; color:var(--text-muted); margin-bottom:8px;">Alterar quantidade:</div>
                        <div class="food-log-edit-row">
                            <div style="flex:1;">
                                <input type="number" 
                                       class="food-log-edit-input" 
                                       id="edit-qty-${item.timestamp}"
                                       value="${item.quantidade}"
                                       inputmode="decimal"
                                       oninput="previewEditFoodLog('${date}', ${item.timestamp}, '${item.id}')"
                                />
                                <div style="text-align:center; font-size:10px; color:var(--text-muted); margin-top:4px;">
                                    ${item.unidade === 'un' ? 'unidades' : item.unidade}
                                </div>
                            </div>
                            <div class="food-log-edit-btns">
                                <button class="save-btn" onclick="saveEditFoodLog('${date}', ${item.timestamp}, '${item.id}')">✅ Salvar</button>
                                <button class="cancel-btn" onclick="cancelEditFoodLog()">Cancelar</button>
                            </div>
                        </div>
                        <div class="food-log-edit-preview" id="edit-preview-${item.timestamp}">
                            <div><span class="value" style="color:var(--primary);">${Math.round(item.calorias)}</span><br/>kcal</div>
                            <div><span class="value" style="color:var(--danger);">${Math.round(item.proteina)}g</span><br/>prot</div>
                            <div><span class="value" style="color:var(--success);">${Math.round(item.carboidrato)}g</span><br/>carb</div>
                            <div><span class="value" style="color:var(--warning);">${Math.round(item.gordura)}g</span><br/>gord</div>
                        </div>
                    </div>
                </div>
            `;
        }).join('');
    }
    
    document.getElementById('dayKcal').textContent = Math.round(totalKcal);
    document.getElementById('dayProt').textContent = Math.round(totalProt) + 'g';
    document.getElementById('dayFat').textContent = Math.round(totalFat) + 'g';
    document.getElementById('dayCarbs').textContent = Math.round(totalCarb) + 'g';
    
    renderNutritionStats();
    renderMetasProgress();
    renderCaloriesTrendChart();
    renderRecentFoods();
}

// ==================== EDIÇÃO DE ITEM ====================

function toggleEditFoodLog(date, timestamp) {
    if (editingFoodLogItem && editingFoodLogItem.timestamp === timestamp) {
        // Se já está editando este item, fecha
        cancelEditFoodLog();
    } else {
        // Abre edição para este item
        editingFoodLogItem = { date, timestamp };
        loadFoodLog();
        
        // Foca no input
        setTimeout(() => {
            const input = document.getElementById(`edit-qty-${timestamp}`);
            if (input) {
                input.focus();
                input.select();
            }
        }, 100);
    }
}

function cancelEditFoodLog() {
    editingFoodLogItem = null;
    loadFoodLog();
}

function previewEditFoodLog(date, timestamp, foodId) {
    const input = document.getElementById(`edit-qty-${timestamp}`);
    const previewDiv = document.getElementById(`edit-preview-${timestamp}`);
    if (!input || !previewDiv) return;
    
    const newQty = parseFloat(input.value) || 0;
    const originalFood = trackcalFoodsDatabase.find(f => f.id === foodId);
    if (!originalFood) return;
    
    let multiplier = 1;
    if (originalFood.unidade === 'g' || originalFood.unidade === 'ml') {
        multiplier = newQty / 100; 
    } else {
        multiplier = newQty; 
    }
    
    const newKcal = Math.round(originalFood.calorias * multiplier);
    const newProt = Math.round(originalFood.proteina * multiplier);
    const newCarb = Math.round(originalFood.carboidrato * multiplier);
    const newFat = Math.round(originalFood.gordura * multiplier);
    
    previewDiv.innerHTML = `
        <div><span class="value" style="color:var(--primary);">${newKcal}</span><br/>kcal</div>
        <div><span class="value" style="color:var(--danger);">${newProt}g</span><br/>prot</div>
        <div><span class="value" style="color:var(--success);">${newCarb}g</span><br/>carb</div>
        <div><span class="value" style="color:var(--warning);">${newFat}g</span><br/>gord</div>
    `;
}

function saveEditFoodLog(date, timestamp, foodId) {
    const input = document.getElementById(`edit-qty-${timestamp}`);
    if (!input) return;
    
    const newQty = parseFloat(input.value);
    if (!newQty || newQty <= 0) {
        showToast('❌ Quantidade inválida');
        return;
    }
    
    const originalFood = trackcalFoodsDatabase.find(f => f.id === foodId);
    if (!originalFood) {
        showToast('❌ Alimento não encontrado');
        return;
    }
    
    // Encontra o item no histórico
    const dayData = foodHistory[date];
    if (!dayData) return;
    
    const itemIndex = dayData.findIndex(item => item.timestamp === timestamp);
    if (itemIndex === -1) return;
    
    // Recalcula os macros
    let multiplier = 1;
    if (originalFood.unidade === 'g' || originalFood.unidade === 'ml') {
        multiplier = newQty / 100; 
    } else {
        multiplier = newQty; 
    }
    
    // Atualiza o item
    foodHistory[date][itemIndex] = {
        ...foodHistory[date][itemIndex],
        quantidade: newQty,
        calorias: parseFloat((originalFood.calorias * multiplier).toFixed(2)),
        proteina: parseFloat((originalFood.proteina * multiplier).toFixed(2)),
        carboidrato: parseFloat((originalFood.carboidrato * multiplier).toFixed(2)),
        gordura: parseFloat((originalFood.gordura * multiplier).toFixed(2)),
        fibra: parseFloat((originalFood.fibra * multiplier).toFixed(2))
    };
    
    localStorage.setItem('foodHistory', JSON.stringify(foodHistory));
    
    editingFoodLogItem = null;
    loadFoodLog();
    showToast('✅ Quantidade atualizada!');
}

