function renderMeasureChart(key) {
    const container = document.getElementById('measureChartCanvas');
    const summaryStart = document.getElementById('mcStart');
    const summaryCurrent = document.getElementById('mcCurrent');
    const summaryDiff = document.getElementById('mcDiff');

    // 1. Prepara os dados
    // Filtra histórico para pegar apenas registros que tenham essa medida preenchida e válida
    // Ordena do mais antigo para o mais novo
    let data = measurementsHistory
        .filter(r => r[key] !== null && r[key] !== undefined && !isNaN(parseFloat(r[key])) && parseFloat(r[key]) > 0)
        .sort((a, b) => new Date(a.date) - new Date(b.date))
        .map(r => ({
            date: new Date(r.date).toLocaleDateString('pt-BR', {day:'2-digit', month:'2-digit'}),
            value: parseFloat(r[key])
        }));

    // Se a chave for peso, podemos pegar do histórico de peso também para ficar mais completo,
    // mas por simplicidade vamos usar o histórico de medidas aqui.
    
    if (data.length === 0) {
        container.innerHTML = '<div style="color:var(--text-muted); font-size:12px; align-self:center;">Sem dados para esta medida.</div>';
        summaryStart.textContent = '--'; summaryCurrent.textContent = '--'; summaryDiff.textContent = '--';
        return;
    }

    // 2. Atualiza Resumo
    const startVal = data[0].value;
    const currentVal = data[data.length - 1].value;
    const diff = currentVal - startVal;
    
    const unit = key === 'weight' ? 'kg' : 'cm';
    
    summaryStart.textContent = `${startVal}${unit}`;
    summaryCurrent.textContent = `${currentVal}${unit}`;
    
    const diffSign = diff > 0 ? '+' : '';
    const diffColor = diff > 0 ? 'var(--success)' : (diff < 0 ? 'var(--danger)' : 'var(--text)');
    
    // Contexto: Para cintura/abd, perder é bom (verde). Para bíceps, ganhar é bom (verde).
    // Vamos simplificar: Positivo = Verde, Negativo = Vermelho (exceto cintura/abs)
    let finalColor = diffColor;
    if (['waist', 'abs', 'hips'].includes(key)) {
         finalColor = diff < 0 ? 'var(--success)' : (diff > 0 ? 'var(--danger)' : 'var(--text)');
    }

    summaryDiff.innerHTML = `<span style="color:${finalColor}">${diffSign}${diff.toFixed(1)}${unit}</span>`;

    // 3. Desenha o SVG (Mesma lógica do peso, mas genérica)
    const width = container.clientWidth || 300; // Fallback se estiver oculto
    const height = 200;
    const padding = 20;

    // Achar Min e Max para escala Y
    const values = data.map(d => d.value);
    let minV = Math.min(...values);
    let maxV = Math.max(...values);
    
    // Margem para não colar no topo/fundo
    if (minV === maxV) { minV -= 1; maxV += 1; }
    else { const paddingVal = (maxV - minV) * 0.1; minV -= paddingVal; maxV += paddingVal; }

    const getX = (i) => padding + (i * (width - (padding * 2)) / (data.length > 1 ? data.length - 1 : 1));
    const getY = (v) => height - padding - ((v - minV) / (maxV - minV) * (height - (padding * 2)));

    // Construir Linha
    let pathD = '';
    let pointsHtml = '';

    data.forEach((d, i) => {
        const x = data.length === 1 ? width / 2 : getX(i);
        const y = getY(d.value);

        if (i === 0) pathD += `M ${x} ${y}`;
        else pathD += ` L ${x} ${y}`;

        // Pontos
        pointsHtml += `<circle cx="${x}" cy="${y}" r="4" fill="var(--primary)" stroke="var(--bg-card)" stroke-width="2" />`;
        
        // Texto (Valor) em alguns pontos
        if (data.length <= 10 || i === 0 || i === data.length - 1 || i % Math.ceil(data.length/5) === 0) {
            pointsHtml += `<text x="${x}" y="${y - 8}" fill="var(--text)" font-size="10" text-anchor="middle" font-weight="bold">${d.value}</text>`;
            // Texto (Data) embaixo
             pointsHtml += `<text x="${x}" y="${height - 2}" fill="var(--text-muted)" font-size="9" text-anchor="middle">${d.date}</text>`;
        }
    });

    const svg = `
        <svg width="${width}" height="${height}" style="overflow:visible">
            <line x1="${padding}" y1="${getY((minV+maxV)/2)}" x2="${width-padding}" y2="${getY((minV+maxV)/2)}" stroke="var(--border)" stroke-dasharray="4" opacity="0.5" />
            
            <path d="${pathD}" fill="none" stroke="var(--primary)" stroke-width="3" stroke-linecap="round" stroke-linejoin="round" />
            
            ${pointsHtml}
        </svg>
    `;

    container.innerHTML = svg;
}


// ==================== ESTATÍSTICAS E GRÁFICOS DE NUTRIÇÃO ====================

function calculateNutritionStats() {
    const now = new Date();
    const stats = {
        week: { days: 0, kcal: 0, prot: 0, carb: 0, fat: 0 },
        month: { days: 0, kcal: 0, prot: 0, carb: 0, fat: 0 },
        semester: { days: 0, kcal: 0, prot: 0, carb: 0, fat: 0 }
    };
    
    // Datas de referência
    const oneWeekAgo = new Date(now);
    oneWeekAgo.setDate(oneWeekAgo.getDate() - 7);
    
    const oneMonthAgo = new Date(now);
    oneMonthAgo.setMonth(oneMonthAgo.getMonth() - 1);
    
    const sixMonthsAgo = new Date(now);
    sixMonthsAgo.setMonth(sixMonthsAgo.getMonth() - 6);
    
    // Itera sobre o histórico de alimentação
    Object.keys(foodHistory).forEach(dateStr => {
        const date = new Date(dateStr + 'T12:00:00'); // Adiciona hora para evitar problemas de timezone
        const dayData = foodHistory[dateStr];
        
        if (!dayData || dayData.length === 0) return;
        
        // Soma totais do dia
        let dayKcal = 0, dayProt = 0, dayCarb = 0, dayFat = 0;
        dayData.forEach(item => {
            dayKcal += item.calorias || 0;
            dayProt += item.proteina || 0;
            dayCarb += item.carboidrato || 0;
            dayFat += item.gordura || 0;
        });
        
        // Semana
        if (date >= oneWeekAgo) {
            stats.week.days++;
            stats.week.kcal += dayKcal;
            stats.week.prot += dayProt;
            stats.week.carb += dayCarb;
            stats.week.fat += dayFat;
        }
        
        // Mês
        if (date >= oneMonthAgo) {
            stats.month.days++;
            stats.month.kcal += dayKcal;
            stats.month.prot += dayProt;
            stats.month.carb += dayCarb;
            stats.month.fat += dayFat;
        }
        
        // Semestre
        if (date >= sixMonthsAgo) {
            stats.semester.days++;
            stats.semester.kcal += dayKcal;
            stats.semester.prot += dayProt;
            stats.semester.carb += dayCarb;
            stats.semester.fat += dayFat;
        }
    });
    
    return stats;
}

function renderNutritionStats() {
    const container = document.getElementById('nutritionStats');
    if (!container) return;
    
    const stats = calculateNutritionStats();
    
    const formatAvg = (total, days) => days > 0 ? Math.round(total / days) : 0;
    
    const createStatRow = (label, data, color) => {
        const avgKcal = formatAvg(data.kcal, data.days);
        const avgProt = formatAvg(data.prot, data.days);
        const avgCarb = formatAvg(data.carb, data.days);
        const avgFat = formatAvg(data.fat, data.days);
        
        return `
            <div style="background: var(--bg-input); border-radius: 10px; padding: 12px; margin-bottom: 10px; border-left: 4px solid ${color};">
                <div style="display: flex; justify-content: space-between; align-items: center; margin-bottom: 8px;">
                    <span style="font-weight: 700; color: var(--text); font-size: 14px;">${label}</span>
                    <span style="font-size: 11px; color: var(--text-muted);">${data.days} dias registrados</span>
                </div>
                <div style="display: grid; grid-template-columns: repeat(4, 1fr); gap: 8px; text-align: center;">
                    <div>
                        <div style="font-size: 16px; font-weight: 700; color: var(--primary);">${avgKcal}</div>
                        <div style="font-size: 9px; color: var(--text-muted);">kcal/dia</div>
                    </div>
                    <div>
                        <div style="font-size: 16px; font-weight: 700; color: var(--danger);">${avgProt}g</div>
                        <div style="font-size: 9px; color: var(--text-muted);">prot/dia</div>
                    </div>
                    <div>
                        <div style="font-size: 16px; font-weight: 700; color: var(--success);">${avgCarb}g</div>
                        <div style="font-size: 9px; color: var(--text-muted);">carb/dia</div>
                    </div>
                    <div>
                        <div style="font-size: 16px; font-weight: 700; color: var(--warning);">${avgFat}g</div>
                        <div style="font-size: 9px; color: var(--text-muted);">gord/dia</div>
                    </div>
                </div>
            </div>
        `;
    };
    
    if (stats.week.days === 0 && stats.month.days === 0 && stats.semester.days === 0) {
        container.innerHTML = '<div style="text-align:center; color:var(--text-muted); padding:20px; font-size:13px;">Nenhum registro de alimentação encontrado.</div>';
        return;
    }
    
    container.innerHTML = `
        ${createStatRow('📅 Última Semana', stats.week, 'var(--primary)')}
        ${createStatRow('📆 Último Mês', stats.month, 'var(--success)')}
        ${createStatRow('📊 Último Semestre', stats.semester, 'var(--warning)')}
    `;
}

