const TABATA_PRESETS = {
  classico: { name: 'Tabata Clássico', work: 20, rest: 10, rounds: 8, prep: 10 },
  remo: { name: 'Remo Indoor', work: 30, rest: 15, rounds: 10, prep: 10 },
  bike: { name: 'Bike Indoor', work: 40, rest: 20, rounds: 8, prep: 10 },
  iniciante: { name: 'Iniciante', work: 20, rest: 30, rounds: 6, prep: 15 },
  avancado: { name: 'Avançado', work: 30, rest: 10, rounds: 12, prep: 10 },
  emom: { name: 'EMOM Style', work: 45, rest: 15, rounds: 10, prep: 10 },
  boxing: { name: 'Boxing', work: 180, rest: 60, rounds: 5, prep: 10 },
  sprint: { name: 'Sprint', work: 15, rest: 45, rounds: 10, prep: 10 },
  seven_min: { name: '7 Min Workout', work: 30, rest: 10, rounds: 12, prep: 10 },
  strength: { name: 'Força', work: 40, rest: 20, rounds: 10, prep: 10 },
  rope: { name: 'Pular Corda', work: 60, rest: 30, rounds: 10, prep: 10 },
  core: { name: 'Abdômen', work: 45, rest: 15, rounds: 8, prep: 10 }
};

let tabataState = {
  isRunning: false,
  isPaused: false,
  phase: 'idle',
  currentRound: 0,
  totalRounds: 8,
  workTime: 20,
  restTime: 10,
  prepTime: 10,
  currentSeconds: 0,
  totalElapsed: 0,
  totalDuration: 0,
  interval: null,
  wakeLock: null,
  lastPreset: null
};

function loadTabataPreset(presetId) {
  const preset = TABATA_PRESETS[presetId];
  if (!preset) return;
  
  document.getElementById('tabataWork').value = preset.work;
  document.getElementById('tabataRest').value = preset.rest;
  document.getElementById('tabataRounds').value = preset.rounds;
  document.getElementById('tabataPrep').value = preset.prep;
  
  document.querySelectorAll('.tabata-preset-btn').forEach(btn => btn.classList.remove('active'));
  event.target.closest('.tabata-preset-btn')?.classList.add('active');
  
  document.getElementById('tabataTotalRounds').textContent = preset.rounds;
  tabataState.lastPreset = presetId;
  updateTabataTotalTime();
  
  showToast(`✅ ${preset.name} carregado!`);
}

function updateTabataTotalTime() {
  const work = parseInt(document.getElementById('tabataWork').value) || 20;
  const rest = parseInt(document.getElementById('tabataRest').value) || 10;
  const rounds = parseInt(document.getElementById('tabataRounds').value) || 8;
  const prep = parseInt(document.getElementById('tabataPrep').value) || 10;
  
  const totalSeconds = prep + (work * rounds) + (rest * (rounds - 1));
  tabataState.totalDuration = totalSeconds;
  document.getElementById('tabataTotal').textContent = formatTabataTime(totalSeconds);
}

function formatTabataTime(seconds) {
  const mins = Math.floor(seconds / 60);
  const secs = seconds % 60;
  return `${mins.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
}

async function startTabata() {
  if (tabataState.isPaused) {
    tabataState.isPaused = false;
    tabataState.isRunning = true;
    updateTabataControls(true);
    runTabataTimer();
    await requestWakeLock();
    return;
  }
  
  tabataState.workTime = parseInt(document.getElementById('tabataWork').value) || 20;
  tabataState.restTime = parseInt(document.getElementById('tabataRest').value) || 10;
  tabataState.totalRounds = parseInt(document.getElementById('tabataRounds').value) || 8;
  tabataState.prepTime = parseInt(document.getElementById('tabataPrep').value) || 10;
  
  tabataState.isRunning = true;
  tabataState.isPaused = false;
  tabataState.phase = 'prep';
  tabataState.currentRound = 0;
  tabataState.currentSeconds = tabataState.prepTime;
  tabataState.totalElapsed = 0;
  
  updateTabataTotalTime();
  updateTabataControls(true);
  document.getElementById('tabataTotalRounds').textContent = tabataState.totalRounds;
  document.getElementById('tabataSessionCard').style.display = 'none';
  
  await requestWakeLock();
  updateTabataDisplay();
  runTabataTimer();
  
  speakTabata('Prepare-se');
}

function updateTabataControls(running) {
  document.getElementById('tabataStartBtn').style.display = running ? 'none' : 'block';
  document.getElementById('tabataPauseBtn').style.display = running ? 'block' : 'none';
  document.getElementById('tabataSkipBtn').style.display = running ? 'block' : 'none';
  document.getElementById('tabataStartBtn').innerHTML = tabataState.isPaused ? '▶️ CONTINUAR' : '▶️ INICIAR';
}

function pauseTabata() {
  tabataState.isPaused = true;
  tabataState.isRunning = false;
  
  if (tabataState.interval) {
    clearInterval(tabataState.interval);
    tabataState.interval = null;
  }
  
  updateTabataControls(false);
  document.getElementById('tabataStartBtn').innerHTML = '▶️ CONTINUAR';
  document.getElementById('tabataStartBtn').style.display = 'block';
  releaseWakeLock();
}

function resetTabata() {
  tabataState.isRunning = false;
  tabataState.isPaused = false;
  tabataState.phase = 'idle';
  tabataState.currentRound = 0;
  tabataState.currentSeconds = 0;
  tabataState.totalElapsed = 0;
  
  if (tabataState.interval) {
    clearInterval(tabataState.interval);
    tabataState.interval = null;
  }
  
  const display = document.getElementById('tabataDisplay');
  display.className = 'card tabata-display';
  
  document.getElementById('tabataPhaseLabel').textContent = 'PREPARADO';
  document.getElementById('tabataTimer').textContent = '00:00';
  document.getElementById('tabataCurrentRound').textContent = '0';
  document.getElementById('tabataCalories').textContent = '0';
  document.getElementById('tabataProgressBar').style.width = '0%';
  document.getElementById('tabataTotalProgressBar').style.width = '0%';
  document.getElementById('tabataElapsed').textContent = '00:00';
  document.getElementById('tabataNextPhase').textContent = '';
  
  updateTabataControls(false);
  document.getElementById('tabataMiniTimer').style.display = 'none';
  updateTabataTotalTime();
  releaseWakeLock();
}

function skipTabataPhase() {
  if (!tabataState.isRunning || tabataState.phase === 'idle') return;
  tabataState.currentSeconds = 1;
}

function runTabataTimer() {
  if (tabataState.interval) clearInterval(tabataState.interval);
  
  tabataState.interval = setInterval(() => {
    if (!tabataState.isRunning) return;
    
    tabataState.currentSeconds--;
    tabataState.totalElapsed++;
    
    // Urgência nos últimos 3 segundos
    const display = document.getElementById('tabataDisplay');
    if (tabataState.currentSeconds <= 3 && tabataState.currentSeconds > 0) {
      display.classList.add('urgent');
      showCountdownOverlay(tabataState.currentSeconds);
      playTabataBeep(false);
      if (tabataState.currentSeconds <= 3) {
        speakTabata(tabataState.currentSeconds.toString());
      }
    } else {
      display.classList.remove('urgent');
    }
    
    if (tabataState.currentSeconds <= 0) {
      handleTabataPhaseTransition();
    }
    
    updateTabataDisplay();
    updateMiniTimer();
    
  }, 1000);
}

function showCountdownOverlay(num) {
  const overlay = document.getElementById('tabataCountdownOverlay');
  overlay.textContent = num;
  overlay.style.animation = 'none';
  overlay.offsetHeight;
  overlay.style.animation = 'countdownPop 1s ease-out';
  
  const colors = { prep: 'var(--warning)', work: 'var(--success)', rest: 'var(--danger)' };
  overlay.style.color = colors[tabataState.phase] || 'var(--primary)';
}

function handleTabataPhaseTransition() {
  if (tabataState.phase === 'prep') {
    tabataState.phase = 'work';
    tabataState.currentRound = 1;
    tabataState.currentSeconds = tabataState.workTime;
    playTabataBeep(true);
    vibrateTabata([200, 100, 200]);
    speakTabata('Vai!');
    
  } else if (tabataState.phase === 'work') {
    if (tabataState.currentRound >= tabataState.totalRounds) {
      finishTabata();
      return;
    }
    tabataState.phase = 'rest';
    tabataState.currentSeconds = tabataState.restTime;
    playTabataBeep(true);
    vibrateTabata([300]);
    speakTabata('Descansa');
    
  } else if (tabataState.phase === 'rest') {
    tabataState.phase = 'work';
    tabataState.currentRound++;
    tabataState.currentSeconds = tabataState.workTime;
    playTabataBeep(true);
    vibrateTabata([200, 100, 200]);
    speakTabata(`Round ${tabataState.currentRound}`);
  }
}

function finishTabata() {
  tabataState.isRunning = false;
  tabataState.phase = 'done';
  
  if (tabataState.interval) {
    clearInterval(tabataState.interval);
    tabataState.interval = null;
  }
  
  playTabataFinish();
  vibrateTabata([500, 200, 500, 200, 500]);
  speakTabata('Parabéns! Treino finalizado!');
  
  const display = document.getElementById('tabataDisplay');
  display.className = 'card tabata-display phase-done';
  display.classList.remove('urgent');
  
  document.getElementById('tabataPhaseLabel').textContent = '🎉 CONCLUÍDO!';
  document.getElementById('tabataProgressBar').style.width = '100%';
  document.getElementById('tabataTotalProgressBar').style.width = '100%';
  document.getElementById('tabataNextPhase').textContent = '';
  document.getElementById('tabataMiniTimer').style.display = 'none';
  
  updateTabataControls(false);
  document.getElementById('tabataStartBtn').innerHTML = '▶️ NOVO';
  document.getElementById('tabataSkipBtn').style.display = 'none';
  
  showTabataSessionSummary();
  saveTabataToHistory();
  releaseWakeLock();
}

function repeatTabataSession() {
  resetTabata();
  setTimeout(() => startTabata(), 300);
}

function updateTabataDisplay() {
  const display = document.getElementById('tabataDisplay');
  
  display.className = 'card tabata-display';
  if (tabataState.phase !== 'idle') {
    display.classList.add('phase-' + tabataState.phase);
  }
  if (tabataState.currentSeconds <= 3 && tabataState.currentSeconds > 0 && tabataState.isRunning) {
    display.classList.add('urgent');
  }
  
  const phaseLabels = {
    'idle': 'PREPARADO',
    'prep': '⏳ PREPARE-SE',
    'work': '🔥 VAI! VAI! VAI!',
    'rest': '😮‍💨 DESCANSE',
    'done': '🎉 CONCLUÍDO!'
  };
  
  document.getElementById('tabataPhaseLabel').textContent = phaseLabels[tabataState.phase] || 'PREPARADO';
  document.getElementById('tabataTimer').textContent = formatTabataTime(tabataState.currentSeconds);
  document.getElementById('tabataCurrentRound').textContent = tabataState.currentRound;
  document.getElementById('tabataElapsed').textContent = formatTabataTime(tabataState.totalElapsed);
  
  // Calorias estimadas (12 kcal/min esforço, 4 kcal/min descanso)
  const workSeconds = tabataState.phase === 'work' ? 
    (tabataState.currentRound - 1) * tabataState.workTime + (tabataState.workTime - tabataState.currentSeconds) :
    tabataState.currentRound * tabataState.workTime;
  const restSeconds = tabataState.totalElapsed - tabataState.prepTime - workSeconds;
  const calories = Math.round((workSeconds * 0.2) + (Math.max(0, restSeconds) * 0.07));
  document.getElementById('tabataCalories').textContent = calories;
  
  // Progress bar da fase atual
  let progressPercent = 0;
  let phaseTotal = 1;
  if (tabataState.phase === 'prep') {
    phaseTotal = tabataState.prepTime;
  } else if (tabataState.phase === 'work') {
    phaseTotal = tabataState.workTime;
  } else if (tabataState.phase === 'rest') {
    phaseTotal = tabataState.restTime;
  }
  progressPercent = ((phaseTotal - tabataState.currentSeconds) / phaseTotal) * 100;
  document.getElementById('tabataProgressBar').style.width = progressPercent + '%';
  
  // Progress bar total
  const totalProgress = (tabataState.totalElapsed / tabataState.totalDuration) * 100;
  document.getElementById('tabataTotalProgressBar').style.width = Math.min(100, totalProgress) + '%';
  
  // Próxima fase
  let nextText = '';
  if (tabataState.phase === 'prep') {
    nextText = `Próximo: 🟢 Esforço ${tabataState.workTime}s`;
  } else if (tabataState.phase === 'work' && tabataState.currentRound < tabataState.totalRounds) {
    nextText = `Próximo: 🔴 Descanso ${tabataState.restTime}s`;
  } else if (tabataState.phase === 'work' && tabataState.currentRound >= tabataState.totalRounds) {
    nextText = '🏁 Último round!';
  } else if (tabataState.phase === 'rest') {
    nextText = `Próximo: 🟢 Round ${tabataState.currentRound + 1}`;
  }
  document.getElementById('tabataNextPhase').textContent = nextText;
}

function updateMiniTimer() {
  const mini = document.getElementById('tabataMiniTimer');
  const tabataSection = document.getElementById('tabata');
  
  // Mostra mini timer se Tabata não está visível e está rodando
  const rect = tabataSection.getBoundingClientRect();
  const isVisible = rect.top < window.innerHeight && rect.bottom > 0;
  
  if (tabataState.isRunning && !isVisible) {
    mini.style.display = 'block';
    mini.className = 'tabata-mini-timer mini-' + tabataState.phase;
    document.getElementById('miniPhase').textContent = tabataState.phase.toUpperCase();
    document.getElementById('miniTime').textContent = formatTabataTime(tabataState.currentSeconds);
    document.getElementById('miniRound').textContent = tabataState.currentRound;
    document.getElementById('miniTotal').textContent = tabataState.totalRounds;
  } else {
    mini.style.display = 'none';
  }
}

