function adjustLoad(exerciseName, delta) {
  const safeId = exerciseName.replace(/[^a-zA-Z0-9]/g, '_');
  const input = document.getElementById('load-' + safeId);
  
  if (!input) {
    // Fallback: busca pelo data-ex
    const fallbackInput = document.querySelector(`.load-input[data-ex="${exerciseName}"]`);
    if (fallbackInput) {
      let currentVal = parseFloat(fallbackInput.value) || 0;
      let newVal = Math.max(0, currentVal + delta);
      fallbackInput.value = newVal;
      saveExerciseData(fallbackInput, 'load');
      flashButton(event.target);
      return;
    }
    return;
  }
  
  let currentVal = parseFloat(input.value) || 0;
  let newVal = Math.max(0, currentVal + delta);
  
  // Arredonda para evitar decimais estranhos
  newVal = Math.round(newVal * 10) / 10;
  
  input.value = newVal;
  saveExerciseData(input, 'load');
  
  // Feedback visual
  flashButton(event.target);
}

function flashButton(btn) {
  if (!btn) return;
  btn.style.background = 'var(--success)';
  btn.style.borderColor = 'var(--success)';
  btn.style.color = 'white';
  
  setTimeout(() => {
    btn.style.background = '';
    btn.style.borderColor = '';
    btn.style.color = '';
  }, 150);
}


