// Estado dos desafios
let challengeData = {
  active: null,          // Desafio ativo atual
  completed: [],         // Histórico de desafios concluídos
  customChallenges: [],  // Desafios customizados
  stats: {
    totalDaysCompleted: 0,
    bestStreak: 0
  }
};

// Carregar dados
function loadChallengeData() {
  const saved = localStorage.getItem('challengeData');
  if (saved) {
    challengeData = JSON.parse(saved);
  }
  renderChallenges();
  updateChallengeStats();
}

// Salvar dados
function saveChallengeData() {
  localStorage.setItem('challengeData', JSON.stringify(challengeData));
}

// Renderizar lista de desafios
function renderChallenges(filter = 'todos') {
  const container = document.getElementById('challengesList');
  if (!container) return;
  
  let challenges = [...predefinedChallenges, ...challengeData.customChallenges];
  
  if (filter !== 'todos') {
    challenges = challenges.filter(c => c.category === filter);
  }
  
  container.innerHTML = challenges.map(challenge => {
    const diffClass = challenge.difficulty || 'medium';
    const diffText = { easy: 'Fácil', medium: 'Médio', hard: 'Difícil' }[diffClass];
    
    return `
      <div class="challenge-card" onclick="startChallenge('${challenge.id}')">
        <div class="challenge-icon">${challenge.icon}</div>
        <div class="challenge-info">
          <div class="challenge-name">${challenge.name}</div>
          <div class="challenge-desc">${challenge.description}</div>
          <div class="challenge-meta">
            <span class="challenge-tag">${challenge.duration} dias</span>
            <span class="challenge-difficulty ${diffClass}">${diffText}</span>
          </div>
        </div>
        <div class="challenge-arrow">›</div>
      </div>
    `;
  }).join('');
  
  // Mostrar/esconder cards
  if (challengeData.active) {
    document.getElementById('activeChallengeCard').style.display = 'block';
    document.getElementById('challengeSelectionCard').style.display = 'none';
    renderActiveChallenge();
  } else {
    document.getElementById('activeChallengeCard').style.display = 'none';
    document.getElementById('challengeSelectionCard').style.display = 'block';
  }
  
  renderCompletedChallenges();
}

// Filtrar desafios
function filterChallenges(filter, btn) {
  document.querySelectorAll('.challenge-filter-btn').forEach(b => b.classList.remove('active'));
  btn.classList.add('active');
  renderChallenges(filter);
}

// Iniciar desafio
function startChallenge(challengeId) {
  if (challengeData.active) {
    if (!confirm('Você já tem um desafio ativo. Deseja abandoná-lo e iniciar um novo?')) {
      return;
    }
  }
  
  const allChallenges = [...predefinedChallenges, ...challengeData.customChallenges];
  const challenge = allChallenges.find(c => c.id === challengeId);
  
  if (!challenge) return;
  
  const today = new Date();
  today.setHours(0, 0, 0, 0);
  
  challengeData.active = {
    ...challenge,
    startDate: today.toISOString(),
    days: {},       // { '2024-01-15': true/false }
    currentStreak: 0
  };
  
  saveChallengeData();
  renderChallenges();
  
  showToast(`🔥 Desafio "${challenge.name}" iniciado!`);
}

// Renderizar desafio ativo
function renderActiveChallenge() {
  const active = challengeData.active;
  if (!active) return;
  
  document.getElementById('activeChallengeIcon').textContent = active.icon;
  document.getElementById('activeChallengeName').textContent = active.name;
  document.getElementById('activeChallengeDesc').textContent = active.description;
  
  // Calcular estatísticas
  const today = new Date();
  today.setHours(0, 0, 0, 0);
  const startDate = new Date(active.startDate);
  const daysPassed = Math.floor((today - startDate) / (1000 * 60 * 60 * 24)) + 1;
  const currentDay = Math.min(daysPassed, active.duration);
  
  let completed = 0;
  let missed = 0;
  let streak = 0;
  let tempStreak = 0;
  
  for (let i = 0; i < daysPassed && i < active.duration; i++) {
    const d = new Date(startDate);
    d.setDate(d.getDate() + i);
    const key = d.toISOString().split('T')[0];
    
    if (active.days[key]) {
      completed++;
      tempStreak++;
      streak = Math.max(streak, tempStreak);
    } else if (i < daysPassed - 1) { // Não conta hoje como perdido ainda
      missed++;
      tempStreak = 0;
    }
  }
  
  // Verificar se hoje já foi marcado para streak atual
  const todayKey = today.toISOString().split('T')[0];
  if (active.days[todayKey]) {
    // Recalcular streak atual contando de trás pra frente
    let currentStreak = 0;
    for (let i = daysPassed - 1; i >= 0; i--) {
      const d = new Date(startDate);
      d.setDate(d.getDate() + i);
      const key = d.toISOString().split('T')[0];
      if (active.days[key]) {
        currentStreak++;
      } else {
        break;
      }
    }
    streak = currentStreak;
  }
  
  document.getElementById('challengeCurrentDay').textContent = currentDay;
  document.getElementById('challengeStreak').textContent = streak;
  document.getElementById('challengeCompleted').textContent = completed;
  document.getElementById('challengeMissed').textContent = missed;
  
  // Progresso
  const progress = (completed / active.duration) * 100;
  document.getElementById('challengeProgressBar').style.width = progress + '%';
  document.getElementById('challengeProgressText').textContent = Math.round(progress) + '%';
  
  // Grade de dias
  renderDaysGrid(active);
  
  // Tarefa do dia
  if (active.tasks && active.tasks[currentDay - 1]) {
    document.getElementById('challengeTodayTask').style.display = 'block';
    document.getElementById('challengeTodayTaskText').textContent = active.tasks[currentDay - 1];
  } else {
    document.getElementById('challengeTodayTask').style.display = 'none';
  }
  
  // Botão de marcar
  const btn = document.getElementById('markTodayBtn');
  if (active.days[todayKey]) {
    btn.innerHTML = '✅ Hoje já foi marcado!';
    btn.style.background = 'var(--bg-input)';
    btn.style.color = 'var(--success)';
    btn.disabled = true;
  } else {
    btn.innerHTML = '✅ Marcar Hoje como Concluído';
    btn.style.background = '';
    btn.style.color = '';
    btn.disabled = false;
  }
  
  // Verificar se completou
  if (completed >= active.duration) {
    completeChallenge();
  }
}

// Renderizar grade de dias
function renderDaysGrid(active) {
  const container = document.getElementById('challengeDaysGrid');
  const startDate = new Date(active.startDate);
  const today = new Date();
  today.setHours(0, 0, 0, 0);
  
  let html = '';
  
  for (let i = 0; i < active.duration; i++) {
    const d = new Date(startDate);
    d.setDate(d.getDate() + i);
    const key = d.toISOString().split('T')[0];
    const dayNum = i + 1;
    
    let classes = 'challenge-day';
    
    if (d.getTime() === today.getTime()) {
      classes += ' today';
    } else if (d > today) {
      classes += ' future';
    }
    
    if (active.days[key]) {
      classes += ' completed';
    } else if (d < today) {
      classes += ' missed';
    }
    
    html += `<div class="${classes}" data-day="${dayNum}">${dayNum}</div>`;
  }
  
  container.innerHTML = html;
}

// Marcar dia
function markChallengeDay() {
  const active = challengeData.active;
  if (!active) return;
  
  const today = new Date();
  today.setHours(0, 0, 0, 0);
  const todayKey = today.toISOString().split('T')[0];
  
  if (active.days[todayKey]) {
    showToast('Hoje já foi marcado!');
    return;
  }
  
  active.days[todayKey] = true;
  challengeData.stats.totalDaysCompleted++;
  
  // Atualizar streak
  const startDate = new Date(active.startDate);
  const daysPassed = Math.floor((today - startDate) / (1000 * 60 * 60 * 24)) + 1;
  
  let currentStreak = 0;
  for (let i = daysPassed - 1; i >= 0; i--) {
    const d = new Date(startDate);
    d.setDate(d.getDate() + i);
    const key = d.toISOString().split('T')[0];
    if (active.days[key]) {
      currentStreak++;
    } else {
      break;
    }
  }
  
  if (currentStreak > challengeData.stats.bestStreak) {
    challengeData.stats.bestStreak = currentStreak;
  }
  
  saveChallengeData();
  renderActiveChallenge();
  updateChallengeStats();
  
  // Animação
  const dayEl = document.querySelector(`.challenge-day[data-day="${daysPassed}"]`);
  if (dayEl) {
    dayEl.classList.add('just-marked');
  }
  
  // Vibrar
  if (navigator.vibrate) {
    navigator.vibrate([100, 50, 100]);
  }
  
  showToast(`🔥 Dia ${daysPassed} completo! Sequência: ${currentStreak} dias`);
}

// Completar desafio
function completeChallenge() {
  const active = challengeData.active;
  if (!active) return;
  
  // Calcular estatísticas finais
  const completed = Object.values(active.days).filter(v => v).length;
  
  challengeData.completed.push({
    id: active.id,
    name: active.name,
    icon: active.icon,
    completedDate: new Date().toISOString(),
    daysCompleted: completed,
    totalDays: active.duration,
    percentage: Math.round((completed / active.duration) * 100)
  });
  
  challengeData.active = null;
  saveChallengeData();
  renderChallenges();
  updateChallengeStats();
  
  // Celebração
  showToast('🏆 PARABÉNS! Desafio concluído!');
  if (navigator.vibrate) {
    navigator.vibrate([200, 100, 200, 100, 400]);
  }
}

// Abandonar desafio
function abandonChallenge() {
  if (!confirm('Tem certeza que deseja abandonar este desafio? Seu progresso será perdido.')) {
    return;
  }
  
  challengeData.active = null;
  saveChallengeData();
  renderChallenges();
  
  showToast('Desafio abandonado');
}

