// Carregar foto
function loadShapePhoto(type, input) {
  const file = input.files[0];
  if (!file) return;
  
  const reader = new FileReader();
  reader.onload = function(e) {
    const img = new Image();
    img.onload = function() {
      shapeData[type].img = img;
      
      // Mostrar preview
      const previewImg = document.getElementById(type + 'Img');
      const placeholder = document.getElementById(type + 'Preview').querySelector('.shape-photo-placeholder');
      const previewContainer = document.getElementById(type + 'Preview');
      
      previewImg.src = e.target.result;
      previewImg.style.display = 'block';
      if (placeholder) placeholder.style.display = 'none';
      previewContainer.classList.add('has-photo');
      
      // Atualizar botão
      checkShapeReady();
      updateShapePreview();
    };
    img.src = e.target.result;
  };
  reader.readAsDataURL(file);
}

