// ==================== NAVEGAÇÃO RÁPIDA ENTRE ABAS ====================

function goToTab(tabId) {
    if (tabId === currentVisualTab) return;

    if (tabId === 'treino') {
        // Se quer ir para treino e não estamos lá, voltamos no histórico
        // isso simula o botão voltar, limpando a pilha
        if (currentVisualTab !== 'treino') {
            history.back();
        }
        // Nota: O history.back() aciona o popstate, que chamará o switchVisualTab
    } else {
        // Indo para uma aba secundária
        if (currentVisualTab === 'treino') {
            // Se saiu do treino, empilha (Push)
            history.pushState({ tab: tabId }, '', '?tab=' + tabId);
        } else {
            // Se trocou de secundária para secundária (ex: Peso -> Dieta), substitui (Replace)
            // Assim o botão voltar sempre cai direto no Treino, não na aba anterior
            history.replaceState({ tab: tabId }, '', '?tab=' + tabId);
        }
        switchVisualTab(tabId);
    }
}


// Apenas troca a visualização (HTML/CSS) sem mexer no histórico
function switchVisualTab(tabId) {
    currentVisualTab = tabId;

    // Remove active de todas
    document.querySelectorAll('.nav-tab').forEach(t => t.classList.remove('active'));
    document.querySelectorAll('.section').forEach(s => s.classList.remove('active'));
    
    // Ativa a nova
    const tab = document.querySelector(`.nav-tab[data-tab="${tabId}"]`);
    const section = document.getElementById(tabId);
    
    if (tab) tab.classList.add('active');
    if (section) section.classList.add('active');
    
    // Scroll para o topo
    window.scrollTo({ top: 0, behavior: 'smooth' });

    // Funções específicas de cada aba
    if (tabId === 'peso') {
        setTimeout(() => {
             if(typeof renderWeightChart === 'function') renderWeightChart();
             if(typeof renderBodyCompChart === 'function') renderBodyCompChart();
        }, 10);
    }
    if (tabId === 'alimentacao' && typeof isFavoritesFilterActive !== 'undefined' && isFavoritesFilterActive) {
        if(typeof searchFood === 'function') searchFood();
    }
    if (tabId === 'mobilidade') {
        if(typeof renderMobilityRoutines === 'function') renderMobilityRoutines();
    }
    if (tabId === 'desafios') {
        if(typeof loadChallengeData === 'function') loadChallengeData();
    }
    
    // ===== ADICIONAR ESTA LINHA =====
    if (tabId === 'abault') {
        if(typeof renderAbaultTab === 'function') renderAbaultTab();
    }
}


