let selectedDietPreset = null;

// Renderiza os seletores de dieta
function renderDietPresetSelector() {
  const container = document.getElementById('dietPresetsSelector');
  if (!container) return;
  
  const currentPreset = localStorage.getItem('currentDietPreset');
  
  container.innerHTML = Object.values(DIET_PRESETS).map(diet => {
    const isSelected = selectedDietPreset === diet.id;
    const isCurrent = currentPreset === diet.id;
    
    return `
    <button class='diet-preset-btn ${isSelected ? "active" : ""}' 
            onclick='selectDietPreset("${diet.id}")' 
            style='padding:12px 16px; border:2px solid ${isSelected ? "var(--primary)" : (isCurrent ? "var(--success)" : "var(--border)")}; border-radius:12px; background:${isSelected ? "rgba(99,102,241,0.1)" : (isCurrent ? "rgba(34,197,94,0.1)" : "var(--bg-input)")}; cursor:pointer; text-align:left; flex:1; min-width:140px; position:relative;'>
      ${isCurrent ? '<div style="position:absolute; top:4px; right:4px; font-size:10px; background:var(--success); color:white; padding:2px 6px; border-radius:4px;">ATUAL</div>' : ''}
      <div style='font-size:24px; margin-bottom:4px;'>${diet.icon}</div>
      <div style='font-size:13px; font-weight:600; color:var(--text);'>${diet.name}</div>
      <div style='font-size:11px; color:var(--text-muted);'>${diet.kcal} kcal • ${diet.protein}g prot</div>
    </button>
  `}).join('');
}

// Seleciona uma dieta
function selectDietPreset(dietId) {
  selectedDietPreset = dietId;
  const diet = DIET_PRESETS[dietId];
  
  if (!diet) return;
  
  // Atualiza seletor
  renderDietPresetSelector();
  
  // Mostra card
  document.getElementById('selectedDietCard').style.display = 'block';
  
  // Preenche resumo
  document.getElementById('dietPresetIcon').textContent = diet.icon;
  document.getElementById('dietPresetName').textContent = diet.name;
  document.getElementById('dietPresetKcal').textContent = diet.kcal;
  document.getElementById('dietPresetProt').textContent = diet.protein + 'g';
  document.getElementById('dietPresetCarb').textContent = diet.carbs + 'g';
  document.getElementById('dietPresetFat').textContent = diet.fat + 'g';
  document.getElementById('dietPresetFiber').textContent = (diet.fiber || '-') + 'g';
  document.getElementById('dietPresetDesc').textContent = diet.description;
  
  // Renderiza refeições
  renderDietPresetMeals(diet);
  
  // Renderiza lista de compras
  renderDietPresetShopping(diet);
  
  // Renderiza dicas
  renderDietPresetTips(diet);
  
  // Scroll para o card
  document.getElementById('selectedDietCard').scrollIntoView({ behavior: 'smooth', block: 'start' });
}


// Usa dieta pré-pronta como dieta atual
function useDietPresetAsCurrent() {
  if (!selectedDietPreset) {
    showToast('❌ Selecione uma dieta primeiro!');
    return;
  }
  
  const diet = DIET_PRESETS[selectedDietPreset];
  
  if (!diet) return;
  
  // Cria objeto no formato do calculatedDiet
  const dietData = {
    targetCalories: diet.kcal,
    protein: diet.protein,
    carbs: diet.carbs,
    fat: diet.fat,
    proteinKcal: diet.protein * 4,
    carbsKcal: diet.carbs * 4,
    fatKcal: diet.fat * 9,
    proteinPct: Math.round((diet.protein * 4 / diet.kcal) * 100),
    carbsPct: Math.round((diet.carbs * 4 / diet.kcal) * 100),
    fatPct: Math.round((diet.fat * 9 / diet.kcal) * 100),
    goal: 'preset',
    presetName: diet.name,
    presetIcon: diet.icon,
    fiber: diet.fiber || 0
  };
  
  // Salva no localStorage
  localStorage.setItem('savedDiet', JSON.stringify(dietData));
  localStorage.setItem('currentDietPreset', selectedDietPreset);
  
  // Atualiza variável global
  calculatedDiet = dietData;
  
  // Atualiza o card de dieta salva
  loadSavedDiet();
  
  showToast(`✅ "${diet.name}" definida como dieta atual!`);
}

