// ==================== COMPARATIVO SEMANAL ====================

function renderWeeklyComparison() {
  const container = document.getElementById('weeklyComparison');
  if (!container) return;
  
  const now = new Date();
  
  // Esta semana
  const thisWeekStart = new Date(now);
  thisWeekStart.setDate(now.getDate() - now.getDay());
  
  // Semana passada
  const lastWeekStart = new Date(thisWeekStart);
  lastWeekStart.setDate(lastWeekStart.getDate() - 7);
  const lastWeekEnd = new Date(thisWeekStart);
  lastWeekEnd.setDate(lastWeekEnd.getDate() - 1);
  
  let thisWeekTotal = 0;
  let lastWeekTotal = 0;
  
  waterHistory.forEach(entry => {
    const entryDate = new Date(entry.date);
    
    if (entryDate >= thisWeekStart && entryDate <= now) {
      thisWeekTotal += entry.amount;
    } else if (entryDate >= lastWeekStart && entryDate <= lastWeekEnd) {
      lastWeekTotal += entry.amount;
    }
  });
  
  const percentageChange = lastWeekTotal > 0 
    ? Math.round(((thisWeekTotal - lastWeekTotal) / lastWeekTotal) * 100) 
    : (thisWeekTotal > 0 ? 100 : 0);
  
  const improved = thisWeekTotal >= lastWeekTotal;
  const arrow = improved ? '📈' : '📉';
  const color = improved ? 'var(--success)' : 'var(--danger)';
  const sign = percentageChange >= 0 ? '+' : '';
  
  container.innerHTML = `
    <div style="display:flex; justify-content:space-between; align-items:center; margin-bottom:15px;">
      <span style="font-size:12px; color:var(--text-muted);">Esta Semana vs Anterior</span>
      <span style="font-size:14px; font-weight:700; color:${color};">${arrow} ${sign}${percentageChange}%</span>
    </div>
    
    <div style="display:flex; align-items:center; gap:10px;">
      <div style="flex:1; text-align:center; padding:12px; background:var(--bg-input); border-radius:10px;">
        <div style="font-size:10px; color:var(--text-muted);">Esta Semana</div>
        <div style="font-size:24px; font-weight:800; color:#3b82f6;">${(thisWeekTotal / 1000).toFixed(1)}L</div>
      </div>
      <div style="font-size:12px; font-weight:700; color:var(--text-muted);">VS</div>
      <div style="flex:1; text-align:center; padding:12px; background:var(--bg-input); border-radius:10px;">
        <div style="font-size:10px; color:var(--text-muted);">Semana Passada</div>
        <div style="font-size:24px; font-weight:800; color:#3b82f6;">${(lastWeekTotal / 1000).toFixed(1)}L</div>
      </div>
    </div>
    
    <div style="text-align:center; font-size:12px; color:var(--text); margin-top:12px; padding:10px; background:rgba(59, 130, 246, 0.1); border-radius:8px;">
      ${improved 
        ? '✨ Você está bebendo mais água que na semana passada!' 
        : '💪 Vamos melhorar esta semana!'}
    </div>
  `;
}

// ==================== MODO SILENCIOSO ====================

function toggleQuietHours(enabled) {
  waterQuietHours.enabled = enabled;
  saveWaterData();
  renderQuietHoursSettings();
}

function setQuietHours(start, end) {
  waterQuietHours.start = start;
  waterQuietHours.end = end;
  saveWaterData();
  showToast(`🌙 Modo silencioso: ${start} - ${end}`);
}

function isQuietHoursActive() {
  if (!waterQuietHours.enabled) return false;
  
  const now = new Date();
  const currentTime = now.getHours() * 60 + now.getMinutes();
  
  const [startH, startM] = waterQuietHours.start.split(':').map(Number);
  const [endH, endM] = waterQuietHours.end.split(':').map(Number);
  
  const startMinutes = startH * 60 + startM;
  const endMinutes = endH * 60 + endM;
  
  if (startMinutes > endMinutes) {
    return currentTime >= startMinutes || currentTime <= endMinutes;
  }
  
  return currentTime >= startMinutes && currentTime <= endMinutes;
}

