function renderWaterWeeklyChart() {
  const container = document.getElementById('waterWeeklyChart');
  const legend = document.getElementById('waterWeeklyLegend');
  if (!container) return;
  
  const days = [];
  const dayNames = ['Dom', 'Seg', 'Ter', 'Qua', 'Qui', 'Sex', 'Sáb'];
  
  for (let i = 6; i >= 0; i--) {
    const date = new Date();
    date.setDate(date.getDate() - i);
    const dateStr = date.toISOString().split('T')[0];
    const total = waterHistory
      .filter(e => e.date === dateStr)
      .reduce((sum, e) => sum + e.amount, 0);
    
    days.push({
      date: dateStr,
      dayName: dayNames[date.getDay()],
      total: total,
      isToday: i === 0
    });
  }
  
  const maxValue = Math.max(...days.map(d => d.total), waterGoal);
  
  container.innerHTML = `
    <div class="water-chart-bar-container">
      ${days.map(day => {
        const height = maxValue > 0 ? (day.total / maxValue) * 140 : 0;
        const goalMet = day.total >= waterGoal;
        const isLow = day.total > 0 && day.total < waterGoal * 0.5;
        
        return `
          <div class="water-chart-bar-wrapper">
            <div class="water-chart-bar ${goalMet ? 'goal-met' : isLow ? 'low' : ''}" 
                 style="height:${Math.max(height, 4)}px; ${day.isToday ? 'box-shadow: 0 0 10px rgba(59,130,246,0.5);' : ''}">
            </div>
            <div class="water-chart-bar-label" style="${day.isToday ? 'color:var(--primary); font-weight:700;' : ''}">${day.dayName}</div>
            <div class="water-chart-bar-value">${day.total > 0 ? (day.total >= 1000 ? (day.total/1000).toFixed(1)+'L' : day.total+'ml') : '-'}</div>
          </div>
        `;
      }).join('')}
    </div>
    <div style="height:2px; background:var(--warning); margin-top:5px; opacity:0.5; position:relative;">
      <span style="position:absolute; right:0; top:-15px; font-size:9px; color:var(--warning);">Meta: ${waterGoal}ml</span>
    </div>
  `;
  
  const metDays = days.filter(d => d.total >= waterGoal).length;
  if (legend) {
    legend.innerHTML = `<span style="font-size:11px; color:var(--text-muted);">${metDays}/7 dias com meta atingida</span>`;
  }
}

function renderWaterMonthlyCalendar() {
  const container = document.getElementById('waterMonthlyCalendar');
  const statsContainer = document.getElementById('waterMonthlyStats');
  if (!container) return;
  
  const now = new Date();
  const year = now.getFullYear();
  const month = now.getMonth();
  const firstDay = new Date(year, month, 1);
  const lastDay = new Date(year, month + 1, 0);
  const daysInMonth = lastDay.getDate();
  const startDayOfWeek = firstDay.getDay();
  
  const dayHeaders = ['D', 'S', 'T', 'Q', 'Q', 'S', 'S'];
  let html = '<div class="water-calendar-grid">';
  
  dayHeaders.forEach(d => {
    html += `<div class="water-calendar-header">${d}</div>`;
  });
  
  for (let i = 0; i < startDayOfWeek; i++) {
    html += '<div class="water-calendar-day empty"></div>';
  }
  
  let metGoalCount = 0;
  let totalAmount = 0;
  
  for (let day = 1; day <= daysInMonth; day++) {
    const dateStr = `${year}-${(month+1).toString().padStart(2,'0')}-${day.toString().padStart(2,'0')}`;
    const dayTotal = waterHistory
      .filter(e => e.date === dateStr)
      .reduce((sum, e) => sum + e.amount, 0);
    
    totalAmount += dayTotal;
    
    const isToday = day === now.getDate();
    const hasData = dayTotal > 0;
    const goalMet = dayTotal >= waterGoal;
    const isPartial = dayTotal >= waterGoal * 0.5 && !goalMet;
    const isLow = dayTotal > 0 && dayTotal < waterGoal * 0.5;
    
    if (goalMet) metGoalCount++;
    
    let classes = 'water-calendar-day';
    if (isToday) classes += ' today';
    if (hasData) classes += ' has-data';
    if (goalMet) classes += ' goal-met';
    else if (isPartial) classes += ' partial';
    else if (isLow) classes += ' low';
    
    html += `<div class="${classes}" title="${dateStr}: ${dayTotal}ml">${day}</div>`;
  }
  
  html += '</div>';
  container.innerHTML = html;
  
  const monthName = now.toLocaleDateString('pt-BR', { month: 'long' });
  const currentDay = now.getDate();
  const avgPerDay = currentDay > 0 ? Math.round(totalAmount / currentDay) : 0;
  
  if (statsContainer) {
    statsContainer.innerHTML = `
      <div style="display:grid; grid-template-columns:repeat(3, 1fr); gap:10px; text-align:center;">
        <div>
          <div style="font-size:20px; font-weight:800; color:var(--success);">${metGoalCount}</div>
          <div style="font-size:10px; color:var(--text-muted);">dias com meta</div>
        </div>
        <div>
          <div style="font-size:20px; font-weight:800; color:#3b82f6;">${(totalAmount/1000).toFixed(1)}L</div>
          <div style="font-size:10px; color:var(--text-muted);">total em ${monthName}</div>
        </div>
        <div>
          <div style="font-size:20px; font-weight:800; color:var(--text);">${avgPerDay}ml</div>
          <div style="font-size:10px; color:var(--text-muted);">média/dia</div>
        </div>
      </div>
    `;
  }
}

