function renderWaterHistory() {
  const container = document.getElementById('waterHistoryList');
  if (!container) return;
  
  const filterEl = document.getElementById('waterHistoryFilter');
  const filterValue = filterEl ? filterEl.value : '30';
  
  const byDay = {};
  waterHistory.forEach(entry => {
    if (!byDay[entry.date]) byDay[entry.date] = [];
    byDay[entry.date].push(entry);
  });
  
  let dates = Object.keys(byDay).sort().reverse();
  
  if (filterValue !== 'all') {
    const days = parseInt(filterValue);
    const cutoff = new Date();
    cutoff.setDate(cutoff.getDate() - days);
    dates = dates.filter(d => new Date(d) >= cutoff);
  }
  
  if (dates.length === 0) {
    container.innerHTML = '<div style="text-align:center; padding:20px; color:var(--text-muted);">Nenhum registro encontrado</div>';
    return;
  }
  
  container.innerHTML = dates.map(date => {
    const entries = byDay[date];
    const total = entries.reduce((sum, e) => sum + e.amount, 0);
    const goalMet = total >= waterGoal;
    const percentage = Math.min(100, Math.round((total / waterGoal) * 100));
    
    const dateObj = new Date(date + 'T12:00:00');
    const formattedDate = dateObj.toLocaleDateString('pt-BR', { weekday: 'short', day: '2-digit', month: '2-digit' });
    
    return `
      <div class="water-history-day">
        <div class="water-history-day-header">
          <span class="water-history-day-date">${formattedDate}</span>
          <span class="water-history-day-total ${goalMet ? 'goal-met' : ''}">${total}ml ${goalMet ? '✓' : ''}</span>
        </div>
        <div class="water-history-day-bar">
          <div class="water-history-day-bar-fill ${goalMet ? 'goal-met' : ''}" style="width:${percentage}%"></div>
        </div>
      </div>
    `;
  }).join('');
}

