function renderWaterTab() {
  loadWaterData();
  
  const todayTotal = getTodayWaterTotal();
  const percentage = Math.min(100, Math.round((todayTotal / waterGoal) * 100));
  
  // Atualizar display principal
  const currentEl = document.getElementById('waterCurrent');
  const percentageEl = document.getElementById('waterPercentage');
  const goalDisplayEl = document.getElementById('waterGoalDisplay');
  const goalLabelEl = document.getElementById('waterGoalLabel');
  const progressBar = document.getElementById('waterProgressBar');
  const waterLevel = document.getElementById('waterLevel');
  
  if (currentEl) currentEl.textContent = todayTotal;
  if (percentageEl) {
    percentageEl.textContent = percentage + '%';
    percentageEl.style.color = percentage >= 100 ? 'var(--success)' : percentage >= 50 ? '#3b82f6' : 'var(--warning)';
  }
  if (goalDisplayEl) goalDisplayEl.textContent = waterGoal;
  if (goalLabelEl) goalLabelEl.textContent = waterGoal + ' ml';
  if (progressBar) progressBar.style.width = percentage + '%';
  if (waterLevel) waterLevel.style.height = Math.min(percentage, 100) + '%';
  
  // Último registro
  const lastEntry = getTodayWaterEntries()[0];
  const lastEntryEl = document.getElementById('lastWaterEntry');
  if (lastEntryEl && lastEntry) {
    const time = new Date(lastEntry.timestamp).toLocaleTimeString('pt-BR', { hour: '2-digit', minute: '2-digit' });
    const drinkInfo = DRINK_TYPES[lastEntry.type] || DRINK_TYPES.water;
    lastEntryEl.textContent = `Último: ${drinkInfo.icon} ${lastEntry.amount}ml às ${time}`;
  } else if (lastEntryEl) {
    lastEntryEl.textContent = '';
  }
  
  // Marcar botão de meta ativo
  document.querySelectorAll('.water-goal-btn').forEach(btn => {
    btn.classList.toggle('active', parseInt(btn.dataset.goal) === waterGoal);
  });
  
  // Renderizar todos os componentes
  renderDrinkTypeSelector();
  renderDrinkTypeBreakdown();
  renderContainerButtons();
  renderWaterChallenge();
  renderHourlyChart();
  renderWeeklyComparison();
  renderQuietHoursSettings();
  renderWaterStats('day');
  renderWaterReminders();
  renderWaterNotificationStatus();
  renderWaterTodayLog();
  renderWaterWeeklyChart();
  renderWaterMonthlyCalendar();
  renderWaterHistory();
  renderWaterAchievements();
  
  startWaterReminderChecker();
}

