function waterQuickCelebrate() {
  const modal = document.getElementById('waterQuickModal');
  if (!modal) return;
  
  const content = modal.querySelector('.waterbtn-content');
  if (!content) return;
  
  const emojis = ['💧', '🎉', '✨', '💦', '🌊', '⭐'];
  for (let i = 0; i < 8; i++) {
    setTimeout(() => {
      const confetti = document.createElement('span');
      confetti.className = 'waterbtn-confetti';
      confetti.textContent = emojis[Math.floor(Math.random() * emojis.length)];
      confetti.style.left = Math.random() * 80 + 10 + '%';
      confetti.style.bottom = '20%';
      confetti.style.animationDelay = (i * 0.1) + 's';
      content.appendChild(confetti);
      
      setTimeout(() => confetti.remove(), 1000);
    }, i * 50);
  }
  
  showToast('🎉 Meta diária atingida!');
  
  if (navigator.vibrate) {
    navigator.vibrate([100, 50, 100]);
  }
}

