function clearCustomMealForm() {
  document.getElementById('customMealName').value = '';
  document.getElementById('customMealKcal').value = '';
  document.getElementById('customMealProt').value = '';
  document.getElementById('customMealCarb').value = '';
  document.getElementById('customMealFat').value = '';
}

function addCustomMealToLog() {
  const name = document.getElementById('customMealName').value.trim();
  const kcal = parseFloat(document.getElementById('customMealKcal').value) || 0;
  const prot = parseFloat(document.getElementById('customMealProt').value) || 0;
  const carb = parseFloat(document.getElementById('customMealCarb').value) || 0;
  const fat = parseFloat(document.getElementById('customMealFat').value) || 0;
  
  // Validações
  if (!name) {
    showToast('❌ Digite o nome da refeição!');
    document.getElementById('customMealName').focus();
    return;
  }
  
  if (kcal === 0 && prot === 0 && carb === 0 && fat === 0) {
    showToast('❌ Preencha pelo menos um valor nutricional!');
    return;
  }
  
  // Cria o item para o histórico
  const logItem = {
    id: 'custom_' + Date.now(), // ID único com prefixo custom
    nome: name,
    quantidade: 1,
    unidade: 'un',
    calorias: parseFloat(kcal.toFixed(2)),
    proteina: parseFloat(prot.toFixed(2)),
    carboidrato: parseFloat(carb.toFixed(2)),
    gordura: parseFloat(fat.toFixed(2)),
    fibra: 0,
    timestamp: Date.now(),
    isCustomMeal: true // Flag para identificar como customizado
  };
  
  const date = document.getElementById('foodDate').value;
  
  if (!foodHistory[date]) {
    foodHistory[date] = [];
  }
  
  foodHistory[date].push(logItem);
  localStorage.setItem('foodHistory', JSON.stringify(foodHistory));
  
  // Fecha o formulário e limpa
  toggleCustomMealForm();
  
  // Atualiza a tela
  loadFoodLog();
  renderRecentFoods();
  
  showToast('✅ Refeição customizada adicionada!');
}



