// ==================== ABAULT - MOTIVOS/RAZÕES ====================

function markAbaultWithReason(itemId) {
  pendingAbaultMark = itemId;
  
  const item = ABAULT_DEFAULT_ITEMS.find(i => i.id === itemId);
  const data = abaultData[itemId] || {};
  const name = item.isCustom ? (data.customName || 'Item') : item.name;
  
  document.getElementById('abaultReasonTitle').innerHTML = `${item.icon} Por que você consumiu ${name}?`;
  document.getElementById('abaultReasonNote').value = '';
  document.querySelectorAll('.abault-reason-btn').forEach(btn => btn.classList.remove('active'));
  
  document.getElementById('abaultReasonModal').classList.add('active');
}

function closeAbaultReasonModal() {
  document.getElementById('abaultReasonModal').classList.remove('active');
  pendingAbaultMark = null;
}

function selectAbaultReason(reason) {
  document.querySelectorAll('.abault-reason-btn').forEach(btn => {
    btn.classList.toggle('active', btn.textContent.toLowerCase().includes(reason));
  });
}

function skipAbaultReason() {
  if (pendingAbaultMark) {
    markAbaultNowDirect(pendingAbaultMark, null, null);
  }
  closeAbaultReasonModal();
}

function confirmAbaultWithReason() {
  if (!pendingAbaultMark) return;
  
  const activeBtn = document.querySelector('.abault-reason-btn.active');
  const reason = activeBtn ? activeBtn.textContent.trim() : null;
  const note = document.getElementById('abaultReasonNote').value.trim() || null;
  
  markAbaultNowDirect(pendingAbaultMark, reason, note);
  closeAbaultReasonModal();
}

function markAbaultNowDirect(itemId, reason, note) {
  if (!abaultData[itemId]) {
    abaultData[itemId] = { history: [], customName: '' };
  }
  
  abaultData[itemId].neverConsumed = false;
  
  const entry = {
    id: Date.now(),
    date: new Date().toISOString(),
    reason: reason,
    note: note
  };
  
  abaultData[itemId].history.unshift(entry);
  saveAbaultData();
  renderAbaultTab();
  
  const item = ABAULT_DEFAULT_ITEMS.find(i => i.id === itemId);
  const name = item.isCustom ? (abaultData[itemId].customName || 'Item') : item.name;
  showToast(`📌 ${name} registrado!`);
}

// Atualizar markAbaultNow para usar o modal de motivo (opcional)
function markAbaultNow(itemId) {
  // Verifica nas configurações se deve pedir motivo
  const askReason = localStorage.getItem('abaultAskReason') !== 'false';
  
  if (askReason) {
    markAbaultWithReason(itemId);
  } else {
    markAbaultNowDirect(itemId, null, null);
  }
}

// ==================== ATUALIZAR HISTÓRICO PARA MOSTRAR MOTIVOS ====================

function openAbaultHistory(itemId) {
  const item = ABAULT_DEFAULT_ITEMS.find(i => i.id === itemId);
  const data = abaultData[itemId] || { history: [], customName: '' };
  const name = item.isCustom ? (data.customName || 'Personalizado') : item.name;
  
  document.getElementById('abaultHistoryTitle').innerHTML = `${item.icon} Histórico: ${name}`;
  
  const history = data.history || [];
  
  if (history.length === 0) {
    document.getElementById('abaultHistoryContent').innerHTML = `
      <div class="empty-state">Nenhum registro ainda.</div>
    `;
  } else {
    let html = `
      <div style="font-size: 12px; color: var(--text-muted); margin-bottom: 10px;">
        Total de ${history.length} registro(s)
      </div>
      <div class="abault-history-list">
    `;
    
    history.forEach((entry) => {
      const date = new Date(entry.date);
      const days = getDaysSinceAbault(entry.date);
      const dateStr = date.toLocaleDateString('pt-BR', { 
        day: '2-digit', month: '2-digit', year: '2-digit',
        hour: '2-digit', minute: '2-digit'
      });
      
      // Motivo e nota
      let reasonHtml = '';
      if (entry.reason) {
        reasonHtml += `<div class="history-reason">${entry.reason}</div>`;
      }
      if (entry.note) {
        reasonHtml += `<div class="history-note">📝 ${entry.note}</div>`;
      }
      
      html += `
        <div class="abault-history-item">
          <div class="history-main">
            <div class="date">${dateStr}</div>
            <div class="ago">${days} dias atrás</div>
            ${reasonHtml}
          </div>
          <button class="delete-abault" onclick="deleteAbaultEntry('${itemId}', ${entry.id})">🗑️</button>
        </div>
      `;
    });
    
    html += '</div>';
    document.getElementById('abaultHistoryContent').innerHTML = html;
  }
  
  document.getElementById('abaultHistoryModal').classList.add('active');
}

// ==================== ATUALIZAR renderAbaultTab ====================

function renderAbaultTab() {
  const foodGrid = document.getElementById('abaultFoodGrid');
  const otherGrid = document.getElementById('abaultOtherGrid');
  const customGrid = document.getElementById('abaultCustomGrid');
  
  if (!foodGrid) return;
  
  foodGrid.innerHTML = '';
  otherGrid.innerHTML = '';
  customGrid.innerHTML = '';
  
  ABAULT_DEFAULT_ITEMS.forEach(item => {
    const data = abaultData[item.id] || { history: [], customName: '' };
    const html = renderAbaultItem(item, data);
    
    if (item.category === 'food') {
      foodGrid.innerHTML += html;
    } else if (item.category === 'other') {
      otherGrid.innerHTML += html;
    } else {
      customGrid.innerHTML += html;
    }
  });
  
  updateAbaultSummary();
  renderAbaultQuickGrid();
  renderAbaultHighlights();
  renderAbaultGoals();
  renderAbaultWeeklyReport();
  renderAbaultMotivation();
  
  if (abaultCurrentFilter !== 'all') {
    filterAbaultItems(abaultCurrentFilter);
  }
  if (abaultCurrentSort !== 'default') {
    sortAbaultItems(abaultCurrentSort);
  }
}









