function renderWeightHistory() {
  const container = document.getElementById('weightHistory');
  if (!container) return;
  
  if (weightHistory.length === 0) {
    container.innerHTML = '<div class="empty-state"><div>Nenhum registro ainda</div></div>';
    return;
  }
  
  // Cálculos da Paginação
  const totalPages = Math.ceil(weightHistory.length / HISTORY_ITEMS_PER_PAGE);
  if (weightPage > totalPages) weightPage = totalPages;
  if (weightPage < 1) weightPage = 1;

  const startIndex = (weightPage - 1) * HISTORY_ITEMS_PER_PAGE;
  const endIndex = startIndex + HISTORY_ITEMS_PER_PAGE;
  const pageItems = weightHistory.slice(startIndex, endIndex);

  let html = '<div class="weight-history-title">Últimos Registros</div>';
  
  pageItems.forEach((record, idx) => {
    const date = new Date(record.date).toLocaleDateString('pt-BR');
    const globalIdx = startIndex + idx;
  
    // Calcula diferença com registro anterior
    let diffBadge = '';
    let borderColor = 'var(--border)';
  
    if (globalIdx < weightHistory.length - 1) {
      const prevRecord = weightHistory[globalIdx + 1];
      const diff = record.weight - prevRecord.weight;
      
      if (diff !== 0) {
        const isGood = weightGoalType 
          ? ((weightGoalType === 'lose' && diff < 0) || (weightGoalType === 'gain' && diff > 0))
          : null;
        
        const diffColor = isGood === true ? 'var(--success)' : (isGood === false ? 'var(--danger)' : 'var(--text-muted)');
        const diffSign = diff > 0 ? '+' : '';
        const diffIcon = diff > 0 ? '↑' : '↓';
        
        borderColor = isGood === true ? 'var(--success)' : (isGood === false ? 'var(--danger)' : 'var(--border)');
        
        diffBadge = `<span style="font-size:10px; color:${diffColor}; font-weight:bold; margin-right:5px;">${diffIcon}${diffSign}${diff.toFixed(1)}</span>`;
      }
    }
  
    // Monta a badge de BF e Composição Corporal
    let compCorporalHtml = '';
    if (record.bf && record.weight) {
        const fatMass = (record.weight * (record.bf / 100)).toFixed(1);
        const leanMass = (record.weight - fatMass).toFixed(1);

        compCorporalHtml = `
          <div style="margin-top:4px; font-size:11px; background:var(--bg-card); padding:6px; border-radius:6px; border:1px solid var(--border);">
             <div style="display:flex; align-items:center; gap:6px; margin-bottom:2px;">
                <span style="background:var(--primary); color:white; padding:1px 6px; border-radius:4px; font-weight:bold; font-size:10px;">BF ${record.bf}%</span>
                <span style="color:var(--text-muted);">Gordura: <strong>${fatMass}kg</strong></span>
             </div>
             <div style="color:var(--text-muted);">
                Massa Magra: <strong style="color:var(--success);">${leanMass}kg</strong>
             </div>
          </div>
        `;
    }

    html += `
      <div class="weight-history-item" style="align-items:flex-start; flex-direction:column; border-left: 3px solid ${borderColor};">
        <div style="display:flex; justify-content:space-between; width:100%; align-items:center;">
            <div style="display:flex; flex-direction:column;">
                <span style="font-weight:500; color:var(--text-muted); font-size:12px;">${date}</span>
                <span style="font-size:16px; font-weight:700; color:var(--text)">${record.weight} kg</span>
            </div>
            
            <div style="display:flex; align-items:center;">
                ${diffBadge}
                <button onclick="deleteWeight(${record.id})" style="background:none; border:none; cursor:pointer; padding:5px; margin-left:5px; opacity:0.6;" title="Apagar Registro">
                    ❌
                </button>
            </div>
        </div>
        ${compCorporalHtml}
      </div>
    `;
  });

  // Adiciona os Controles de Paginação (Mantidos iguais)
  if (totalPages > 1) {
    const btnStyle = "padding: 8px 5px; font-size: 12px; flex: 1; min-width: 35px; justify-content:center;";
    html += `
      <div style="margin-top:15px; padding-top:10px; border-top:1px dashed var(--border);">
        <div style="display:flex; gap:4px; justify-content:center; margin-bottom:8px;">
            <button onclick="changeWeightPage('first')" class="series-btn" style="${btnStyle}">⏮</button>
            <button onclick="changeWeightPage(-1)" class="series-btn" style="${btnStyle}">◀</button>
            <button onclick="changeWeightPage(1)" class="series-btn" style="${btnStyle}">▶</button>
            <button onclick="changeWeightPage('last')" class="series-btn" style="${btnStyle}">⏭</button>
        </div>
        <div style="text-align:center; font-size:12px; color:var(--text-muted); font-weight:bold;">
          Página ${weightPage} de ${totalPages}
        </div>
      </div>
    `;
  }
  
  container.innerHTML = html;
}

