// 1. Salva carga temporária (enquanto digita)
// ==================== SALVAR DADOS TEMPORÁRIOS DO EXERCÍCIO ====================

function saveExerciseData(input, type) {
  const name = input.getAttribute('data-ex');
  
  // Inicializa objetos se não existirem
  if (!currentWorkout.loads) currentWorkout.loads = {};
  if (!currentWorkout.reps) currentWorkout.reps = {};
  if (!currentWorkout.rpes) currentWorkout.rpes = {};
  
  if (input.value) {
    if (type === 'load') {
      currentWorkout.loads[name] = input.value;
    } else if (type === 'reps') {
      currentWorkout.reps[name] = input.value;
    } else if (type === 'rpe') {
      currentWorkout.rpes[name] = input.value;
    }
  } else {
    // Remove se vazio
    if (type === 'load') delete currentWorkout.loads[name];
    if (type === 'reps') delete currentWorkout.reps[name];
    if (type === 'rpe') delete currentWorkout.rpes[name];
  }
}

// 2. Busca última carga
// ==================== BUSCA ÚLTIMOS DADOS DO EXERCÍCIO ====================

function getLastExerciseData(exerciseName) {
  // Retorna { load, reps, rpe, date } do último registro desse exercício
  for (let i = 0; i < workoutHistory.length; i++) {
    const r = workoutHistory[i];
    
    if (r.loads && r.loads[exerciseName]) {
      return {
        load: r.loads[exerciseName],
        reps: r.reps ? r.reps[exerciseName] : null,
        rpe: r.rpes ? r.rpes[exerciseName] : null,
        date: new Date(r.date).toLocaleDateString('pt-BR')
      };
    }
  }
  return { load: null, reps: null, rpe: null, date: null };
}

// Mantém compatibilidade com código antigo que usa getLastLoad
function getLastLoad(exerciseName) {
  const data = getLastExerciseData(exerciseName);
  if (data.load) {
    return { load: data.load, date: data.date };
  }
  return null;
}





// ==================== DASHBOARD ESTATÍSTICAS AVANÇADAS ====================

function renderStats() {
  const container = document.getElementById('statsGrid');
  if (!container) return;

  if (!workoutHistory || workoutHistory.length === 0) {
    container.innerHTML = '<div class="empty-state">Nenhum dado para analisar ainda.</div>';
    return;
  }

  try {
    // --- 1. Cálculos de Tempo (Streak) ---
    const now = new Date();
    const today = new Date(now.getFullYear(), now.getMonth(), now.getDate()).getTime();
    let streak = 0;

    const uniqueDates = [...new Set(workoutHistory.map(r => {
      const d = new Date(r.date);
      if (isNaN(d.getTime())) return null;
      return new Date(d.getFullYear(), d.getMonth(), d.getDate()).getTime();
    }))].filter(d => d !== null).sort((a, b) => b - a);

    if (uniqueDates.length > 0) {
      if (uniqueDates[0] === today || uniqueDates[0] === today - 86400000) {
        streak = 1;
        for (let i = 0; i < uniqueDates.length - 1; i++) {
          if (uniqueDates[i] - uniqueDates[i + 1] === 86400000) streak++;
          else break;
        }
      }
    }

    // --- 2. MAPEAMENTO DE EXERCÍCIOS ---
    const muscleKeywords = {
      "Peitoral": ["supino", "crucifixo", "peck", "crossover", "cross over", "flexão", "flexao", "pullover", "peito", "peitoral", "chest", "fly", "mergulho"],
      "Costas": ["puxada", "remada", "barra fixa", "chin", "pulldown", "pull down", "pulley", "dorsal", "costas", "lombar", "terra", "rack pull", "serrote"],
      "Ombros": ["desenvolvimento", "elevação lateral", "elevacao lateral", "elevação frontal", "elevacao frontal", "face pull", "deltóide", "deltoide", "ombro", "arnold", "militar", "encolhimento", "trapézio", "trapezio"],
      "Bíceps": ["rosca", "biceps", "bíceps", "curl", "scott", "martelo", "concentrada"],
      "Tríceps": ["tríceps", "triceps", "francês", "frances", "testa", "pulley", "corda", "coice", "paralela"],
      "Pernas": ["agachamento", "leg press", "cadeira", "extensora", "flexora", "stiff", "afundo", "passada", "búlgaro", "bulgaro", "panturrilha", "gêmeos", "gemeos", "coxa", "quadríceps", "quadriceps", "posterior", "glúteo", "gluteo", "adutora", "abdutora", "hack"],
      "Abdômen": ["abdominal", "abdomen", "abdômen", "prancha", "oblíquo", "obliquo", "crunch", "elevação de perna", "elevacao de perna", "vacuum", "abs"]
    };

function getMuscleGroup(exerciseName) {
  const name = exerciseName
    .toLowerCase()
    .normalize("NFD")
    .replace(/[\u0300-\u036f]/g, ""); // Remove acentos
  
  // PRIORIDADE 1: Tríceps (verifica ANTES de costas por causa do "pulley")
  const tricepsKeywords = ["triceps", "tricep", "frances", "testa", "coice", "corda"];
  for (const kw of tricepsKeywords) {
    if (name.includes(kw)) return "Tríceps";
  }
  // Pulley sem ser puxada/costas = tríceps
  if (name.includes("pulley") && !name.includes("puxada") && !name.includes("costas")) {
    return "Tríceps";
  }
  
  // PRIORIDADE 2: Bíceps
  const bicepsKeywords = ["biceps", "bicep", "rosca", "curl", "scott", "martelo", "concentrada"];
  for (const kw of bicepsKeywords) {
    if (name.includes(kw)) return "Bíceps";
  }
  
  // PRIORIDADE 3: Ombros
  const ombrosKeywords = ["desenvolvimento", "elevacao lateral", "elevacao frontal", "face pull", "deltoide", "ombro", "arnold", "militar", "encolhimento", "trapezio", "remada alta"];
  for (const kw of ombrosKeywords) {
    if (name.includes(kw)) return "Ombros";
  }
  
  // PRIORIDADE 4: Peitoral
  const peitoralKeywords = ["supino", "crucifixo", "peck", "crossover", "cross over", "flexao", "pullover", "peito", "peitoral", "chest", "fly", "voador", "mergulho"];
  for (const kw of peitoralKeywords) {
    if (name.includes(kw)) return "Peitoral";
  }
  
  // PRIORIDADE 5: Costas
  const costasKeywords = ["puxada", "remada", "barra fixa", "chin", "pulldown", "pull down", "dorsal", "costas", "lombar", "terra", "rack pull", "serrote", "graviton"];
  for (const kw of costasKeywords) {
    if (name.includes(kw)) return "Costas";
  }
  
  // PRIORIDADE 6: Pernas
  const pernasKeywords = ["agachamento", "leg press", "leg", "cadeira", "extensora", "flexora", "stiff", "afundo", "passada", "bulgaro", "panturrilha", "gemeos", "coxa", "quadriceps", "posterior", "gluteo", "adutora", "abdutora", "hack"];
  for (const kw of pernasKeywords) {
    if (name.includes(kw)) return "Pernas";
  }
  
  // PRIORIDADE 7: Abdômen
  const abdomenKeywords = ["abdominal", "abdomen", "prancha", "obliquo", "crunch", "elevacao de perna", "vacuum", "abs", "infra", "supra"];
  for (const kw of abdomenKeywords) {
    if (name.includes(kw)) return "Abdômen";
  }
  
  return "Outros";
}

    // --- 3. Cálculos de Volume ---
    let totalSets = 0;
    let weekSets = 0;
    let muscleVolume = {};
    let dayFreq = [0, 0, 0, 0, 0, 0, 0];

    const oneWeekAgo = new Date();
    oneWeekAgo.setDate(oneWeekAgo.getDate() - 7);

    workoutHistory.forEach(r => {
      const d = new Date(r.date);
      if (isNaN(d.getTime())) return;

      dayFreq[d.getDay()]++;

      if (r.exercises) {
        Object.entries(r.exercises).forEach(([key, val]) => {
          if (['alongamento', 'cardioType', 'cardioTime', 'notes', 'loads', 'reps', 'rpes'].includes(key)) return;

          const s = parseInt(val) || 0;
          if (s <= 0) return;

          totalSets += s;
          if (d >= oneWeekAgo) weekSets += s;

          const cat = getMuscleGroup(key);
          muscleVolume[cat] = (muscleVolume[cat] || 0) + s;
        });
      }
    });

    // --- 4. Peso ---
    let weightDiffVal = 0;
    if (typeof weightHistory !== 'undefined' && weightHistory.length >= 2) {
      weightDiffVal = weightHistory[0].weight - weightHistory[weightHistory.length - 1].weight;
    }
    const weightSign = weightDiffVal > 0 ? '+' : '';
    const weightColor = weightDiffVal > 0 ? 'var(--danger)' : (weightDiffVal < 0 ? 'var(--success)' : 'var(--text)');

    // --- 5. Gráfico de Frequência ---
    const maxFreq = Math.max(...dayFreq) || 1;
    const dayLabels = ['D', 'S', 'T', 'Q', 'Q', 'S', 'S'];
    const todayIdx = new Date().getDay();

    const chartHtml = dayFreq.map((count, i) => {
      const h = count > 0 ? Math.max((count / maxFreq) * 100, 15) : 4;
      const bg = i === todayIdx ? 'var(--success)' : (count > 0 ? 'var(--primary)' : 'var(--border)');
      const labelColor = i === todayIdx ? 'var(--success)' : 'var(--text-muted)';
      const fw = i === todayIdx ? 'bold' : 'normal';

      return `
        <div style="display:flex; flex-direction:column; align-items:center; gap:4px; flex:1; height:100%; justify-content:flex-end;">
          <div style="font-size:9px; color:var(--text-muted);">${count}</div>
          <div style="width:60%; height:${h}%; background:${bg}; border-radius:4px 4px 0 0; min-height:4px;"></div>
          <span style="font-size:10px; color:${labelColor}; font-weight:${fw}">${dayLabels[i]}</span>
        </div>
      `;
    }).join('');

    // --- 6. Cores e Volume por Músculo ---
    const muscleColors = {
      "Peitoral": "#ef4444", "Costas": "#3b82f6", "Ombros": "#f59e0b",
      "Bíceps": "#8b5cf6", "Tríceps": "#ec4899", "Pernas": "#22c55e",
      "Abdômen": "#06b6d4", "Outros": "#6b7280"
    };

    const sortedMuscles = Object.entries(muscleVolume).sort((a, b) => b[1] - a[1]);
    const maxMuscleVol = sortedMuscles.length > 0 ? sortedMuscles[0][1] : 1;

    const muscleVolumeHtml = sortedMuscles.map(([cat, vol]) => {
      const pct = (vol / maxMuscleVol) * 100;
      const color = muscleColors[cat] || muscleColors["Outros"];
      const icon = { "Peitoral": "💪", "Costas": "🔙", "Ombros": "🎯", "Bíceps": "💪", "Tríceps": "💪", "Pernas": "🦵", "Abdômen": "🔥", "Outros": "📦" }[cat] || "📦";

      return `
        <div style="margin-bottom: 10px;">
          <div style="display:flex; justify-content:space-between; font-size:12px; margin-bottom:4px;">
            <span>${icon} ${cat}</span>
            <span style="font-weight:bold; color:${color};">${vol} séries</span>
          </div>
          <div style="width:100%; height:8px; background:var(--bg); border-radius:4px; overflow:hidden;">
            <div style="width:${pct}%; height:100%; background:${color}; border-radius:4px;"></div>
          </div>
        </div>
      `;
    }).join('');

    // --- 7. HTML Final ---
    container.innerHTML = `
      <div class="stat-card">
        <div class="stat-value">🔥 ${streak}</div>
        <div class="stat-label">Dias Seguidos</div>
      </div>
      <div class="stat-card">
        <div class="stat-value">${weekSets}</div>
        <div class="stat-label">Séries (7 dias)</div>
      </div>
      <div class="stat-card">
        <div class="stat-value" style="color:${weightColor}">${weightSign}${weightDiffVal.toFixed(1)}</div>
        <div class="stat-label">Evolução Peso</div>
      </div>
      <div class="stat-card">
        <div class="stat-value">${workoutHistory.length}</div>
        <div class="stat-label">Total Treinos</div>
      </div>

      <div style="grid-column: 1 / -1; margin-top: 10px;">
        <h4 style="font-size:14px; color:var(--text-muted); margin-bottom:12px; display:flex; justify-content:space-between; align-items:center;">
          <span>💪 Volume por Músculo (Total)</span>
          <span style="font-size:11px; color:var(--primary);">${totalSets} séries</span>
        </h4>
        <div style="background: var(--bg-input); border-radius: 12px; padding: 14px;">
          ${muscleVolumeHtml || '<div style="text-align:center; font-size:12px; color:var(--text-muted)">Sem dados</div>'}
        </div>
      </div>

      <div style="grid-column: 1 / -1; margin-top: 16px;">
        <h4 style="font-size:14px; color:var(--text-muted); margin-bottom:10px;">📊 Frequência Semanal</h4>
        <div style="display:flex; justify-content:space-between; align-items:flex-end; height: 100px; padding: 10px; background: var(--bg-input); border-radius: 12px;">
          ${chartHtml}
        </div>
      </div>
    `;

    // Gráfico de Tonelagem
    try { renderVolumeLoadChart(); } catch (e) { console.error(e); }

  } catch (error) {
    console.error("Erro em renderStats:", error);
    container.innerHTML = '<div class="empty-state">Erro ao carregar estatísticas.</div>';
  }
}


