function calculateIMC(weightOverride) {
  // 1. Tenta pegar o peso informado (argumento) ou o que está digitado no input
  const wInput = document.getElementById('weightInput');
  let weight = weightOverride;

  if (!weight && wInput) {
    // Pega o valor do input tratando vírgula
    const val = wInput.value.replace(',', '.');
    weight = val ? parseFloat(val) : 0;
  }

  // 2. Se ainda não tem peso, tenta pegar do histórico
  if ((!weight || weight <= 0) && weightHistory.length > 0) {
    weight = weightHistory[0].weight;
  }

  // 3. Pega a altura
  const height = parseFloat(userHeight);

  // Elementos da tela
  const resultCard = document.getElementById('imcResult');
  const valEl = document.getElementById('imcValue');
  const labelEl = document.getElementById('imcLabel');

  // 4. Validação e Cálculo
  if (weight > 0 && height > 0) {
    const imc = weight / (height * height);
    const imcFormatted = imc.toFixed(1);
    
    if(resultCard) resultCard.style.display = 'block';
    if(valEl) valEl.textContent = imcFormatted;
    
    let label = '';
    let color = '';
    let bg = '';

    if (imc < 18.5) {
      label = 'Abaixo do Peso'; color = '#3b82f6'; bg = 'rgba(59, 130, 246, 0.2)';
    } else if (imc < 24.9) {
      label = 'Peso Normal'; color = '#22c55e'; bg = 'rgba(34, 197, 94, 0.2)';
    } else if (imc < 29.9) {
      label = 'Sobrepeso'; color = '#f59e0b'; bg = 'rgba(245, 158, 11, 0.2)';
    } else {
      label = 'Obesidade'; color = '#ef4444'; bg = 'rgba(239, 68, 68, 0.2)';
    }

    if(labelEl) {
      labelEl.textContent = label;
      labelEl.style.color = color;
      labelEl.style.background = bg;
    }
  } else {
    // Se faltar dados, esconde o card (a menos que estejamos digitando)
    if(resultCard && !wInput.value) resultCard.style.display = 'none';
  }
}




// ==================== GRÁFICO DE TONELAGEM (VOLUME LOAD) ====================

function calculateWeeklyVolumeLoad() {
    const weeks = {};
    
    workoutHistory.forEach(record => {
        if (!record.date) return;
        
        const date = new Date(record.date);
        if (isNaN(date.getTime())) return;
        
        // Calcula o número da semana corretamente
        const startOfYear = new Date(date.getFullYear(), 0, 1);
        const days = Math.floor((date - startOfYear) / 86400000);
        const weekNumber = Math.ceil((days + startOfYear.getDay() + 1) / 7);
        const weekKey = `${date.getFullYear()}-W${weekNumber.toString().padStart(2, '0')}`;
        
        if (!weeks[weekKey]) {
            weeks[weekKey] = { 
                total: 0, 
                workouts: 0,
                startDate: date
            };
        }
        
        weeks[weekKey].workouts++;
        
        // Calcula tonelagem deste treino
        if (record.exercises) {
            Object.entries(record.exercises).forEach(([exercise, series]) => {
                // Ignora campos não-exercício
                if (['alongamento', 'cardioType', 'cardioTime', 'notes', 'loads', 'reps', 'rpes'].includes(exercise)) return;
                
                const numSeries = parseInt(series) || 0;
                if (numSeries <= 0) return;
                
                // Pega carga - tenta várias formas de encontrar
                let load = 0;
                if (record.loads) {
                    // Tenta nome exato primeiro
                    if (record.loads[exercise] !== undefined) {
                        load = parseFloat(record.loads[exercise]) || 0;
                    } else {
                        // Tenta nome limpo (sem parênteses)
                        const cleanEx = exercise.split('(')[0].trim();
                        if (record.loads[cleanEx] !== undefined) {
                            load = parseFloat(record.loads[cleanEx]) || 0;
                        } else {
                            // Tenta busca parcial
                            for (const [key, val] of Object.entries(record.loads)) {
                                if (key.includes(exercise) || exercise.includes(key) || 
                                    key.includes(cleanEx) || cleanEx.includes(key)) {
                                    load = parseFloat(val) || 0;
                                    break;
                                }
                            }
                        }
                    }
                }
                
                // Pega reps - mesma lógica
                let reps = 10; // padrão
                if (record.reps) {
                    if (record.reps[exercise] !== undefined) {
                        reps = parseInt(record.reps[exercise]) || 10;
                    } else {
                        const cleanEx = exercise.split('(')[0].trim();
                        if (record.reps[cleanEx] !== undefined) {
                            reps = parseInt(record.reps[cleanEx]) || 10;
                        } else {
                            for (const [key, val] of Object.entries(record.reps)) {
                                if (key.includes(exercise) || exercise.includes(key) ||
                                    key.includes(cleanEx) || cleanEx.includes(key)) {
                                    reps = parseInt(val) || 10;
                                    break;
                                }
                            }
                        }
                    }
                }
                
                // Volume Load = Séries × Reps × Carga
                const volumeLoad = numSeries * reps * load;
                weeks[weekKey].total += volumeLoad;
            });
        }
    });
    
    return weeks;
}

function renderVolumeLoadChart() {
    const container = document.getElementById('volumeLoadChart');
    const summaryContainer = document.getElementById('volumeLoadSummary');
    
    if (!container) return;
    
    const weeksData = calculateWeeklyVolumeLoad();
    
    // Converte para array e ordena por data
    const sortedWeeks = Object.entries(weeksData)
        .sort((a, b) => a[0].localeCompare(b[0]))
        .slice(-12); // Últimas 12 semanas
    
    // --- CORREÇÃO: Permite mostrar gráfico com apenas 1 semana ---
    if (sortedWeeks.length === 0) {
        container.innerHTML = '<div style="text-align:center; color:var(--text-muted); font-size:12px; align-self:center;">Registre treinos com carga e repetições para ver o gráfico.</div>';
        if (summaryContainer) summaryContainer.innerHTML = '';
        return;
    }
    
    const values = sortedWeeks.map(([_, data]) => data.total);
    const maxVal = Math.max(...values) || 1;
    
    const width = container.clientWidth || 300;
    const height = 200;
    const padding = 35;
    const paddingBottom = 45;
    
    // Ajusta largura da barra dependendo se tem 1 ou mais semanas
    const barWidth = sortedWeeks.length === 1 
        ? 50 
        : Math.max(15, (width - padding * 2) / sortedWeeks.length - 4);
    
    // --- CÁLCULO DE TENDÊNCIA (Só se tiver mais de 1 semana) ---
    const n = values.length;
    let trendHtml = '';
    let avgVal = 0;
    
    // Calcula média simples
    let sumY = 0;
    values.forEach(v => sumY += v);
    avgVal = sumY / n;

    if (n > 1) {
        let sumX = 0, sumXY = 0, sumX2 = 0;
        values.forEach((v, i) => {
            sumX += i;
            sumXY += i * v;
            sumX2 += i * i;
        });
        
        const slope = (n * sumXY - sumX * sumY) / (n * sumX2 - sumX * sumX);
        const trendColor = slope > 0 ? 'var(--success)' : (slope < 0 ? 'var(--danger)' : 'var(--text-muted)');
        const trendLabel = slope > 0 ? '📈 Crescendo' : (slope < 0 ? '📉 Diminuindo' : '➡️ Estável');
        
        trendHtml = `<text x="${padding}" y="15" fill="${trendColor}" font-size="11" font-weight="bold">${trendLabel}</text>`;
    } else {
        trendHtml = `<text x="${padding}" y="15" fill="var(--text-muted)" font-size="11" font-weight="bold">📊 Primeira Semana</text>`;
    }
    
    // Gera SVG das Barras
    let barsHtml = '';
    
    sortedWeeks.forEach(([weekKey, data], i) => {
        // Se só tem 1 semana, centraliza a barra
        let x;
        if (n === 1) {
            x = (width / 2) - (barWidth / 2);
        } else {
            x = padding + i * (barWidth + 4);
        }

        const barHeight = (data.total / maxVal) * (height - padding - paddingBottom);
        const y = height - paddingBottom - barHeight;
        
        // Cor baseada no valor (verde se acima da média)
        const barColor = data.total >= avgVal ? 'var(--success)' : 'var(--primary)';
        
        // Formata a semana para exibir
        const weekLabel = weekKey.split('-W')[1];
        const displayTotal = data.total >= 1000 ? (data.total/1000).toFixed(1) + 't' : Math.round(data.total);
        
        barsHtml += `
            <rect x="${x}" y="${y}" width="${barWidth}" height="${barHeight}" fill="${barColor}" rx="4" opacity="0.8">
                <title>Semana ${weekLabel}: ${(data.total/1000).toFixed(1)}t (${data.workouts} treinos)</title>
            </rect>
            <text x="${x + barWidth/2}" y="${y - 5}" fill="var(--text)" font-size="9" text-anchor="middle" font-weight="bold">
                ${displayTotal}
            </text>
            <text x="${x + barWidth/2}" y="${height - 15}" fill="var(--text-muted)" font-size="8" text-anchor="middle">
                S${weekLabel}
            </text>
        `;
    });
    
    // Linha de média
    const avgY = height - paddingBottom - (avgVal / maxVal) * (height - padding - paddingBottom);
    
    const svg = `
        <svg width="${width}" height="${height}" style="overflow:visible">
            <line x1="${padding}" y1="${avgY}" x2="${width - padding}" y2="${avgY}" stroke="var(--warning)" stroke-width="2" stroke-dasharray="5,5" opacity="0.6" />
            <text x="${width - padding + 5}" y="${avgY + 4}" fill="var(--warning)" font-size="9">Média</text>
            
            ${barsHtml}
            
            ${trendHtml}
        </svg>
    `;
    
    container.innerHTML = svg;
    
    // Resumo Abaixo do Gráfico
    if (summaryContainer) {
        const lastWeek = sortedWeeks[sortedWeeks.length - 1][1].total;
        let diffPct = 0;
        let diffColor = 'var(--text-muted)';
        let diffSign = '';
        
        if (n > 1) {
            const prevWeek = sortedWeeks[sortedWeeks.length - 2][1].total;
            const diff = lastWeek - prevWeek;
            diffPct = prevWeek > 0 ? ((diff / prevWeek) * 100).toFixed(1) : 0;
            diffColor = diff >= 0 ? 'var(--success)' : 'var(--danger)';
            diffSign = diff >= 0 ? '+' : '';
        }
        
        summaryContainer.innerHTML = `
            <div style="text-align:center;">
                <div style="font-size:10px; color:var(--text-muted);">Esta Semana</div>
                <div style="font-size:18px; font-weight:700; color:var(--primary);">${(lastWeek/1000).toFixed(1)}t</div>
            </div>
            <div style="text-align:center;">
                <div style="font-size:10px; color:var(--text-muted);">Evolução</div>
                <div style="font-size:18px; font-weight:700; color:${diffColor};">${n > 1 ? diffSign + diffPct + '%' : '-'}</div>
            </div>
            <div style="text-align:center;">
                <div style="font-size:10px; color:var(--text-muted);">Média Semanal</div>
                <div style="font-size:18px; font-weight:700; color:var(--warning);">${(avgVal/1000).toFixed(1)}t</div>
            </div>
        `;
    }
}

