function calculateMacros() {
  // Pega valores
  const weight = parseFloat(document.getElementById('dietWeight').value.replace(',', '.'));
  const height = parseFloat(document.getElementById('dietHeight').value.replace(',', '.'));
  const age = parseInt(document.getElementById('dietAge').value);
  const activity = parseFloat(document.getElementById('activityLevel').value);
  
  // Validação
  if (!weight || !height || !age) {
    showToast('❌ Preencha peso, altura e idade!');
    return;
  }
  
  // Converte altura para cm se necessário
  const heightCm = height < 3 ? height * 100 : height;
  
  // Calcula TMB (Mifflin-St Jeor)
  let tmb;
  if (dietSettings.sex === 'M') {
    tmb = (10 * weight) + (6.25 * heightCm) - (5 * age) + 5;
  } else {
    tmb = (10 * weight) + (6.25 * heightCm) - (5 * age) - 161;
  }
  
  // Calcula TDEE
  const tdee = tmb * activity;
  
  // Aplica ajuste do objetivo
  let calorieAdjust = 0;
  let adjustLabel = '';
  
  if (dietSettings.goalType === 'cutting') {
    switch (dietSettings.intensity) {
      case 'leve': calorieAdjust = -0.10; adjustLabel = '-10%'; break;
      case 'normal': calorieAdjust = -0.20; adjustLabel = '-20%'; break;
      case 'pesada': calorieAdjust = -0.25; adjustLabel = '-25%'; break;
    }
  } else if (dietSettings.goalType === 'bulking') {
    switch (dietSettings.intensity) {
      case 'leve': calorieAdjust = 0.10; adjustLabel = '+10%'; break;
      case 'normal': calorieAdjust = 0.15; adjustLabel = '+15%'; break;
      case 'pesada': calorieAdjust = 0.20; adjustLabel = '+20%'; break;
    }
  }
  
  const targetCalories = Math.round(tdee * (1 + calorieAdjust));
  
  // Calcula Macros
  // Proteína: baseada no peso
  const proteinGrams = Math.round(weight * dietSettings.proteinPerKg);
  const proteinKcal = proteinGrams * 4;
  
  // Gordura: 25% das calorias (mínimo saudável)
  const fatKcal = Math.round(targetCalories * 0.25);
  const fatGrams = Math.round(fatKcal / 9);
  
  // Carboidratos: o restante
  const carbsKcal = targetCalories - proteinKcal - fatKcal;
  const carbsGrams = Math.round(carbsKcal / 4);
  
  // Percentuais
  const proteinPct = Math.round((proteinKcal / targetCalories) * 100);
  const fatPct = Math.round((fatKcal / targetCalories) * 100);
  const carbsPct = Math.round((carbsKcal / targetCalories) * 100);
  
  // Atualiza UI
  document.getElementById('resultTMB').textContent = Math.round(tmb);
  document.getElementById('resultTDEE').textContent = Math.round(tdee);
  document.getElementById('resultCalories').textContent = targetCalories;
  
  const diffKcal = targetCalories - Math.round(tdee);
  const diffText = diffKcal > 0 ? `+${diffKcal} kcal (superávit)` : (diffKcal < 0 ? `${diffKcal} kcal (déficit)` : 'manutenção');
  document.getElementById('resultCaloriesDiff').textContent = adjustLabel ? `${adjustLabel} → ${diffText}` : 'manutenção';
  
  document.getElementById('resultProtein').textContent = proteinGrams + 'g';
  document.getElementById('resultProteinKcal').textContent = proteinKcal + ' kcal';
  document.getElementById('resultFat').textContent = fatGrams + 'g';
  document.getElementById('resultFatKcal').textContent = fatKcal + ' kcal';
  document.getElementById('resultCarbs').textContent = carbsGrams + 'g';
  document.getElementById('resultCarbsKcal').textContent = carbsKcal + ' kcal';
  
  // Barra de distribuição
  document.getElementById('barProtein').style.width = proteinPct + '%';
  document.getElementById('barFat').style.width = fatPct + '%';
  document.getElementById('barCarbs').style.width = carbsPct + '%';
  document.getElementById('barProteinPct').textContent = proteinPct + '%';
  document.getElementById('barFatPct').textContent = fatPct + '%';
  document.getElementById('barCarbsPct').textContent = carbsPct + '%';
  
  // Dicas contextuais
  let tips = '';
  if (dietSettings.goalType === 'cutting') {
    tips = `🔥 <strong>Cutting:</strong> Priorize proteína para preservar massa magra. Distribua as calorias ao longo do dia para controlar a fome. Carboidratos preferencialmente no pré/pós treino.`;
  } else if (dietSettings.goalType === 'bulking') {
    tips = `💪 <strong>Bulking:</strong> Foque em ganhar peso gradualmente (0.25-0.5kg/semana). Se o peso estagnar por 2 semanas, aumente 100-200kcal.`;
  } else {
    tips = `⚖️ <strong>Manutenção:</strong> Ideal para recomposição corporal. Mantenha treino intenso e proteína alta. Ajuste conforme sua resposta.`;
  }
  
  tips += `<br/><br/>📌 <strong>Dica:</strong> ${proteinGrams}g de proteína = aproximadamente ${Math.round(proteinGrams/30)} porções de frango/carne (100g cada) ou ${Math.round(proteinGrams/6)} ovos.`;
  
  document.getElementById('macroTips').innerHTML = tips;
  
  // Mostra resultado
  document.getElementById('macroResults').style.display = 'block';
  
  // Scroll para resultado
  document.getElementById('macroResults').scrollIntoView({ behavior: 'smooth', block: 'start' });
  
  showToast('✅ Macros calculados!');
}



