function renderDietAlerts(calories, tdee, protein, weight) {
  const alertBox = document.getElementById('dietAlertBox');
  if (!alertBox) return;
  
  let html = '';
  
  // Déficit muito agressivo
  const deficit = tdee - calories;
  if (deficit > 750) {
    html += `
      <div class="diet-alert danger">
        <span style="font-size:20px;">⚠️</span>
        <div>
          <strong>Déficit Muito Agressivo!</strong><br/>
          Déficit de ${deficit} kcal pode causar perda muscular e efeitos negativos no metabolismo.
        </div>
      </div>
    `;
  } else if (deficit > 500 && deficit <= 750) {
    html += `
      <div class="diet-alert warning">
        <span style="font-size:20px;">⚡</span>
        <div>
          <strong>Déficit Moderado-Alto</strong><br/>
          Mantenha a proteína alta e monitore sua energia e humor.
        </div>
      </div>
    `;
  }
  
  // Calorias muito baixas
  const minCal = dietSettings.sex === 'M' ? 1500 : 1200;
  if (calories < minCal) {
    html += `
      <div class="diet-alert danger">
        <span style="font-size:20px;">🚨</span>
        <div>
          <strong>Calorias Muito Baixas!</strong><br/>
          ${calories} kcal está abaixo do recomendado (mín: ${minCal} kcal). Risco de deficiências nutricionais.
        </div>
      </div>
    `;
  }
  
  // Proteína baixa para o peso
  const minProtein = weight * 1.6;
  if (protein < minProtein) {
    html += `
      <div class="diet-alert warning">
        <span style="font-size:20px;">🥩</span>
        <div>
          <strong>Proteína Baixa</strong><br/>
          ${protein}g pode ser insuficiente para seu peso. Considere aumentar para pelo menos ${Math.round(minProtein)}g.
        </div>
      </div>
    `;
  }
  
  // Tudo ok
  if (!html) {
    html = `
      <div class="diet-alert success">
        <span style="font-size:20px;">✅</span>
        <div>
          <strong>Dieta Equilibrada</strong><br/>
          Seus macros estão dentro de parâmetros saudáveis!
        </div>
      </div>
    `;
  }
  
  alertBox.innerHTML = html;
}

function renderDietTimeline(currentWeight, goalWeight, weeklyChange) {
  const timelineDiv = document.getElementById('dietTimeline');
  if (!timelineDiv || !goalWeight || weeklyChange === 0) {
    timelineDiv.style.display = 'none';
    return;
  }
  
  const weightDiff = Math.abs(goalWeight - currentWeight);
  const weeks = Math.ceil(weightDiff / Math.abs(weeklyChange));
  
  const targetDate = new Date();
  targetDate.setDate(targetDate.getDate() + (weeks * 7));
  const dateStr = targetDate.toLocaleDateString('pt-BR', { day: 'numeric', month: 'long', year: 'numeric' });
  
  document.getElementById('timelineWeeks').textContent = weeks;
  document.getElementById('timelineDate').textContent = `Previsão: ${dateStr}`;
  
  timelineDiv.style.display = 'block';
}

function renderDietComparison(targetCal, targetProt, targetCarb, targetFat) {
  const section = document.getElementById('dietComparisonSection');
  const container = document.getElementById('dietComparison');
  
  if (!section || !container) return;
  
  // Calcula consumo real dos últimos 7 dias
  const now = new Date();
  const weekAgo = new Date(now);
  weekAgo.setDate(weekAgo.getDate() - 7);
  
  let realCal = 0, realProt = 0, realCarb = 0, realFat = 0, daysWithData = 0;
  
  if (typeof foodHistory !== 'undefined') {
    Object.keys(foodHistory).forEach(dateStr => {
      const date = new Date(dateStr + 'T12:00:00');
      if (date >= weekAgo && date <= now) {
        const dayData = foodHistory[dateStr];
        if (dayData && dayData.length > 0) {
          daysWithData++;
          dayData.forEach(item => {
            realCal += item.calorias || 0;
            realProt += item.proteina || 0;
            realCarb += item.carboidrato || 0;
            realFat += item.gordura || 0;
          });
        }
      }
    });
  }
  
  if (daysWithData === 0) {
    section.style.display = 'none';
    return;
  }
  
  // Médias
  const avgCal = Math.round(realCal / daysWithData);
  const avgProt = Math.round(realProt / daysWithData);
  
  const calDiff = avgCal - targetCal;
  const calDiffColor = Math.abs(calDiff) <= 100 ? 'var(--success)' : (calDiff > 0 ? 'var(--danger)' : 'var(--warning)');
  const calIcon = Math.abs(calDiff) <= 100 ? '✅' : (calDiff > 0 ? '📈' : '📉');
  
  container.innerHTML = `
    <div class="diet-comparison-item">
      <div style="font-size:10px; color:var(--text-muted);">Meta</div>
      <div style="font-size:22px; font-weight:700; color:var(--primary);">${targetCal}</div>
      <div style="font-size:9px; color:var(--text-muted);">kcal/dia</div>
    </div>
    <div class="diet-comparison-vs">${calIcon}</div>
    <div class="diet-comparison-item">
      <div style="font-size:10px; color:var(--text-muted);">Real (${daysWithData}d)</div>
      <div style="font-size:22px; font-weight:700; color:${calDiffColor};">${avgCal}</div>
      <div style="font-size:9px; color:var(--text-muted);">kcal/dia</div>
    </div>
  `;
  
  section.style.display = 'block';
}

