// Calculadora de água
function renderWaterCalculator() {
  const weight = parseFloat(document.getElementById('dietWeight').value) || 
                 parseFloat(localStorage.getItem('lastWeight')) || 70;
  const activity = parseFloat(document.getElementById('activityLevel').value) || 1.55;
  
  // Fórmula: 35ml por kg * fator de atividade
  let waterMl = weight * 35;
  if (activity >= 1.55) waterMl *= 1.2;
  if (activity >= 1.725) waterMl *= 1.1;
  
  const waterLiters = (waterMl / 1000).toFixed(1);
  const glasses = Math.ceil(waterMl / 250); // Copos de 250ml
  
  const litersEl = document.getElementById('waterGoalLiters');
  const glassesEl = document.getElementById('waterGlasses');
  
  if (litersEl) litersEl.textContent = waterLiters + ' L';
  
  if (glassesEl) {
    glassesEl.innerHTML = Array(Math.min(glasses, 12)).fill(0).map((_, i) => 
      `<span class="water-glass filled">💧</span>`
    ).join('');
    
    if (glasses > 12) {
      glassesEl.innerHTML += `<span style="font-size:12px; color:rgba(255,255,255,0.8);"> +${glasses - 12}</span>`;
    }
  }
}

// Salvar e compartilhar dieta
function saveDietResults() {
  if (!calculatedDiet) {
    showToast('❌ Calcule os macros primeiro!');
    return;
  }
  
  localStorage.setItem('savedDiet', JSON.stringify(calculatedDiet));
  loadSavedDiet();
  showToast('💾 Dieta salva!');
}

function loadSavedDiet() {
  const saved = localStorage.getItem('savedDiet');
  const card = document.getElementById('savedDietCard');
  
  if (saved && card) {
    const diet = JSON.parse(saved);
    
    document.getElementById('savedCalories').textContent = diet.targetCalories;
    document.getElementById('savedProtein').textContent = diet.protein + 'g';
    document.getElementById('savedCarbs').textContent = diet.carbs + 'g';
    document.getElementById('savedFat').textContent = diet.fat + 'g';
    
    card.style.display = 'block';
    
    // Também define como dieta calculada atual
    calculatedDiet = diet;
    
    // Se for uma dieta pré-pronta, marca ela como selecionada
    const presetId = localStorage.getItem('currentDietPreset');
    if (presetId && DIET_PRESETS[presetId]) {
      selectedDietPreset = presetId;
    }
  }
}

function clearSavedDiet() {
  if (confirm('Limpar dieta salva?')) {
    localStorage.removeItem('savedDiet');
    document.getElementById('savedDietCard').style.display = 'none';
    showToast('🗑️ Dieta removida');
  }
}

function shareDietResults() {
  if (!calculatedDiet) {
    showToast('❌ Calcule os macros primeiro!');
    return;
  }
  
  const d = calculatedDiet;
  const goalLabels = { cutting: '🔥 Cutting', manutencao: '⚖️ Manutenção', bulking: '💪 Bulking' };
  
  let text = `🥗 *MINHA DIETA*\n`;
  text += `━━━━━━━━━━━━━━━━━\n\n`;
  text += `📊 Objetivo: ${goalLabels[d.goal] || 'Personalizado'}\n`;
  text += `⚖️ Peso: ${d.weight}kg\n\n`;
  text += `🎯 *Meta Calórica: ${d.targetCalories} kcal*\n\n`;
  text += `🥩 Proteína: ${d.protein}g (${d.proteinPct}%)\n`;
  text += `🍚 Carboidrato: ${d.carbs}g (${d.carbsPct}%)\n`;
  text += `🥑 Gordura: ${d.fat}g (${d.fatPct}%)\n\n`;
  text += `━━━━━━━━━━━━━━━━━\n`;
  text += `📱 _Exercise Tracker_`;
  
  if (navigator.share) {
    navigator.share({ title: 'Minha Dieta', text: text })
      .catch(() => copyToClipboard(text));
  } else {
    copyToClipboard(text);
  }
}

// Inicialização
document.addEventListener('DOMContentLoaded', function() {
  // Carrega dieta salva
  setTimeout(loadSavedDiet, 500);
  
  // Observer para carregar dados quando entrar na aba
  const dietaSection = document.getElementById('dieta');
  if (dietaSection) {
    const observer = new MutationObserver(function(mutations) {
      mutations.forEach(function(mutation) {
        if (mutation.target.classList.contains('active')) {
          loadDietData();
          renderWaterCalculator();
        }
      });
    });
    observer.observe(dietaSection, { attributes: true, attributeFilter: ['class'] });
  }
});


