// ==================== SELEÇÃO RÁPIDA DE ÚLTIMOS ALIMENTOS ====================

function getRecentFoods(limit = 10) {
    const recentItems = [];
    const seenIds = new Set();
    
    // Pega todas as datas e ordena da mais recente para a mais antiga
    const dates = Object.keys(foodHistory).sort((a, b) => new Date(b) - new Date(a));
    
    for (const date of dates) {
        const dayItems = foodHistory[date] || [];
        
        // Ordena por timestamp decrescente (mais recente primeiro)
        const sortedItems = [...dayItems].sort((a, b) => b.timestamp - a.timestamp);
        
        for (const item of sortedItems) {
            if (!seenIds.has(item.id) && recentItems.length < limit) {
                seenIds.add(item.id);
                recentItems.push({
                    id: item.id,
                    nome: item.nome,
                    quantidade: item.quantidade,
                    unidade: item.unidade,
                    calorias: item.calorias,
                    lastUsed: item.timestamp
                });
            }
        }
        
        if (recentItems.length >= limit) break;
    }
    
    return recentItems;
}

