function quickAddFood(foodId, lastQty) {
    // Busca no banco de dados completo
    const food = getAllFoods().find(f => f.id === foodId);
    if (!food) {
        showToast('❌ Alimento não encontrado');
        return;
    }
    
    selectedFoodItem = food;
    
    document.getElementById('selectedFoodName').textContent = food.nome;
    document.getElementById('selectedFoodUnit').textContent = food.unidade;
    document.getElementById('foodQtyInput').value = lastQty || '';
    document.getElementById('foodQtyPreview').style.display = 'none';
    
    updateFavIcon(food.id);
    
    document.getElementById('foodQuantityArea').style.display = 'block';
    document.getElementById('foodSearchResults').style.display = 'none';
    
    if (lastQty) {
        updateFoodQtyPreview();
    }
    
    setTimeout(() => {
        const input = document.getElementById('foodQtyInput');
        input.focus();
        input.select();
    }, 50);
    
    showToast(`📋 Selecionado - ajuste a quantidade`);
}

// ==================== PREVIEW DE MACROS AO DIGITAR ====================

function updateFoodQtyPreview() {
    if (!selectedFoodItem) return;
    
    const qty = parseFloat(document.getElementById('foodQtyInput').value) || 0;
    const previewDiv = document.getElementById('foodQtyPreview');
    
    if (qty <= 0) {
        previewDiv.style.display = 'none';
        return;
    }
    
    let multiplier = 1;
    if (selectedFoodItem.unidade === 'g' || selectedFoodItem.unidade === 'ml') {
        multiplier = qty / 100; 
    } else {
        multiplier = qty; 
    }
    
    document.getElementById('previewKcal').textContent = Math.round(selectedFoodItem.calorias * multiplier);
    document.getElementById('previewProt').textContent = Math.round(selectedFoodItem.proteina * multiplier);
    document.getElementById('previewCarb').textContent = Math.round(selectedFoodItem.carboidrato * multiplier);
    document.getElementById('previewFat').textContent = Math.round(selectedFoodItem.gordura * multiplier);
    
    previewDiv.style.display = 'block';
}



