// ==================== EDIÇÃO DE ITEM ====================

function toggleEditFoodLog(date, timestamp) {
    if (editingFoodLogItem && editingFoodLogItem.timestamp === timestamp) {
        // Se já está editando este item, fecha
        cancelEditFoodLog();
    } else {
        // Abre edição para este item
        editingFoodLogItem = { date, timestamp };
        loadFoodLog();
        
        // Foca no input
        setTimeout(() => {
            const input = document.getElementById(`edit-qty-${timestamp}`);
            if (input) {
                input.focus();
                input.select();
            }
        }, 100);
    }
}

function cancelEditFoodLog() {
    editingFoodLogItem = null;
    loadFoodLog();
}

function previewEditFoodLog(date, timestamp, foodId) {
    const input = document.getElementById(`edit-qty-${timestamp}`);
    const previewDiv = document.getElementById(`edit-preview-${timestamp}`);
    if (!input || !previewDiv) return;
    
    const newQty = parseFloat(input.value) || 0;
    
    // Busca no banco completo
    const originalFood = getAllFoods().find(f => f.id === foodId);
    if (!originalFood) return;
    
    let multiplier = 1;
    if (originalFood.unidade === 'g' || originalFood.unidade === 'ml') {
        multiplier = newQty / 100; 
    } else {
        multiplier = newQty; 
    }
    
    const newKcal = Math.round(originalFood.calorias * multiplier);
    const newProt = Math.round(originalFood.proteina * multiplier);
    const newCarb = Math.round(originalFood.carboidrato * multiplier);
    const newFat = Math.round(originalFood.gordura * multiplier);
    
    previewDiv.innerHTML = `
        <div><span class="value" style="color:var(--primary);">${newKcal}</span><br/>kcal</div>
        <div><span class="value" style="color:var(--danger);">${newProt}g</span><br/>prot</div>
        <div><span class="value" style="color:var(--success);">${newCarb}g</span><br/>carb</div>
        <div><span class="value" style="color:var(--warning);">${newFat}g</span><br/>gord</div>
    `;
}

function saveEditFoodLog(date, timestamp, foodId) {
    const input = document.getElementById(`edit-qty-${timestamp}`);
    if (!input) return;
    
    const newQty = parseFloat(input.value);
    if (!newQty || newQty <= 0) {
        showToast('❌ Quantidade inválida');
        return;
    }
    
    // Busca no banco completo
    const originalFood = getAllFoods().find(f => f.id === foodId);
    if (!originalFood) {
        showToast('❌ Alimento não encontrado');
        return;
    }
    
    const dayData = foodHistory[date];
    if (!dayData) return;
    
    const itemIndex = dayData.findIndex(item => item.timestamp === timestamp);
    if (itemIndex === -1) return;
    
    let multiplier = 1;
    if (originalFood.unidade === 'g' || originalFood.unidade === 'ml') {
        multiplier = newQty / 100; 
    } else {
        multiplier = newQty; 
    }
    
    foodHistory[date][itemIndex] = {
        ...foodHistory[date][itemIndex],
        quantidade: newQty,
        calorias: parseFloat((originalFood.calorias * multiplier).toFixed(2)),
        proteina: parseFloat((originalFood.proteina * multiplier).toFixed(2)),
        carboidrato: parseFloat((originalFood.carboidrato * multiplier).toFixed(2)),
        gordura: parseFloat((originalFood.gordura * multiplier).toFixed(2)),
        fibra: parseFloat(((originalFood.fibra || 0) * multiplier).toFixed(2))
    };
    
    localStorage.setItem('foodHistory', JSON.stringify(foodHistory));
    
    editingFoodLogItem = null;
    loadFoodLog();
    showToast('✅ Quantidade atualizada!');
}

