function renderMeasureChart(key) {
    const container = document.getElementById('measureChartCanvas');
    const summaryStart = document.getElementById('mcStart');
    const summaryCurrent = document.getElementById('mcCurrent');
    const summaryDiff = document.getElementById('mcDiff');

    // 1. Prepara os dados
    // Filtra histórico para pegar apenas registros que tenham essa medida preenchida e válida
    // Ordena do mais antigo para o mais novo
    let data = measurementsHistory
        .filter(r => r[key] !== null && r[key] !== undefined && !isNaN(parseFloat(r[key])) && parseFloat(r[key]) > 0)
        .sort((a, b) => new Date(a.date) - new Date(b.date))
        .map(r => ({
            date: new Date(r.date).toLocaleDateString('pt-BR', {day:'2-digit', month:'2-digit'}),
            value: parseFloat(r[key])
        }));

    // Se a chave for peso, podemos pegar do histórico de peso também para ficar mais completo,
    // mas por simplicidade vamos usar o histórico de medidas aqui.
    
    if (data.length === 0) {
        container.innerHTML = '<div style="color:var(--text-muted); font-size:12px; align-self:center;">Sem dados para esta medida.</div>';
        summaryStart.textContent = '--'; summaryCurrent.textContent = '--'; summaryDiff.textContent = '--';
        return;
    }

    // 2. Atualiza Resumo
    const startVal = data[0].value;
    const currentVal = data[data.length - 1].value;
    const diff = currentVal - startVal;
    
    const unit = key === 'weight' ? 'kg' : 'cm';
    
    summaryStart.textContent = `${startVal}${unit}`;
    summaryCurrent.textContent = `${currentVal}${unit}`;
    
    const diffSign = diff > 0 ? '+' : '';
    const diffColor = diff > 0 ? 'var(--success)' : (diff < 0 ? 'var(--danger)' : 'var(--text)');
    
    // Contexto: Para cintura/abd, perder é bom (verde). Para bíceps, ganhar é bom (verde).
    // Vamos simplificar: Positivo = Verde, Negativo = Vermelho (exceto cintura/abs)
    let finalColor = diffColor;
    if (['waist', 'abs', 'hips'].includes(key)) {
         finalColor = diff < 0 ? 'var(--success)' : (diff > 0 ? 'var(--danger)' : 'var(--text)');
    }

    summaryDiff.innerHTML = `<span style="color:${finalColor}">${diffSign}${diff.toFixed(1)}${unit}</span>`;

    // 3. Desenha o SVG (Mesma lógica do peso, mas genérica)
    const width = container.clientWidth || 300; // Fallback se estiver oculto
    const height = 200;
    const padding = 20;

    // Achar Min e Max para escala Y
    const values = data.map(d => d.value);
    let minV = Math.min(...values);
    let maxV = Math.max(...values);
    
    // Margem para não colar no topo/fundo
    if (minV === maxV) { minV -= 1; maxV += 1; }
    else { const paddingVal = (maxV - minV) * 0.1; minV -= paddingVal; maxV += paddingVal; }

    const getX = (i) => padding + (i * (width - (padding * 2)) / (data.length > 1 ? data.length - 1 : 1));
    const getY = (v) => height - padding - ((v - minV) / (maxV - minV) * (height - (padding * 2)));

    // Construir Linha
    let pathD = '';
    let pointsHtml = '';

    data.forEach((d, i) => {
        const x = data.length === 1 ? width / 2 : getX(i);
        const y = getY(d.value);

        if (i === 0) pathD += `M ${x} ${y}`;
        else pathD += ` L ${x} ${y}`;

        // Pontos
        pointsHtml += `<circle cx="${x}" cy="${y}" r="4" fill="var(--primary)" stroke="var(--bg-card)" stroke-width="2" />`;
        
        // Texto (Valor) em alguns pontos
        if (data.length <= 10 || i === 0 || i === data.length - 1 || i % Math.ceil(data.length/5) === 0) {
            pointsHtml += `<text x="${x}" y="${y - 8}" fill="var(--text)" font-size="10" text-anchor="middle" font-weight="bold">${d.value}</text>`;
            // Texto (Data) embaixo
             pointsHtml += `<text x="${x}" y="${height - 2}" fill="var(--text-muted)" font-size="9" text-anchor="middle">${d.date}</text>`;
        }
    });

    const svg = `
        <svg width="${width}" height="${height}" style="overflow:visible">
            <line x1="${padding}" y1="${getY((minV+maxV)/2)}" x2="${width-padding}" y2="${getY((minV+maxV)/2)}" stroke="var(--border)" stroke-dasharray="4" opacity="0.5" />
            
            <path d="${pathD}" fill="none" stroke="var(--primary)" stroke-width="3" stroke-linecap="round" stroke-linejoin="round" />
            
            ${pointsHtml}
        </svg>
    `;

    container.innerHTML = svg;
}


