// ==================== ESTATÍSTICAS E GRÁFICOS DE NUTRIÇÃO ====================

function calculateNutritionStats() {
    const now = new Date();
    const stats = {
        week: { days: 0, kcal: 0, prot: 0, carb: 0, fat: 0 },
        month: { days: 0, kcal: 0, prot: 0, carb: 0, fat: 0 },
        semester: { days: 0, kcal: 0, prot: 0, carb: 0, fat: 0 }
    };
    
    // Datas de referência
    const oneWeekAgo = new Date(now);
    oneWeekAgo.setDate(oneWeekAgo.getDate() - 7);
    
    const oneMonthAgo = new Date(now);
    oneMonthAgo.setMonth(oneMonthAgo.getMonth() - 1);
    
    const sixMonthsAgo = new Date(now);
    sixMonthsAgo.setMonth(sixMonthsAgo.getMonth() - 6);
    
    // Itera sobre o histórico de alimentação
    Object.keys(foodHistory).forEach(dateStr => {
        const date = new Date(dateStr + 'T12:00:00'); // Adiciona hora para evitar problemas de timezone
        const dayData = foodHistory[dateStr];
        
        if (!dayData || dayData.length === 0) return;
        
        // Soma totais do dia
        let dayKcal = 0, dayProt = 0, dayCarb = 0, dayFat = 0;
        dayData.forEach(item => {
            dayKcal += item.calorias || 0;
            dayProt += item.proteina || 0;
            dayCarb += item.carboidrato || 0;
            dayFat += item.gordura || 0;
        });
        
        // Semana
        if (date >= oneWeekAgo) {
            stats.week.days++;
            stats.week.kcal += dayKcal;
            stats.week.prot += dayProt;
            stats.week.carb += dayCarb;
            stats.week.fat += dayFat;
        }
        
        // Mês
        if (date >= oneMonthAgo) {
            stats.month.days++;
            stats.month.kcal += dayKcal;
            stats.month.prot += dayProt;
            stats.month.carb += dayCarb;
            stats.month.fat += dayFat;
        }
        
        // Semestre
        if (date >= sixMonthsAgo) {
            stats.semester.days++;
            stats.semester.kcal += dayKcal;
            stats.semester.prot += dayProt;
            stats.semester.carb += dayCarb;
            stats.semester.fat += dayFat;
        }
    });
    
    return stats;
}

function renderNutritionStats() {
    const container = document.getElementById('nutritionStats');
    if (!container) return;
    
    const stats = calculateNutritionStats();
    
    const formatAvg = (total, days) => days > 0 ? Math.round(total / days) : 0;
    
    const createStatRow = (label, data, color) => {
        const avgKcal = formatAvg(data.kcal, data.days);
        const avgProt = formatAvg(data.prot, data.days);
        const avgCarb = formatAvg(data.carb, data.days);
        const avgFat = formatAvg(data.fat, data.days);
        
        return `
            <div style="background: var(--bg-input); border-radius: 10px; padding: 12px; margin-bottom: 10px; border-left: 4px solid ${color};">
                <div style="display: flex; justify-content: space-between; align-items: center; margin-bottom: 8px;">
                    <span style="font-weight: 700; color: var(--text); font-size: 14px;">${label}</span>
                    <span style="font-size: 11px; color: var(--text-muted);">${data.days} dias registrados</span>
                </div>
                <div style="display: grid; grid-template-columns: repeat(4, 1fr); gap: 8px; text-align: center;">
                    <div>
                        <div style="font-size: 16px; font-weight: 700; color: var(--primary);">${avgKcal}</div>
                        <div style="font-size: 9px; color: var(--text-muted);">kcal/dia</div>
                    </div>
                    <div>
                        <div style="font-size: 16px; font-weight: 700; color: var(--danger);">${avgProt}g</div>
                        <div style="font-size: 9px; color: var(--text-muted);">prot/dia</div>
                    </div>
                    <div>
                        <div style="font-size: 16px; font-weight: 700; color: var(--success);">${avgCarb}g</div>
                        <div style="font-size: 9px; color: var(--text-muted);">carb/dia</div>
                    </div>
                    <div>
                        <div style="font-size: 16px; font-weight: 700; color: var(--warning);">${avgFat}g</div>
                        <div style="font-size: 9px; color: var(--text-muted);">gord/dia</div>
                    </div>
                </div>
            </div>
        `;
    };
    
    if (stats.week.days === 0 && stats.month.days === 0 && stats.semester.days === 0) {
        container.innerHTML = '<div style="text-align:center; color:var(--text-muted); padding:20px; font-size:13px;">Nenhum registro de alimentação encontrado.</div>';
        return;
    }
    
    container.innerHTML = `
        ${createStatRow('📅 Última Semana', stats.week, 'var(--primary)')}
        ${createStatRow('📆 Último Mês', stats.month, 'var(--success)')}
        ${createStatRow('📊 Último Semestre', stats.semester, 'var(--warning)')}
    `;
}

