// Calcula estatísticas para gamificação
function calculateGamificationStats() {
  const stats = {
    totalKg: 0,
    totalWorkouts: workoutHistory.length,
    totalSets: 0,
    totalPRs: 0,
    totalMinutes: 0,
    maxStreak: 0,
    currentStreak: 0,
    earlyBirdCount: 0,
    nightOwlCount: 0,
    weekendWorkouts: 0,
    uniqueExercises: new Set(),
    exerciseMaxLoads: {},
    weeksTracked: 0,
    avgWorkoutsPerWeek: 0,
    tabataCount: 0,
    weightLogs: weightHistory ? weightHistory.length : 0,
    muscleGroupsTrained: 0,
    legDays: 0,
    muscleGroups: new Set()
  };
  
  // Ordena por data para calcular streak
  const sortedHistory = [...workoutHistory].sort((a, b) => new Date(a.date) - new Date(b.date));
  
  // Para calcular streak
  let lastDate = null;
  let tempStreak = 0;
  
  // Para calcular média semanal
  const weekSet = new Set();
  
  sortedHistory.forEach(record => {
    const date = new Date(record.date);
    const hour = date.getHours();
    const dayOfWeek = date.getDay();
    
    // Horários especiais
    if (hour < 7) stats.earlyBirdCount++;
    if (hour >= 22) stats.nightOwlCount++;
    
    // Fim de semana
    if (dayOfWeek === 0 || dayOfWeek === 6) stats.weekendWorkouts++;
    
	    // Conta Tabata
    if (record.isTabata) {
      stats.tabataCount++;
    }
    
    // Soma tempo de treino
    if (record.durationMinutes) {
      stats.totalMinutes += record.durationMinutes;
    }
    
    // Detecta grupos musculares
    const dayName = (record.dayName || '').toLowerCase();
    if (dayName.includes('perna') || dayName.includes('inferior') || dayName.includes('leg')) {
      stats.legDays++;
      stats.muscleGroups.add('pernas');
    }
    if (dayName.includes('peito') || dayName.includes('chest')) stats.muscleGroups.add('peito');
    if (dayName.includes('costa') || dayName.includes('back') || dayName.includes('dorsal')) stats.muscleGroups.add('costas');
    if (dayName.includes('ombro') || dayName.includes('shoulder')) stats.muscleGroups.add('ombros');
    if (dayName.includes('bíceps') || dayName.includes('biceps') || dayName.includes('braço')) stats.muscleGroups.add('biceps');
    if (dayName.includes('tríceps') || dayName.includes('triceps')) stats.muscleGroups.add('triceps');
	
    // Semanas
    const weekKey = `${date.getFullYear()}-W${Math.ceil((date.getDate() + new Date(date.getFullYear(), date.getMonth(), 1).getDay()) / 7)}`;
    weekSet.add(weekKey);
    
    // Streak
    const dateOnly = new Date(date.getFullYear(), date.getMonth(), date.getDate()).getTime();
    if (lastDate === null) {
      tempStreak = 1;
    } else {
      const diff = (dateOnly - lastDate) / (24 * 60 * 60 * 1000);
      if (diff === 1) {
        tempStreak++;
      } else if (diff > 1) {
        tempStreak = 1;
      }
    }
    lastDate = dateOnly;
    if (tempStreak > stats.maxStreak) stats.maxStreak = tempStreak;
    
    // Calcula tonelagem
    if (record.exercises && record.loads) {
      Object.entries(record.exercises).forEach(([exercise, series]) => {
        if (['alongamento', 'cardioType', 'cardioTime', 'notes', 'loads', 'reps', 'rpes'].includes(exercise)) return;
        
        const cleanEx = exercise.split('(')[0].trim().split(':')[0].trim();
        stats.uniqueExercises.add(cleanEx.toLowerCase());
        
        const numSeries = parseInt(series) || 0;
        stats.totalSets += numSeries;
        
        // Pega carga
        let load = 0;
        if (record.loads) {
          load = parseFloat(record.loads[exercise]) || parseFloat(record.loads[cleanEx]) || 0;
        }
        
        // Pega reps (ou assume 10)
        let reps = 10;
        if (record.reps) {
          reps = parseInt(record.reps[exercise]) || parseInt(record.reps[cleanEx]) || 10;
        }
        
        // Tonelagem = séries × reps × carga
        stats.totalKg += numSeries * reps * load;
        
        // Track max load por exercício
        if (load > 0) {
          if (!stats.exerciseMaxLoads[cleanEx] || load > stats.exerciseMaxLoads[cleanEx]) {
            stats.exerciseMaxLoads[cleanEx] = load;
          }
        }
      });
    }
    
    // Conta PRs
    if (record.prs && record.prs.length > 0) {
      stats.totalPRs += record.prs.length;
    }
  });
  
  // Finaliza estatísticas
  stats.uniqueExercises = stats.uniqueExercises.size;
    stats.muscleGroupsTrained = stats.muscleGroups.size;
  stats.weeksTracked = weekSet.size;
  stats.avgWorkoutsPerWeek = stats.weeksTracked > 0 ? stats.totalWorkouts / stats.weeksTracked : 0;
  
  // Calcula streak atual
  if (sortedHistory.length > 0) {
    const today = new Date();
    today.setHours(0, 0, 0, 0);
    const lastWorkoutDate = new Date(sortedHistory[sortedHistory.length - 1].date);
    lastWorkoutDate.setHours(0, 0, 0, 0);
    
    const daysDiff = Math.floor((today - lastWorkoutDate) / (24 * 60 * 60 * 1000));
    
    if (daysDiff <= 1) {
      // Conta streak atual
      let streak = 1;
      for (let i = sortedHistory.length - 2; i >= 0; i--) {
        const currDate = new Date(sortedHistory[i + 1].date);
        const prevDate = new Date(sortedHistory[i].date);
        currDate.setHours(0, 0, 0, 0);
        prevDate.setHours(0, 0, 0, 0);
        
        const diff = (currDate - prevDate) / (24 * 60 * 60 * 1000);
        if (diff === 1) {
          streak++;
        } else {
          break;
        }
      }
      stats.currentStreak = streak;
    }
  }
  
  return stats;
}

// Calcula nível baseado no XP
function calculateLevel(xp) {
  let level = 1;
  for (let i = LEVEL_THRESHOLDS.length - 1; i >= 0; i--) {
    if (xp >= LEVEL_THRESHOLDS[i]) {
      level = i + 1;
      break;
    }
  }
  return Math.min(level, 50);
}

// Pega título do nível
function getLevelTitle(level) {
  let title = 'Iniciante';
  const sortedKeys = Object.keys(LEVEL_TITLES).map(Number).sort((a, b) => b - a);
  for (const key of sortedKeys) {
    if (level >= key) {
      title = LEVEL_TITLES[key];
      break;
    }
  }
  return title;
}

// Renderiza toda a aba de conquistas
function renderConquistasTab() {
  const stats = calculateGamificationStats();
  const totalXP = Math.round(stats.totalKg);
  const level = calculateLevel(totalXP);
  const title = getLevelTitle(level);
  
  // XP para nível atual e próximo
  const currentLevelXP = LEVEL_THRESHOLDS[level - 1] || 0;
  const nextLevelXP = LEVEL_THRESHOLDS[level] || LEVEL_THRESHOLDS[LEVEL_THRESHOLDS.length - 1];
  const xpInCurrentLevel = totalXP - currentLevelXP;
  const xpNeededForNext = nextLevelXP - currentLevelXP;
  const progressPercent = level >= 50 ? 100 : Math.min((xpInCurrentLevel / xpNeededForNext) * 100, 100);
  
  // Atualiza UI do nível
  const levelEl = document.getElementById('playerLevel');
  const titleEl = document.getElementById('playerTitle');
  const xpEl = document.getElementById('playerXP');
  const barEl = document.getElementById('xpProgressBar');
  const textEl = document.getElementById('xpProgressText');
  const toNextEl = document.getElementById('xpToNextLevel');
  
  if (levelEl) levelEl.textContent = level;
  if (titleEl) titleEl.textContent = title;
  if (xpEl) xpEl.textContent = totalXP.toLocaleString('pt-BR');
  if (barEl) barEl.style.width = progressPercent + '%';
  if (textEl) textEl.textContent = level >= 50 ? 'NÍVEL MÁXIMO!' : `${xpInCurrentLevel.toLocaleString('pt-BR')} / ${xpNeededForNext.toLocaleString('pt-BR')}`;
  if (toNextEl) toNextEl.textContent = level >= 50 ? 'Parabéns!' : (nextLevelXP - totalXP).toLocaleString('pt-BR');
  
  // Atualiza estatísticas
  document.getElementById('totalKgLifted').textContent = (stats.totalKg / 1000).toFixed(1) + 't';
  document.getElementById('totalWorkoutsXP').textContent = stats.totalWorkouts;
  document.getElementById('totalSetsXP').textContent = stats.totalSets;
  document.getElementById('avgKgPerWorkout').textContent = stats.totalWorkouts > 0 
    ? Math.round(stats.totalKg / stats.totalWorkouts).toLocaleString('pt-BR') 
    : '0';
  
  // Renderiza badges
  renderBadges(stats);
  
  // Renderiza próximas metas
  renderNextBadges(stats);
  
  // Renderiza top exercícios
  renderTopExercises(stats.exerciseMaxLoads);
}

function renderBadges(stats) {
  const container = document.getElementById('badgesContainer');
  if (!container) return;
  
  const unlockedBadges = [];
  const lockedBadges = [];
  
  BADGES.forEach(badge => {
    if (badge.check(stats)) {
      unlockedBadges.push(badge);
    } else {
      lockedBadges.push(badge);
    }
  });
  
  // Mostra desbloqueados primeiro, depois bloqueados
  const allBadges = [...unlockedBadges, ...lockedBadges];
  
  container.innerHTML = allBadges.map(badge => {
    const isUnlocked = badge.check(stats);
    return `
      <div class="badge-item ${isUnlocked ? 'unlocked' : 'locked'}" title="${badge.requirement}">
        <span class="badge-icon">${badge.icon}</span>
        <div class="badge-name">${badge.name}</div>
        ${!isUnlocked ? `<div class="badge-requirement">${badge.requirement}</div>` : ''}
      </div>
    `;
  }).join('');
}

function renderNextBadges(stats) {
  const container = document.getElementById('nextBadgesContainer');
  if (!container) return;
  
  // Encontra badges não desbloqueadas e calcula progresso
  const nextBadges = [];
  
  BADGES.forEach(badge => {
    if (badge.check(stats)) return; // Já desbloqueada
    
    // Calcula progresso baseado no tipo
    let progress = 0;
    let current = 0;
    let target = 0;
    
    if (badge.id.startsWith('kg_')) {
      target = parseInt(badge.id.split('_')[1]);
      current = stats.totalKg;
      progress = Math.min((current / target) * 100, 99);
    } else if (badge.id.startsWith('workout_')) {
      target = parseInt(badge.id.split('_')[1]);
      current = stats.totalWorkouts;
      progress = Math.min((current / target) * 100, 99);
    } else if (badge.id.startsWith('streak_')) {
      target = parseInt(badge.id.split('_')[1]);
      current = stats.maxStreak;
      progress = Math.min((current / target) * 100, 99);
    } else if (badge.id.startsWith('pr_')) {
      target = parseInt(badge.id.split('_')[1]);
      current = stats.totalPRs;
      progress = Math.min((current / target) * 100, 99);
    } else if (badge.id.startsWith('exercises_')) {
      target = parseInt(badge.id.split('_')[1]);
      current = stats.uniqueExercises;
      progress = Math.min((current / target) * 100, 99);
    } else if (badge.id.startsWith('early_')) {
      target = parseInt(badge.id.split('_')[1]);
      current = stats.earlyBirdCount;
      progress = Math.min((current / target) * 100, 99);
    } else if (badge.id.startsWith('night_')) {
      target = parseInt(badge.id.split('_')[1]);
      current = stats.nightOwlCount;
      progress = Math.min((current / target) * 100, 99);
    }
    
    if (progress > 0) {
      nextBadges.push({ ...badge, progress, current, target });
    }
  });
  
  // Ordena por progresso (mais próximos primeiro)
  nextBadges.sort((a, b) => b.progress - a.progress);
  
  // Mostra top 5
  const topNext = nextBadges.slice(0, 5);
  
  if (topNext.length === 0) {
    container.innerHTML = '<div style="text-align:center; color:var(--text-muted); padding:15px;">Treine mais para desbloquear metas!</div>';
    return;
  }
  
  container.innerHTML = topNext.map(badge => `
    <div class="next-badge-item">
      <div class="next-badge-icon">${badge.icon}</div>
      <div class="next-badge-info">
        <div class="next-badge-name">${badge.name}</div>
        <div class="next-badge-progress">${badge.current.toLocaleString('pt-BR')} / ${badge.target.toLocaleString('pt-BR')}</div>
        <div class="next-badge-bar">
          <div class="next-badge-bar-fill" style="width:${badge.progress}%"></div>
        </div>
      </div>
      <div style="font-size:12px; font-weight:700; color:var(--primary);">${Math.round(badge.progress)}%</div>
    </div>
  `).join('');
}

function renderTopExercises(exerciseMaxLoads) {
  const container = document.getElementById('topExercisesRanking');
  if (!container) return;
  
  // Converte para array e ordena
  const sorted = Object.entries(exerciseMaxLoads)
    .sort((a, b) => b[1] - a[1])
    .slice(0, 20);
  
  if (sorted.length === 0) {
    container.innerHTML = '<div style="text-align:center; color:var(--text-muted); padding:15px;">Registre exercícios com carga para ver o ranking.</div>';
    return;
  }
  
  container.innerHTML = sorted.map(([name, load], idx) => {
    let rankClass = 'normal';
    if (idx === 0) rankClass = 'gold';
    else if (idx === 1) rankClass = 'silver';
    else if (idx === 2) rankClass = 'bronze';
    
    return `
      <div class="top-exercise-item">
        <div class="top-exercise-rank ${rankClass}">${idx + 1}</div>
        <div style="flex:1;">
          <div style="font-size:13px; font-weight:600; color:var(--text);">${name}</div>
        </div>
        <div style="font-size:16px; font-weight:700; color:var(--primary);">${load}kg</div>
      </div>
    `;
  }).join('');
}


