function calculateReportStats(period) {
  const now = new Date();
  let startDate;
  
  if (period === 'week') {
    startDate = new Date(now);
    startDate.setDate(startDate.getDate() - 7);
  } else {
    startDate = new Date(now);
    startDate.setMonth(startDate.getMonth() - 1);
  }
  
  startDate.setHours(0, 0, 0, 0);
  
  // Filtra treinos do período
  const periodWorkouts = workoutHistory.filter(r => {
    const date = new Date(r.date);
    return date >= startDate && date <= now;
  });
  
  // Estatísticas
  const stats = {
    totalWorkouts: periodWorkouts.length,
    totalSets: 0,
    totalVolume: 0, // Em kg
    totalPRs: 0,
    uniqueDays: new Set(),
    exerciseCount: {},
    bestExercise: null,
    avgSetsPerWorkout: 0,
    avgVolumePerWorkout: 0,
    periodLabel: period === 'week' ? 'Última Semana' : 'Último Mês'
  };
  
  periodWorkouts.forEach(record => {
    // Dias únicos
      const dateStr = getLocalDateString(new Date(record.date));

    stats.uniqueDays.add(dateStr);
    
    // PRs
    if (record.prs && record.prs.length > 0) {
      stats.totalPRs += record.prs.length;
    }
    
    // Exercícios e volume
    if (record.exercises && record.loads) {
      Object.entries(record.exercises).forEach(([exercise, series]) => {
        if (['alongamento', 'cardioType', 'cardioTime', 'notes', 'loads', 'reps', 'rpes'].includes(exercise)) return;
        
        const cleanEx = exercise.split('(')[0].trim().split(':')[0].trim();
        const numSeries = parseInt(series) || 0;
        
        if (numSeries <= 0) return;
        
        stats.totalSets += numSeries;
        
        // Conta exercícios
        stats.exerciseCount[cleanEx] = (stats.exerciseCount[cleanEx] || 0) + numSeries;
        
        // Volume (séries × reps × carga)
        let load = 0;
        if (record.loads) {
          load = parseFloat(record.loads[exercise]) || parseFloat(record.loads[cleanEx]) || 0;
        }
        
        let reps = 10;
        if (record.reps) {
          reps = parseInt(record.reps[exercise]) || parseInt(record.reps[cleanEx]) || 10;
        }
        
        stats.totalVolume += numSeries * reps * load;
      });
    }
  });
  
  // Médias
  if (stats.totalWorkouts > 0) {
    stats.avgSetsPerWorkout = Math.round(stats.totalSets / stats.totalWorkouts);
    stats.avgVolumePerWorkout = Math.round(stats.totalVolume / stats.totalWorkouts);
  }
  
  // Exercício mais feito
  const sortedExercises = Object.entries(stats.exerciseCount).sort((a, b) => b[1] - a[1]);
  if (sortedExercises.length > 0) {
    stats.bestExercise = { name: sortedExercises[0][0], sets: sortedExercises[0][1] };
  }
  
  stats.uniqueDays = stats.uniqueDays.size;
  
  return stats;
}

