// ==================== TIMER DE DURAÇÃO DO TREINO ====================

let workoutStartTime = null;
let workoutDurationInterval = null;
let isWorkoutPaused = false;
let workoutPausedTime = 0; // Acumula o tempo pausado
let pauseStartTime = null; // Marca quando a pausa começou
let isWorkoutActive = false;

function startWorkoutTimer() {
    if (isWorkoutActive) return;

    workoutStartTime = new Date();
    isWorkoutActive = true;

    // --- TRAVA DE SEGURANÇA ---
    // Adiciona o hash na URL atual. Se o usuário estiver em 'Peso', vira 'Peso#treino-ativo'
    // Se estiver em 'Treino', vira 'Treino#treino-ativo'
    window.history.pushState({ workoutRunning: true }, '', window.location.search + '#treino-ativo');
    // -------------------------

    isWorkoutPaused = false;
    workoutPausedTime = 0;
    pauseStartTime = null;

    // (O resto do seu código visual do timer continua igual aqui...)
    const statusDot = document.querySelector('#workoutTimerStatus .status-dot');
    const statusText = document.querySelector('#workoutTimerStatus .status-text');
    const card = document.getElementById('workoutDurationCard');
    if (statusDot) {
        statusDot.style.background = 'var(--success)';
        statusText.textContent = 'Treino em andamento (Toque para pausar)';
        if (card) {
            card.style.borderColor = 'var(--primary)';
            card.style.background = 'rgba(99, 102, 241, 0.05)';
        }
    }
    if (card) card.style.display = 'block';
    workoutDurationInterval = setInterval(updateWorkoutDurationDisplay, 1000);
    updateWorkoutDurationDisplay();
}

function stopWorkoutTimer() {
    if (!isWorkoutActive) return null;

    clearInterval(workoutDurationInterval);
    workoutDurationInterval = null;

    const endTime = new Date();
    let durationMs = endTime - workoutStartTime - workoutPausedTime;
    if (isWorkoutPaused && pauseStartTime) {
        durationMs -= (endTime - pauseStartTime);
    }
    const durationMinutes = Math.floor(durationMs / 60000);
    const card = document.getElementById('workoutDurationCard');
    if (card) card.style.display = 'none';
    const startTimeCopy = new Date(workoutStartTime);

    // Reseta variáveis
    workoutStartTime = null;
    isWorkoutActive = false;
    isWorkoutPaused = false;
    workoutPausedTime = 0;
    pauseStartTime = null;

    // --- REMOVE A TRAVA ---
    // Volta no histórico para remover o hash #treino-ativo se ele ainda estiver lá
    if (location.hash.includes('treino-ativo')) {
        history.back();
    }
    // ---------------------

    return {
        startTime: startTimeCopy.toISOString(),
        endTime: endTime.toISOString(),
        durationMinutes: durationMinutes
    };
}

function updateWorkoutDurationDisplay() {
  if (!workoutStartTime) return;
  
  const now = new Date();
  let elapsed = now - workoutStartTime - workoutPausedTime;
  
  // Se estiver pausado, desconta também o tempo da pausa atual
  if (isWorkoutPaused && pauseStartTime) {
    elapsed -= (now - pauseStartTime);
  }
  
  const hours = Math.floor(elapsed / 3600000);
  const minutes = Math.floor((elapsed % 3600000) / 60000);
  const seconds = Math.floor((elapsed % 60000) / 1000);
  
  let display = '';
  if (hours > 0) {
    display = `${hours.toString().padStart(2, '0')}:${minutes.toString().padStart(2, '0')}:${seconds.toString().padStart(2, '0')}`;
  } else {
    display = `${minutes.toString().padStart(2, '0')}:${seconds.toString().padStart(2, '0')}`;
  }
  
  const el = document.getElementById('workoutDurationDisplay');
  if (el) el.textContent = display;
}

function toggleWorkoutTimerPause() {
  const statusDot = document.querySelector('#workoutTimerStatus .status-dot');
  const statusText = document.querySelector('#workoutTimerStatus .status-text');
  const card = document.getElementById('workoutDurationCard');

  if (isWorkoutPaused) {
    // RETOMAR
    isWorkoutPaused = false;
    // Calcula quanto tempo ficou parado e adiciona ao total pausado
    workoutPausedTime += (new Date() - pauseStartTime);
    pauseStartTime = null;
    
    // Atualiza visual
    statusDot.style.background = 'var(--success)';
    statusDot.style.boxShadow = '0 0 5px var(--success)';
    statusText.textContent = 'Treino em andamento (Toque para pausar)';
    card.style.borderColor = 'var(--primary)';
    card.style.background = 'rgba(99, 102, 241, 0.05)';
    
  } else {
    // PAUSAR
    isWorkoutPaused = true;
    pauseStartTime = new Date();
    
    // Atualiza visual
    statusDot.style.background = 'var(--warning)';
    statusDot.style.boxShadow = '0 0 5px var(--warning)';
    statusText.textContent = '⏸️ PAUSADO (Toque para continuar)';
    card.style.borderColor = 'var(--warning)';
    card.style.background = 'rgba(245, 158, 11, 0.1)';
  }
  
  // Atualiza display imediatamente
  updateWorkoutDurationDisplay();
}

function formatDuration(minutes) {
  if (!minutes || minutes <= 0) return '--';
  
  const hours = Math.floor(minutes / 60);
  const mins = minutes % 60;
  
  if (hours > 0) {
    return `${hours}h ${mins}min`;
  }
  return `${mins}min`;
}





// ==================== ESTATÍSTICAS DE TEMPO ====================

function calculateTimeStats() {
  const now = new Date();
  const currentMonth = now.getMonth();
  const currentYear = now.getFullYear();
  
  const stats = {
    allTime: { totalMinutes: 0, count: 0 },
    thisMonth: { totalMinutes: 0, count: 0 },
    thisYear: { totalMinutes: 0, count: 0 },
    lastMonth: { totalMinutes: 0, count: 0 },
    byMonth: {}, // { 'YYYY-MM': { totalMinutes, count } }
    longestWorkout: 0,
    shortestWorkout: Infinity
  };
  
  workoutHistory.forEach(record => {
    if (!record.durationMinutes || record.durationMinutes <= 0) return;
    
    const date = new Date(record.date);
    const duration = record.durationMinutes;
    const monthKey = `${date.getFullYear()}-${(date.getMonth() + 1).toString().padStart(2, '0')}`;
    
    // All time
    stats.allTime.totalMinutes += duration;
    stats.allTime.count++;
    
    // Longest/Shortest
    if (duration > stats.longestWorkout) stats.longestWorkout = duration;
    if (duration < stats.shortestWorkout) stats.shortestWorkout = duration;
    
    // This month
    if (date.getMonth() === currentMonth && date.getFullYear() === currentYear) {
      stats.thisMonth.totalMinutes += duration;
      stats.thisMonth.count++;
    }
    
    // This year
    if (date.getFullYear() === currentYear) {
      stats.thisYear.totalMinutes += duration;
      stats.thisYear.count++;
    }
    
    // Last month
    const lastMonth = currentMonth === 0 ? 11 : currentMonth - 1;
    const lastMonthYear = currentMonth === 0 ? currentYear - 1 : currentYear;
    if (date.getMonth() === lastMonth && date.getFullYear() === lastMonthYear) {
      stats.lastMonth.totalMinutes += duration;
      stats.lastMonth.count++;
    }
    
    // By month
    if (!stats.byMonth[monthKey]) {
      stats.byMonth[monthKey] = { totalMinutes: 0, count: 0 };
    }
    stats.byMonth[monthKey].totalMinutes += duration;
    stats.byMonth[monthKey].count++;
  });
  
  // Fix shortest if no data
  if (stats.shortestWorkout === Infinity) stats.shortestWorkout = 0;
  
  return stats;
}

