function normalizeExerciseName(name) {
  return name
    .toLowerCase()
    .replace(/\(.*?\)/g, "") // Remove tudo entre parênteses ex: (3) ou (4x10)
    .replace(/[0-9]/g, "")   // Remove números soltos
    .trim();
}

function getMuscleGroupForExercise(exerciseName) {
  const cleanName = normalizeExerciseName(exerciseName);
  
  // Remove acentos para comparação mais segura
  const normalizedName = cleanName
    .normalize("NFD")
    .replace(/[\u0300-\u036f]/g, "")
    .toLowerCase();

  // --- REGRAS DE PRIORIDADE (Para resolver conflitos) ---

  // 1. Tríceps (MÁXIMA PRIORIDADE - verifica primeiro)
  if (normalizedName.includes("triceps") || 
      normalizedName.includes("tricep") ||
      normalizedName.includes("testa") || 
      normalizedName.includes("corda") || 
      normalizedName.includes("coice") || 
      normalizedName.includes("frances") ||
      normalizedName.includes("pulley triceps") ||
      normalizedName.includes("triceps pulley") ||
      (normalizedName.includes("pulley") && !normalizedName.includes("puxada") && !normalizedName.includes("costas"))) {
    return "Tríceps";
  }
  if (normalizedName.includes("supino") && normalizedName.includes("fechado")) {
    return "Tríceps";
  }

  // 2. Bíceps (Ganha de Costas no caso de Rosca)
  if (normalizedName.includes("biceps") || 
      normalizedName.includes("bicep") ||
      normalizedName.includes("rosca") || 
      normalizedName.includes("curl") || 
      normalizedName.includes("scott") ||
      normalizedName.includes("martelo") ||
      normalizedName.includes("concentrada")) {
    return "Bíceps";
  }

  // 3. Ombros (Ganha de Costas na "Remada Alta")
  if (normalizedName.includes("desenvolvimento") || 
      normalizedName.includes("elevacao lateral") ||
      normalizedName.includes("elevacao frontal") ||
      normalizedName.includes("face pull") || 
      normalizedName.includes("ombro") || 
      normalizedName.includes("remada alta") ||
      normalizedName.includes("arnold") ||
      normalizedName.includes("militar")) {
    return "Ombros";
  }

  // 4. Peito (Geral)
  if (normalizedName.includes("supino") || 
      normalizedName.includes("crucifixo") || 
      normalizedName.includes("peck") || 
      normalizedName.includes("crossover") || 
      normalizedName.includes("cross over") || 
      normalizedName.includes("flexao") || 
      normalizedName.includes("fly") ||
      normalizedName.includes("voador")) {
    return "Peitoral";
  }

  // 5. Costas (Geral)
  if (normalizedName.includes("puxada") || 
      normalizedName.includes("remada") || 
      normalizedName.includes("barra fixa") ||
      normalizedName.includes("dorsal") || 
      normalizedName.includes("terra") || 
      normalizedName.includes("pulldown") ||
      normalizedName.includes("serrote") ||
      normalizedName.includes("graviton")) {
    return "Costas";
  }
  
  // 6. Pernas (Geral)
  if (normalizedName.includes("agachamento") || 
      normalizedName.includes("leg") || 
      normalizedName.includes("cadeira") || 
      normalizedName.includes("extensora") ||
      normalizedName.includes("flexora") ||
      normalizedName.includes("stiff") || 
      normalizedName.includes("afundo") || 
      normalizedName.includes("panturrilha") || 
      normalizedName.includes("gemeos") ||
      normalizedName.includes("hack") ||
      normalizedName.includes("bulgaro") ||
      normalizedName.includes("gluteo") ||
      normalizedName.includes("adutora") ||
      normalizedName.includes("abdutora")) {
    return "Pernas";
  }

  // 7. Abdômen
  if (normalizedName.includes("abs") || 
      normalizedName.includes("abdominal") || 
      normalizedName.includes("abdomen") ||
      normalizedName.includes("prancha") ||
      normalizedName.includes("obliquo")) {
    return "Abdômen";
  }

  return "Outros";
}

function calculateMuscleVolumeStats() {
  const stats = {};
  
  // Inicializa zerado
  Object.keys(MUSCLE_GROUPS).forEach(muscle => {
    stats[muscle] = { sets: 0, volume: 0, workouts: 0 };
  });
  // Adiciona categoria Outros
  stats["Outros"] = { sets: 0, volume: 0, workouts: 0 };
  
  workoutHistory.forEach(record => {
    if (!record.exercises) return;
    
    const musclesInWorkout = new Set();
    
    Object.entries(record.exercises).forEach(([exercise, series]) => {
      // Ignora chaves de sistema
      if (['alongamento', 'cardioType', 'cardioTime', 'notes', 'loads', 'reps', 'rpes'].includes(exercise)) return;
      
      const muscle = getMuscleGroupForExercise(exercise);
      if (!muscle) return;
      
      const numSeries = parseInt(series) || 0;
      if (numSeries <= 0) return;
      
      // Soma séries
      if (stats[muscle]) {
        stats[muscle].sets += numSeries;
      }
      
      musclesInWorkout.add(muscle);
    });
    
    musclesInWorkout.forEach(muscle => {
      if (stats[muscle]) {
        stats[muscle].workouts++;
      }
    });
  });
  
  return stats;
}



// ==================== SELETOR DE TIPO DE GRÁFICO MUSCULAR ====================

function selectMuscleChartType(type, btn) {
  selectedMuscleChartType = type;
  
  // Atualiza botões
  document.querySelectorAll('.muscle-chart-type').forEach(b => b.classList.remove('active'));
  if (btn) btn.classList.add('active');
  
  // Re-renderiza o gráfico
  renderMuscleChart();
}

function renderMuscleChart() {
  const container = document.getElementById('muscleChartContainer');
  const analysisContainer = document.getElementById('muscleWeaknessAnalysis');
  
  if (!container) return;
  
  const stats = calculateMuscleVolumeStats();
  const muscles = Object.keys(MUSCLE_GROUPS);
  const values = muscles.map(m => stats[m].sets);
  const maxValue = Math.max(...values);
  
  if (maxValue === 0) {
    container.innerHTML = `
      <div style='text-align:center; color:var(--text-muted);'>
        <div style='font-size:40px; margin-bottom:10px;'>📊</div>
        <div>Sem dados suficientes para o gráfico.</div>
        <div style='font-size:11px; margin-top:5px;'>Registre treinos para ver a análise.</div>
      </div>
    `;
    if (analysisContainer) analysisContainer.innerHTML = '';
    return;
  }
  
  // Renderiza o tipo de gráfico selecionado
  switch(selectedMuscleChartType) {
    case 'radar':
      renderMuscleRadarChart();
      break;
    case 'bars':
      renderMuscleBarChart();
      break;
    case 'horizontal':
      renderMuscleHorizontalChart();
      break;
    case 'donut':
      renderMuscleDonutChart();
      break;
    case 'polar':
      renderMusclePolarChart();
      break;
    default:
      renderMuscleRadarChart();
  }
  
  // Análise de Pontos Fracos
  if (analysisContainer) {
    renderMuscleAnalysis(stats, muscles, maxValue, analysisContainer);
  }
}

// ==================== GRÁFICO RADAR (Original) ====================

function renderMuscleRadarChart() {
  const container = document.getElementById('muscleChartContainer');
  if (!container) return;
  
  const stats = calculateMuscleVolumeStats();
  const muscles = Object.keys(MUSCLE_GROUPS);
  const values = muscles.map(m => stats[m].sets);
  const maxValue = Math.max(...values);
  
  if (maxValue === 0) return;
  
  const width = container.clientWidth || 300;
  const height = 280;
  const centerX = width / 2;
  const centerY = height / 2;
  const radius = Math.min(width, height) / 2 - 40;
  const levels = 5;
  const angleStep = (2 * Math.PI) / muscles.length;
  
  const getPoint = (index, value) => {
    const angle = (index * angleStep) - (Math.PI / 2);
    const r = (value / maxValue) * radius;
    return {
      x: centerX + r * Math.cos(angle),
      y: centerY + r * Math.sin(angle)
    };
  };
  
  let gridHtml = '';
  for (let i = 1; i <= levels; i++) {
    const r = (i / levels) * radius;
    gridHtml += `<circle cx="${centerX}" cy="${centerY}" r="${r}" fill="none" stroke="var(--border)" stroke-dasharray="3,3" opacity="0.3"/>`;
  }
  
  let axesHtml = '';
  muscles.forEach((muscle, i) => {
    const point = getPoint(i, maxValue);
    axesHtml += `<line x1="${centerX}" y1="${centerY}" x2="${point.x}" y2="${point.y}" stroke="var(--border)" opacity="0.3"/>`;
    
    const labelPoint = getPoint(i, maxValue * 1.15);
    const color = MUSCLE_GROUPS[muscle].color;
    axesHtml += `<text x="${labelPoint.x}" y="${labelPoint.y}" fill="${color}" font-size="10" font-weight="600" text-anchor="middle" dominant-baseline="middle">${muscle}</text>`;
  });
  
  let polygonPoints = '';
  muscles.forEach((muscle, i) => {
    const value = stats[muscle].sets;
    const point = getPoint(i, value);
    polygonPoints += `${point.x},${point.y} `;
  });
  
  let pointsHtml = '';
  muscles.forEach((muscle, i) => {
    const value = stats[muscle].sets;
    const point = getPoint(i, value);
    const color = MUSCLE_GROUPS[muscle].color;
    pointsHtml += `
      <circle cx="${point.x}" cy="${point.y}" r="5" fill="${color}" stroke="var(--bg-card)" stroke-width="2">
        <title>${muscle}: ${value} séries</title>
      </circle>
    `;
  });
  
  const totalSets = values.reduce((a, b) => a + b, 0);
  
  const svg = `
    <svg width="${width}" height="${height}" style="overflow:visible">
      ${gridHtml}
      ${axesHtml}
      <polygon points="${polygonPoints}" fill="var(--primary)" opacity="0.2" stroke="var(--primary)" stroke-width="2"/>
      ${pointsHtml}
      <text x="${centerX}" y="${centerY - 5}" fill="var(--text)" font-size="16" font-weight="800" text-anchor="middle">${totalSets}</text>
      <text x="${centerX}" y="${centerY + 12}" fill="var(--text-muted)" font-size="10" text-anchor="middle">séries</text>
    </svg>
  `;
  
  container.innerHTML = svg;
}

// ==================== GRÁFICO DE BARRAS VERTICAIS ====================

function renderMuscleBarChart() {
  const container = document.getElementById('muscleChartContainer');
  if (!container) return;
  
  const stats = calculateMuscleVolumeStats();
  const muscles = Object.keys(MUSCLE_GROUPS);
  const values = muscles.map(m => stats[m].sets);
  const maxValue = Math.max(...values);
  const totalSets = values.reduce((a, b) => a + b, 0);
  
  if (maxValue === 0) return;
  
  const width = container.clientWidth || 300;
  const height = 280;
  const padding = 40;
  const paddingBottom = 60;
  const barGap = 8;
  const barWidth = (width - padding * 2 - barGap * (muscles.length - 1)) / muscles.length;
  
  let barsHtml = '';
  
  muscles.forEach((muscle, i) => {
    const value = stats[muscle].sets;
    const barHeight = maxValue > 0 ? (value / maxValue) * (height - padding - paddingBottom) : 0;
    const x = padding + i * (barWidth + barGap);
    const y = height - paddingBottom - barHeight;
    const color = MUSCLE_GROUPS[muscle].color;
    const pct = totalSets > 0 ? Math.round((value / totalSets) * 100) : 0;
    
    barsHtml += `
      <rect x="${x}" y="${y}" width="${barWidth}" height="${barHeight}" fill="${color}" rx="4" opacity="0.85">
        <title>${muscle}: ${value} séries (${pct}%)</title>
      </rect>
      <text x="${x + barWidth/2}" y="${y - 5}" fill="var(--text)" font-size="10" text-anchor="middle" font-weight="bold">
        ${value}
      </text>
      <text x="${x + barWidth/2}" y="${height - paddingBottom + 15}" fill="${color}" font-size="9" text-anchor="middle" font-weight="600" transform="rotate(-45, ${x + barWidth/2}, ${height - paddingBottom + 15})">
        ${muscle.substring(0, 5)}
      </text>
    `;
  });
  
  // Linhas de grade horizontais
  let gridHtml = '';
  for (let i = 0; i <= 4; i++) {
    const y = height - paddingBottom - (i / 4) * (height - padding - paddingBottom);
    const val = Math.round((i / 4) * maxValue);
    gridHtml += `
      <line x1="${padding - 5}" y1="${y}" x2="${width - padding}" y2="${y}" stroke="var(--border)" stroke-dasharray="3,3" opacity="0.3"/>
      <text x="${padding - 10}" y="${y + 3}" fill="var(--text-muted)" font-size="8" text-anchor="end">${val}</text>
    `;
  }
  
  const svg = `
    <svg width="${width}" height="${height}" style="overflow:visible">
      ${gridHtml}
      ${barsHtml}
      <text x="${width/2}" y="${height - 5}" fill="var(--text-muted)" font-size="10" text-anchor="middle">Total: ${totalSets} séries</text>
    </svg>
  `;
  
  container.innerHTML = svg;
}

