// ==================== ESTATÍSTICAS DE HORÁRIO ====================

const TIME_SLOTS = [
  { id: 'madrugada', label: '🌙 Madrugada', range: '00-06h', min: 0, max: 5 },
  { id: 'manha_cedo', label: '🌅 Manhã Cedo', range: '06-09h', min: 6, max: 8 },
  { id: 'manha', label: '☀️ Manhã', range: '09-12h', min: 9, max: 11 },
  { id: 'almoco', label: '🍽️ Almoço', range: '12-14h', min: 12, max: 13 },
  { id: 'tarde', label: '🌤️ Tarde', range: '14-18h', min: 14, max: 17 },
  { id: 'noite', label: '🌆 Noite', range: '18-21h', min: 18, max: 20 },
  { id: 'noite_tarde', label: '🌃 Noite Tarde', range: '21-24h', min: 21, max: 23 }
];

function calculateHourlyStats() {
  const stats = {
    bySlot: {},
    byHour: Array(24).fill(0),
    totalWorkouts: 0,
    peakSlot: null,
    peakHour: null,
    avgVolumeBySlot: {}
  };
  
  // Inicializa slots
  TIME_SLOTS.forEach(slot => {
    stats.bySlot[slot.id] = { count: 0, totalVolume: 0, totalDuration: 0 };
  });
  
  workoutHistory.forEach(record => {
    const date = new Date(record.date);
    const hour = date.getHours();
    
    stats.byHour[hour]++;
    stats.totalWorkouts++;
    
    // Encontra o slot correspondente
    const slot = TIME_SLOTS.find(s => hour >= s.min && hour <= s.max);
    if (slot) {
      stats.bySlot[slot.id].count++;
      
      // Calcula volume
      if (record.exercises && record.loads) {
        Object.entries(record.exercises).forEach(([exercise, series]) => {
          if (['alongamento', 'cardioType', 'cardioTime', 'notes', 'loads', 'reps', 'rpes'].includes(exercise)) return;
          
          const numSeries = parseInt(series) || 0;
          const cleanEx = exercise.split('(')[0].trim();
          const load = parseFloat(record.loads[exercise]) || parseFloat(record.loads[cleanEx]) || 0;
          const reps = record.reps ? (parseInt(record.reps[exercise]) || parseInt(record.reps[cleanEx]) || 10) : 10;
          
          stats.bySlot[slot.id].totalVolume += numSeries * reps * load;
        });
      }
      
      // Duração
      if (record.durationMinutes) {
        stats.bySlot[slot.id].totalDuration += record.durationMinutes;
      }
    }
  });
  
  // Encontra pico
  let maxCount = 0;
  Object.entries(stats.bySlot).forEach(([id, data]) => {
    if (data.count > maxCount) {
      maxCount = data.count;
      stats.peakSlot = TIME_SLOTS.find(s => s.id === id);
    }
    
    // Média de volume
    stats.avgVolumeBySlot[id] = data.count > 0 ? Math.round(data.totalVolume / data.count) : 0;
  });
  
  // Hora de pico
  let maxHour = 0;
  stats.byHour.forEach((count, hour) => {
    if (count > stats.byHour[maxHour]) {
      maxHour = hour;
    }
  });
  stats.peakHour = maxHour;
  
  return stats;
}

function renderHourlyStats() {
  const container = document.getElementById('hourlyStatsChart');
  const summaryContainer = document.getElementById('hourlyStatsSummary');
  
  if (!container) return;
  
  const stats = calculateHourlyStats();
  
  if (stats.totalWorkouts === 0) {
    container.innerHTML = `
      <div style='text-align:center; padding:20px; color:var(--text-muted);'>
        <div style='font-size:24px; margin-bottom:10px;'>🕐</div>
        <div>Nenhum treino registrado ainda.</div>
      </div>
    `;
    if (summaryContainer) summaryContainer.innerHTML = '';
    return;
  }
  
  const maxCount = Math.max(...TIME_SLOTS.map(s => stats.bySlot[s.id].count));
  
  // Cores para os slots
  const slotColors = {
    'madrugada': '#6366f1',
    'manha_cedo': '#f59e0b',
    'manha': '#fbbf24',
    'almoco': '#84cc16',
    'tarde': '#22c55e',
    'noite': '#3b82f6',
    'noite_tarde': '#8b5cf6'
  };
  
  // Gráfico de barras horizontais
  let barsHtml = TIME_SLOTS.map(slot => {
    const data = stats.bySlot[slot.id];
    const pct = maxCount > 0 ? (data.count / maxCount) * 100 : 0;
    const color = slotColors[slot.id];
    const isPeak = stats.peakSlot && stats.peakSlot.id === slot.id;
    const avgVolume = stats.avgVolumeBySlot[slot.id];
    const avgVolumeDisplay = avgVolume >= 1000 ? (avgVolume / 1000).toFixed(1) + 't' : avgVolume + 'kg';
    
    return `
      <div style='display:flex; align-items:center; gap:8px; margin-bottom:8px; ${isPeak ? 'background:rgba(34,197,94,0.1); padding:8px; border-radius:8px; margin:-8px -8px 0 -8px; margin-bottom:8px;' : ''}'>
        <div style='width:100px; font-size:10px;'>
          <div style='color:${color}; font-weight:600;'>${slot.label}</div>
          <div style='color:var(--text-muted); font-size:9px;'>${slot.range}</div>
        </div>
        <div style='flex:1; height:20px; background:var(--bg); border-radius:10px; overflow:hidden; position:relative;'>
          <div style='width:${pct}%; height:100%; background:${color}; border-radius:10px;'></div>
          ${isPeak ? '<div style="position:absolute; right:5px; top:50%; transform:translateY(-50%); font-size:10px;">🏆</div>' : ''}
        </div>
        <div style='width:50px; text-align:right;'>
          <div style='font-size:12px; font-weight:700; color:var(--text);'>${data.count}</div>
          <div style='font-size:8px; color:var(--text-muted);'>${Math.round((data.count / stats.totalWorkouts) * 100)}%</div>
        </div>
      </div>
    `;
  }).join('');
  
  container.innerHTML = barsHtml;
  
  // Resumo
  if (summaryContainer && stats.peakSlot) {
    const peakData = stats.bySlot[stats.peakSlot.id];
    const avgDuration = peakData.count > 0 && peakData.totalDuration > 0 
      ? Math.round(peakData.totalDuration / peakData.count) 
      : null;
    
    // Distribuição simples (manhã vs tarde vs noite)
    const morning = (stats.bySlot['manha_cedo'].count + stats.bySlot['manha'].count);
    const afternoon = (stats.bySlot['almoco'].count + stats.bySlot['tarde'].count);
    const evening = (stats.bySlot['noite'].count + stats.bySlot['noite_tarde'].count + stats.bySlot['madrugada'].count);
    
    const total = morning + afternoon + evening;
    const morningPct = total > 0 ? Math.round((morning / total) * 100) : 0;
    const afternoonPct = total > 0 ? Math.round((afternoon / total) * 100) : 0;
    const eveningPct = total > 0 ? Math.round((evening / total) * 100) : 0;
    
    summaryContainer.innerHTML = `
      <div style='display:grid; grid-template-columns:repeat(3, 1fr); gap:10px; margin-bottom:12px;'>
        <div style='text-align:center; padding:10px; background:var(--bg-input); border-radius:8px;'>
          <div style='font-size:20px;'>🌅</div>
          <div style='font-size:16px; font-weight:700; color:var(--warning);'>${morningPct}%</div>
          <div style='font-size:9px; color:var(--text-muted);'>Manhã</div>
        </div>
        <div style='text-align:center; padding:10px; background:var(--bg-input); border-radius:8px;'>
          <div style='font-size:20px;'>☀️</div>
          <div style='font-size:16px; font-weight:700; color:var(--success);'>${afternoonPct}%</div>
          <div style='font-size:9px; color:var(--text-muted);'>Tarde</div>
        </div>
        <div style='text-align:center; padding:10px; background:var(--bg-input); border-radius:8px;'>
          <div style='font-size:20px;'>🌙</div>
          <div style='font-size:16px; font-weight:700; color:var(--primary);'>${eveningPct}%</div>
          <div style='font-size:9px; color:var(--text-muted);'>Noite</div>
        </div>
      </div>
      
      <div style='background:linear-gradient(135deg, var(--primary), #8b5cf6); padding:12px; border-radius:10px; text-align:center;'>
        <div style='font-size:10px; color:rgba(255,255,255,0.8);'>🏆 Seu Horário Favorito</div>
        <div style='font-size:18px; font-weight:700; color:white;'>${stats.peakSlot.label}</div>
        <div style='font-size:11px; color:rgba(255,255,255,0.7);'>
          ${peakData.count} treinos (${Math.round((peakData.count / stats.totalWorkouts) * 100)}% do total)
          ${avgDuration ? ` • Média: ${avgDuration}min` : ''}
        </div>
      </div>
    `;
  }
}


