function createShortcut(tabName) {
  const baseUrl = window.location.href.split('?')[0].split('#')[0];
  const shortcutUrl = `${baseUrl}?tab=${tabName}`;
  
  // Tenta copiar para a área de transferência
  if (navigator.clipboard && navigator.clipboard.writeText) {
    navigator.clipboard.writeText(shortcutUrl).then(() => {
      showToast(`📋 Link copiado! Cole no navegador e adicione à tela inicial.`);
    }).catch(() => {
      fallbackCopyShortcut(shortcutUrl);
    });
  } else {
    fallbackCopyShortcut(shortcutUrl);
  }
}

function fallbackCopyShortcut(url) {
  const textarea = document.createElement('textarea');
  textarea.value = url;
  textarea.style.position = 'fixed';
  textarea.style.opacity = '0';
  document.body.appendChild(textarea);
  textarea.select();
  
  try {
    document.execCommand('copy');
    showToast(`📋 Link copiado! Cole no navegador e adicione à tela inicial.`);
  } catch (err) {
    // Se não conseguir copiar, mostra o link para o usuário
    prompt('Copie este link:', url);
  }
  
  document.body.removeChild(textarea);
}

