function saveTabataToHistory() {
  const totalWorkTime = tabataState.workTime * tabataState.totalRounds;
  const totalRestTime = tabataState.restTime * (tabataState.totalRounds - 1);
  const calories = Math.round((totalWorkTime * 0.2) + (totalRestTime * 0.07));
  
  let presetName = 'Personalizado';
  for (const [key, preset] of Object.entries(TABATA_PRESETS)) {
    if (preset.work === tabataState.workTime && preset.rest === tabataState.restTime && preset.rounds === tabataState.totalRounds) {
      presetName = preset.name;
      break;
    }
  }
  
  const record = {
    id: Date.now(),
    date: new Date().toISOString(),
    dayName: `Tabata - ${presetName}`,
    dayIndex: new Date().getDay(),
    isTabata: true,
    exercises: { [`Tabata ${presetName}`]: tabataState.totalRounds, cardioType: 'Tabata/HIIT', cardioTime: Math.round(tabataState.totalElapsed / 60) },
    tabataDetails: { preset: presetName, workTime: tabataState.workTime, restTime: tabataState.restTime, rounds: tabataState.totalRounds, totalWorkSeconds: totalWorkTime, totalRestSeconds: totalRestTime, calories },
    durationMinutes: Math.round(tabataState.totalElapsed / 60),
    notes: `${tabataState.workTime}s/${tabataState.restTime}s × ${tabataState.totalRounds} rounds | ~${calories} kcal`,
    weight: typeof weightHistory !== 'undefined' && weightHistory.length > 0 ? weightHistory[0].weight : null
  };
  
  if (typeof workoutHistory !== 'undefined') {
    workoutHistory.unshift(record);
    if (typeof saveData === 'function') saveData();
    if (typeof renderHistory === 'function') renderHistory();
    if (typeof renderWeeklyGoal === 'function') renderWeeklyGoal();
    showToast('✅ Tabata salvo no histórico!');
  }
}

// === ÁUDIO ===
function playTabataBeep(isLong) {
  if (!document.getElementById('tabataSoundEnabled')?.checked) return;
  try {
    const ctx = new (window.AudioContext || window.webkitAudioContext)();
    const osc = ctx.createOscillator();
    const gain = ctx.createGain();
    osc.connect(gain);
    gain.connect(ctx.destination);
    osc.type = 'sine';
    osc.frequency.value = isLong ? 880 : 660;
    gain.gain.value = 0.3;
    osc.start();
    osc.stop(ctx.currentTime + (isLong ? 0.3 : 0.15));
  } catch (e) {}
}

function playTabataFinish() {
  if (!document.getElementById('tabataSoundEnabled')?.checked) return;
  try {
    const ctx = new (window.AudioContext || window.webkitAudioContext)();
    [523, 659, 784, 1047].forEach((freq, i) => {
      const osc = ctx.createOscillator();
      const gain = ctx.createGain();
      osc.connect(gain);
      gain.connect(ctx.destination);
      osc.type = 'sine';
      osc.frequency.value = freq;
      gain.gain.value = 0.2;
      osc.start(ctx.currentTime + (i * 0.15));
      osc.stop(ctx.currentTime + (i * 0.15) + 0.2);
    });
  } catch (e) {}
}

function speakTabata(text) {
  if (!document.getElementById('tabataVoiceEnabled')?.checked) return;
  if ('speechSynthesis' in window) {
    const utter = new SpeechSynthesisUtterance(text);
    utter.lang = 'pt-BR';
    utter.rate = 1.1;
    utter.volume = 0.8;
    speechSynthesis.speak(utter);
  }
}

function vibrateTabata(pattern) {
  if (!document.getElementById('tabataVibrateEnabled')?.checked) return;
  if (navigator.vibrate) navigator.vibrate(pattern);
}

// === WAKE LOCK ===
async function requestWakeLock() {
  if (!document.getElementById('tabataWakeLock')?.checked) return;
  try {
    if ('wakeLock' in navigator) {
      tabataState.wakeLock = await navigator.wakeLock.request('screen');
    }
  } catch (e) {}
}

function releaseWakeLock() {
  if (tabataState.wakeLock) {
    tabataState.wakeLock.release();
    tabataState.wakeLock = null;
  }
}

