let currentCardTheme = 'dark';
let currentCardData = null;

function generateShareCard(recordData = null) {
  // Coleta dados do treino atual ou usa o passado
  currentCardData = recordData || collectCurrentWorkoutData();
  
  if (!currentCardData || !currentCardData.exercises || Object.keys(currentCardData.exercises).length === 0) {
    showToast('⚠️ Marque alguns exercícios primeiro!');
    return;
  }
  
  // Abre o modal
  document.getElementById('shareCardModal').classList.add('active');
  
  // Desenha o cartão
  drawShareCard();
}

function collectCurrentWorkoutData() {
  const workout = getWorkoutForDay(currentDayIndex);
  const today = new Date();
  
  let exercises = {};
  let prs = [];
  let totalSeries = 0;
  
  // Coleta exercícios marcados
  Object.entries(currentWorkout).forEach(([key, val]) => {
    if (['alongamento', 'cardioType', 'cardioTime', 'notes', 'loads', 'reps', 'rpes'].includes(key)) return;
    if (key === 'alongamento') return;
    
    const series = parseInt(val) || 0;
    if (series > 0) {
      const cleanKey = key.split('(')[0].trim();
      exercises[cleanKey] = series;
      totalSeries += series;
      
      // Verifica se é PR
      const loadInput = document.querySelector(`.load-input[data-ex="${cleanKey}"]`);
      if (loadInput && loadInput.value) {
        const currentLoad = parseFloat(loadInput.value);
        const previousBest = personalRecords[cleanKey] || 0;
        if (currentLoad > previousBest && previousBest > 0) {
          prs.push({ name: cleanKey, load: currentLoad });
        }
      }
    }
  });
  
  return {
    date: today,
    dayName: workout.name,
    exercises: exercises,
    totalSeries: totalSeries,
    prs: prs,
    cardio: currentWorkout.cardioType ? `${currentWorkout.cardioType} ${currentWorkout.cardioTime || 20}min` : null,
    weight: weightHistory.length > 0 ? weightHistory[0].weight : null
  };
}

