function displayDailyQuote() {
  const quoteText = document.getElementById('dailyQuoteText');
  const quoteAuthor = document.getElementById('dailyQuoteAuthor');
  
  if (!quoteText || !quoteAuthor) return;
  
  // Usa o dia do ano para selecionar a citação (muda todo dia)
  const now = new Date();
  const start = new Date(now.getFullYear(), 0, 0);
  const diff = now - start;
  const oneDay = 1000 * 60 * 60 * 24;
  const dayOfYear = Math.floor(diff / oneDay);
  
  // Seleciona citação baseada no dia
  const quoteIndex = dayOfYear % STOIC_QUOTES.length;
  const quote = STOIC_QUOTES[quoteIndex];
  
  quoteText.textContent = `"${quote.text}"`;
  quoteAuthor.textContent = `— ${quote.author}`;
}







