// Renderizar lista de rotinas
function renderMobilityRoutines(filter = 'all') {
  const container = document.getElementById('mobilityRoutinesList');
  if (!container) return;
  
  const filteredRoutines = filter === 'all' 
    ? mobilityRoutines 
    : mobilityRoutines.filter(r => r.category === filter);
  
  container.innerHTML = filteredRoutines.map(routine => {
    const totalDuration = routine.exercises.reduce((sum, ex) => sum + ex.duration, 0);
    const minutes = Math.floor(totalDuration / 60);
    const seconds = totalDuration % 60;
    
    return `
      <div class="mobility-routine-card" onclick="selectMobilityRoutine('${routine.id}')" style="border-color: ${routine.color}20;">
        <div class="routine-icon" style="font-size: 36px;">${routine.icon}</div>
        <div class="routine-name">${routine.name}</div>
        <div class="routine-desc">${routine.description}</div>
        <div class="routine-meta">
          <span class="routine-tag">${routine.exercises.length} exercícios</span>
          <span class="routine-difficulty ${routine.difficulty.toLowerCase()}">${routine.difficulty}</span>
        </div>
        <div class="routine-duration" style="background: ${routine.color};">${minutes}:${seconds.toString().padStart(2, '0')}</div>
      </div>
    `;
  }).join('');
}

// Filtrar rotinas
function filterMobilityRoutines(category) {
  document.querySelectorAll('.mobility-filter-btn').forEach(btn => btn.classList.remove('active'));
  event.target.classList.add('active');
  renderMobilityRoutines(category);
}

// Selecionar rotina
function selectMobilityRoutine(routineId) {
  const routine = mobilityRoutines.find(r => r.id === routineId);
  if (!routine) return;
  
  mobilityState.routine = routine;
  mobilityState.currentIndex = 0;
  mobilityState.isPlaying = false;
  mobilityState.totalTime = routine.exercises.reduce((sum, ex) => sum + ex.duration, 0);
  mobilityState.elapsedTime = 0;
  mobilityState.currentSide = null;
  mobilityState.startTime = null;
  
  // Mostrar player
  document.getElementById('mobilityPlayerCard').style.display = 'block';
  document.getElementById('mobilityExerciseList').style.display = 'block';
  document.getElementById('mobilityCompleteCard').style.display = 'none';
  
  // Atualizar header
  document.getElementById('mobilityRoutineName').textContent = routine.name;
  document.getElementById('mobilityRoutineName').style.color = routine.color;
  
  // Carregar primeiro exercício
  loadMobilityExercise(0);
  renderMobilityExerciseList();
  updateMobilityTimeRemaining();
  
  // Scroll para o player
  document.getElementById('mobilityPlayerCard').scrollIntoView({ behavior: 'smooth' });
}

// Carregar exercício
function loadMobilityExercise(index) {
  const routine = mobilityState.routine;
  if (!routine || index >= routine.exercises.length) return;
  
  const exercise = routine.exercises[index];
  mobilityState.currentIndex = index;
  mobilityState.timeRemaining = Math.round(exercise.duration / mobilityState.speed);
  
  // Reset do lado para exercícios bilaterais
  if (exercise.bilateral) {
    mobilityState.currentSide = 'left';
    document.getElementById('mobilitySideIndicator').style.display = 'block';
    document.getElementById('mobilitySideText').textContent = '⬅️ Lado Esquerdo';
  } else {
    mobilityState.currentSide = null;
    document.getElementById('mobilitySideIndicator').style.display = 'none';
  }
  
  // Atualizar display
  document.getElementById('mobilityExerciseIcon').textContent = exercise.icon;
  document.getElementById('mobilityExerciseName').textContent = exercise.name;
  document.getElementById('mobilityExerciseTip').textContent = exercise.tip;
  document.getElementById('mobilityTimer').textContent = formatMobilityTime(mobilityState.timeRemaining);
  document.getElementById('mobilityExerciseProgress').style.width = '100%';
  document.getElementById('mobilityRoutineProgress').textContent = `Exercício ${index + 1} de ${routine.exercises.length}`;
  
  // Próximo exercício
  const nextExercise = routine.exercises[index + 1];
  document.getElementById('mobilityNextUp').textContent = nextExercise 
    ? `Próximo: ${nextExercise.icon} ${nextExercise.name}` 
    : '🎉 Último exercício!';
  
  // Atualizar lista
  renderMobilityExerciseList();
  updateMobilityTimeRemaining();
}

// Atualizar tempo restante total
function updateMobilityTimeRemaining() {
  const routine = mobilityState.routine;
  if (!routine) return;
  
  let remaining = mobilityState.timeRemaining;
  for (let i = mobilityState.currentIndex + 1; i < routine.exercises.length; i++) {
    remaining += Math.round(routine.exercises[i].duration / mobilityState.speed);
  }
  
  document.getElementById('mobilityTimeRemaining').textContent = formatMobilityTime(remaining);
}

// Formatar tempo
function formatMobilityTime(seconds) {
  const mins = Math.floor(seconds / 60);
  const secs = seconds % 60;
  return `${mins.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
}

// Toggle play/pause
function toggleMobilityPlay() {
  if (mobilityState.isPlaying) {
    pauseMobility();
  } else {
    startMobility();
  }
}

// Iniciar
function startMobility() {
  mobilityState.isPlaying = true;
  if (!mobilityState.startTime) {
    mobilityState.startTime = new Date();
  }
  
  document.getElementById('mobilityPlayBtn').innerHTML = '<span>⏸️</span><span>Pausar</span>';
  document.getElementById('mobilityDisplay').classList.add('state-active');
  
  // Iniciar indicador de respiração
  if (document.getElementById('mobilityBreathEnabled').checked) {
    startBreathIndicator();
  }
  
  // Anunciar exercício
  if (document.getElementById('mobilityVoiceEnabled').checked) {
    speakMobility(mobilityState.routine.exercises[mobilityState.currentIndex].name);
  }
  
  mobilityState.interval = setInterval(mobilityTick, 1000);
}

// Pausar
function pauseMobility() {
  mobilityState.isPlaying = false;
  document.getElementById('mobilityPlayBtn').innerHTML = '<span>▶️</span><span>Continuar</span>';
  document.getElementById('mobilityDisplay').classList.remove('state-active');
  
  stopBreathIndicator();
  
  if (mobilityState.interval) {
    clearInterval(mobilityState.interval);
    mobilityState.interval = null;
  }
}

// Tick do timer
function mobilityTick() {
  if (mobilityState.timeRemaining <= 0) {
    // Verificar se é exercício bilateral e precisa trocar de lado
    const exercise = mobilityState.routine.exercises[mobilityState.currentIndex];
    if (exercise.bilateral && mobilityState.currentSide === 'left') {
      // Trocar para lado direito
      mobilityState.currentSide = 'right';
      mobilityState.timeRemaining = Math.round(exercise.duration / mobilityState.speed);
      document.getElementById('mobilitySideText').textContent = '➡️ Lado Direito';
      document.getElementById('mobilityExerciseProgress').style.width = '100%';
      
      if (document.getElementById('mobilityVoiceEnabled').checked) {
        speakMobility('Agora lado direito');
      }
      if (document.getElementById('mobilitySoundEnabled').checked) {
        playMobilityTransition();
      }
      if (document.getElementById('mobilityVibrateEnabled').checked && navigator.vibrate) {
        navigator.vibrate([100, 50, 100]);
      }
      return;
    }
    
    // Próximo exercício
    mobilityNextExercise();
    return;
  }
  
  mobilityState.timeRemaining--;
  mobilityState.elapsedTime++;
  
  const exercise = mobilityState.routine.exercises[mobilityState.currentIndex];
  const totalExerciseTime = Math.round(exercise.duration / mobilityState.speed);
  const progress = (mobilityState.timeRemaining / totalExerciseTime) * 100;
  
  // Atualizar display
  document.getElementById('mobilityTimer').textContent = formatMobilityTime(mobilityState.timeRemaining);
  document.getElementById('mobilityExerciseProgress').style.width = progress + '%';
  
  // Progresso total
  const totalProgress = (mobilityState.elapsedTime / (mobilityState.totalTime / mobilityState.speed)) * 100;
  document.getElementById('mobilityTotalProgress').style.width = Math.min(totalProgress, 100) + '%';
  document.getElementById('mobilityTotalProgressText').textContent = Math.round(totalProgress) + '%';
  
  updateMobilityTimeRemaining();
  
  // Countdown nos últimos 3 segundos
  if (mobilityState.timeRemaining <= 3 && mobilityState.timeRemaining > 0) {
    document.getElementById('mobilityDisplay').classList.add('state-countdown');
    showMobilityCountdown(mobilityState.timeRemaining);
    
    if (document.getElementById('mobilitySoundEnabled').checked) {
      playMobilityBeep();
    }
    if (document.getElementById('mobilityVibrateEnabled').checked && navigator.vibrate) {
      navigator.vibrate(100);
    }
  } else {
    document.getElementById('mobilityDisplay').classList.remove('state-countdown');
    hideMobilityCountdown();
  }
}

// Mostrar countdown visual
function showMobilityCountdown(number) {
  const overlay = document.getElementById('mobilityCountdownOverlay');
  const numEl = document.getElementById('mobilityCountdownNumber');
  numEl.textContent = number;
  overlay.classList.add('visible');
}

function hideMobilityCountdown() {
  document.getElementById('mobilityCountdownOverlay').classList.remove('visible');
}

// Próximo exercício automático
function mobilityNextExercise() {
  const nextIndex = mobilityState.currentIndex + 1;
  
  if (nextIndex >= mobilityState.routine.exercises.length) {
    // Rotina completa
    completeMobilityRoutine();
    return;
  }
  
  // Som/vibração de transição
  if (document.getElementById('mobilitySoundEnabled').checked) {
    playMobilityTransition();
  }
  if (document.getElementById('mobilityVibrateEnabled').checked && navigator.vibrate) {
    navigator.vibrate([100, 50, 100]);
  }
  
  loadMobilityExercise(nextIndex);
  
  // Anunciar próximo
  if (document.getElementById('mobilityVoiceEnabled').checked) {
    setTimeout(() => {
      speakMobility(mobilityState.routine.exercises[nextIndex].name);
    }, 300);
  }
}

// Botão próximo
function mobilityNext() {
  mobilityNextExercise();
}

// Botão anterior
function mobilityPrev() {
  if (mobilityState.currentIndex > 0) {
    const prevExercise = mobilityState.routine.exercises[mobilityState.currentIndex];
    mobilityState.elapsedTime -= (Math.round(prevExercise.duration / mobilityState.speed) - mobilityState.timeRemaining);
    
    loadMobilityExercise(mobilityState.currentIndex - 1);
    mobilityState.elapsedTime -= Math.round(mobilityState.routine.exercises[mobilityState.currentIndex].duration / mobilityState.speed);
    if (mobilityState.elapsedTime < 0) mobilityState.elapsedTime = 0;
  }
}

// Skip segundos
function mobilitySkip(seconds) {
  mobilityState.timeRemaining += seconds;
  if (mobilityState.timeRemaining < 0) mobilityState.timeRemaining = 0;
  const maxTime = Math.round(mobilityState.routine.exercises[mobilityState.currentIndex].duration / mobilityState.speed);
  if (mobilityState.timeRemaining > maxTime) mobilityState.timeRemaining = maxTime;
  document.getElementById('mobilityTimer').textContent = formatMobilityTime(mobilityState.timeRemaining);
}

// Velocidade
function setMobilitySpeed(speed) {
  mobilityState.speed = speed;
  document.querySelectorAll('.speed-btn').forEach(btn => btn.classList.remove('active'));
  event.target.classList.add('active');
  
  // Recalcular tempo do exercício atual
  const exercise = mobilityState.routine.exercises[mobilityState.currentIndex];
  const originalTime = exercise.duration;
  const elapsed = originalTime - (mobilityState.timeRemaining * mobilityState.speed);
  mobilityState.timeRemaining = Math.round((originalTime - elapsed) / speed);
  
  document.getElementById('mobilityTimer').textContent = formatMobilityTime(mobilityState.timeRemaining);
  updateMobilityTimeRemaining();
}

// Fullscreen
function toggleMobilityFullscreen() {
  const player = document.getElementById('mobilityPlayerCard');
  mobilityState.isFullscreen = !mobilityState.isFullscreen;
  
  if (mobilityState.isFullscreen) {
    player.classList.add('fullscreen');
    document.getElementById('fullscreenIcon').textContent = '⛶';
    document.body.style.overflow = 'hidden';
  } else {
    player.classList.remove('fullscreen');
    document.getElementById('fullscreenIcon').textContent = '⛶';
    document.body.style.overflow = '';
  }
}

// Rotina completa - VERSÃO ATUALIZADA (salva em ambos históricos)
function completeMobilityRoutine() {
  pauseMobility();
  
  const endTime = new Date();
  const durationMs = endTime - mobilityState.startTime;
  const durationMinutes = Math.round(durationMs / 60000);
  
  // Esconde player e mostra card de conclusão
  document.getElementById('mobilityPlayerCard').style.display = 'none';
  document.getElementById('mobilityExerciseList').style.display = 'none';
  document.getElementById('mobilityCompleteCard').style.display = 'block';
  
  // Preencher estatísticas de conclusão
  document.getElementById('mobilityCompleteName').textContent = mobilityState.routine.name;
  document.getElementById('completeExercises').textContent = mobilityState.routine.exercises.length;
  document.getElementById('completeTime').textContent = formatMobilityTime(Math.round(mobilityState.totalTime / mobilityState.speed));
  document.getElementById('completeCalories').textContent = '~' + Math.round(durationMinutes * 3);
  
  // Confetti
  createMobilityConfetti();
  
  // ========== SALVAR NO HISTÓRICO DE MOBILIDADE ==========
  saveMobilitySession(durationMinutes);
  
  // ========== SALVAR NO HISTÓRICO DE TREINOS (NOVO!) ==========
  saveMobilityToWorkoutHistory(durationMinutes);
  
  // Atualizar estatísticas de mobilidade
  updateMobilityStats();
  
  // Som de conclusão
  if (document.getElementById('mobilitySoundEnabled').checked) {
    playMobilityComplete();
  }
  if (document.getElementById('mobilityVibrateEnabled').checked && navigator.vibrate) {
    navigator.vibrate([200, 100, 200, 100, 400]);
  }
  if (document.getElementById('mobilityVoiceEnabled').checked) {
    speakMobility('Rotina concluída! Parabéns!');
  }
  
  showToast('🎉 Rotina de mobilidade concluída e salva no histórico!');
}

// ========== SALVAR MOBILIDADE NO HISTÓRICO DE TREINOS ==========
function saveMobilityToWorkoutHistory(durationMinutes) {
  // Monta a lista de exercícios da rotina
  const exercisesObj = {};
  
  // Adiciona cada exercício da rotina
  mobilityState.routine.exercises.forEach((ex, index) => {
    exercisesObj[ex.name] = 1; // 1 "série" por exercício
  });
  
  // Determina o tipo/categoria da rotina
  let categoryLabel = '';
  switch(mobilityState.routine.category) {
    case 'hipopressivo':
      categoryLabel = '🫁 Hipopressivo';
      break;
    case 'morning':
      categoryLabel = '🌅 Mobilidade Matinal';
      break;
    case 'post':
      categoryLabel = '🏋️ Pós-Treino';
      break;
    case 'focus':
      categoryLabel = '🎯 Mobilidade Foco';
      break;
    case 'relax':
      categoryLabel = '😴 Relaxamento';
      break;
    case 'quick':
      categoryLabel = '⚡ Mobilidade Rápida';
      break;
    case 'workout':
      categoryLabel = '💪 Treino';
      break;
    default:
      categoryLabel = '🧘 Mobilidade';
  }
  
  // Cria o registro no formato do histórico de treinos
  const record = {
    id: Date.now(),
    date: new Date().toISOString(),
    dayName: `${mobilityState.routine.icon} ${mobilityState.routine.name}`,
    dayIndex: -1, // -1 indica que não é um treino do plano semanal
    exercises: exercisesObj,
    loads: {}, // Mobilidade não tem cargas
    reps: {},  // Mobilidade não tem reps
    rpes: {},  // Mobilidade não tem RPE
    notes: `${categoryLabel} | ${mobilityState.routine.exercises.length} exercícios | Dificuldade: ${mobilityState.routine.difficulty}`,
    weight: typeof weightHistory !== 'undefined' && weightHistory.length > 0 ? weightHistory[0].weight : null,
    prs: null, // Mobilidade não tem PRs
    durationMinutes: durationMinutes,
    startTime: mobilityState.startTime ? mobilityState.startTime.toISOString() : null,
    endTime: new Date().toISOString(),
    // Campos extras para identificar que é mobilidade
    isMobility: true,
    mobilityRoutineId: mobilityState.routine.id,
    mobilityCategory: mobilityState.routine.category
  };
  
  // Adiciona ao histórico de treinos
  if (typeof workoutHistory !== 'undefined') {
    workoutHistory.unshift(record);
    
    // Salva no localStorage
    if (typeof saveData === 'function') {
      saveData();
    } else {
      localStorage.setItem('workoutHistory', JSON.stringify(workoutHistory));
    }
    
    // Atualiza as renderizações
    if (typeof renderHistory === 'function') {
      renderHistory();
    }
    if (typeof renderWeeklyGoal === 'function') {
      renderWeeklyGoal();
    }
    if (typeof renderCalendar === 'function') {
      try { renderCalendar(); } catch(e) { console.log('Calendário não disponível'); }
    }
    if (typeof renderStats === 'function') {
      try { renderStats(); } catch(e) { console.log('Stats não disponível'); }
    }
    if (typeof renderConquistasTab === 'function') {
      try { renderConquistasTab(); } catch(e) { console.log('Conquistas não disponível'); }
    }
  }
}

// Confetti
function createMobilityConfetti() {
  const container = document.getElementById('mobilityConfetti');
  container.innerHTML = '';
  const colors = ['#f59e0b', '#22c55e', '#6366f1', '#ec4899', '#14b8a6'];
  
  for (let i = 0; i < 50; i++) {
    const confetti = document.createElement('div');
    confetti.className = 'confetti-piece';
    confetti.style.left = Math.random() * 100 + '%';
    confetti.style.animationDelay = Math.random() * 2 + 's';
    confetti.style.backgroundColor = colors[Math.floor(Math.random() * colors.length)];
    container.appendChild(confetti);
  }
  
  setTimeout(() => container.innerHTML = '', 4000);
}

// Salvar sessão
function saveMobilitySession(duration) {
  const session = {
    routineId: mobilityState.routine.id,
    routineName: mobilityState.routine.name,
    routineIcon: mobilityState.routine.icon,
    date: new Date().toISOString(),
    duration: duration,
    exercises: mobilityState.routine.exercises.length
  };
  
  mobilityHistory.unshift(session);
  if (mobilityHistory.length > 50) mobilityHistory.pop();
  localStorage.setItem('mobilityHistory', JSON.stringify(mobilityHistory));
  
  // Atualizar stats
  mobilityStats.totalRoutines++;
  mobilityStats.totalMinutes += duration;
  
  // Streak
  const today = new Date().toDateString();
  if (mobilityStats.lastDate !== today) {
    const yesterday = new Date();
    yesterday.setDate(yesterday.getDate() - 1);
    if (mobilityStats.lastDate === yesterday.toDateString()) {
      mobilityStats.currentStreak++;
    } else if (mobilityStats.lastDate !== today) {
      mobilityStats.currentStreak = 1;
    }
    mobilityStats.lastDate = today;
  }
  
  // Contagem por rotina
  mobilityStats.routineCounts[mobilityState.routine.id] = 
    (mobilityStats.routineCounts[mobilityState.routine.id] || 0) + 1;
  
  localStorage.setItem('mobilityStats', JSON.stringify(mobilityStats));
}

// Atualizar exibição de stats
function updateMobilityStats() {
  document.getElementById('mobilityTotalRoutines').textContent = mobilityStats.totalRoutines;
  document.getElementById('mobilityTotalMinutes').textContent = mobilityStats.totalMinutes;
  document.getElementById('mobilityCurrentStreak').textContent = mobilityStats.currentStreak;
  
  // Encontrar rotina favorita
  let maxCount = 0;
  let favoriteId = null;
  for (const [id, count] of Object.entries(mobilityStats.routineCounts)) {
    if (count > maxCount) {
      maxCount = count;
      favoriteId = id;
    }
  }
  
  if (favoriteId) {
    const favorite = mobilityRoutines.find(r => r.id === favoriteId);
    document.getElementById('mobilityFavoriteRoutine').textContent = favorite ? favorite.icon : '-';
  }
  
  // Atualizar histórico
  renderMobilityHistory();
}

// Renderizar histórico
function renderMobilityHistory() {
  const container = document.getElementById('mobilityHistoryList');
  if (!container) return;
  
  if (mobilityHistory.length === 0) {
    container.innerHTML = `
      <div class='empty-state' style='padding: 20px;'>
        <div class='empty-state-icon'>🧘</div>
        <div>Complete sua primeira rotina para ver o histórico</div>
      </div>
    `;
    return;
  }
  
  container.innerHTML = mobilityHistory.slice(0, 10).map(session => {
    const date = new Date(session.date);
    const dateStr = date.toLocaleDateString('pt-BR', { 
      day: '2-digit', 
      month: 'short',
      hour: '2-digit',
      minute: '2-digit'
    });
    
    return `
      <div class="mobility-history-item">
        <div class="history-icon">${session.routineIcon}</div>
        <div class="history-info">
          <div class="history-name">${session.routineName}</div>
          <div class="history-date">${dateStr}</div>
        </div>
        <div class="history-stats">
          <span>${session.exercises} ex</span>
          <span>${session.duration}min</span>
        </div>
      </div>
    `;
  }).join('');
}

// Fechar player
function closeMobilityPlayer() {
  pauseMobility();
  document.getElementById('mobilityPlayerCard').style.display = 'none';
  document.getElementById('mobilityExerciseList').style.display = 'none';
  
  if (mobilityState.isFullscreen) {
    toggleMobilityFullscreen();
  }
  
  mobilityState.routine = null;
}

// Reset
function resetMobilityPlayer() {
  document.getElementById('mobilityCompleteCard').style.display = 'none';
  mobilityState.routine = null;
  window.scrollTo({ top: 0, behavior: 'smooth' });
}

// Repetir rotina
function repeatMobilityRoutine() {
  const routineId = mobilityState.routine.id;
  resetMobilityPlayer();
  setTimeout(() => selectMobilityRoutine(routineId), 100);
}

// Toggle lista de exercícios
function toggleExerciseList() {
  const content = document.getElementById('mobilityExerciseListContent');
  const toggle = document.getElementById('exerciseListToggle');
  content.classList.toggle('collapsed');
  toggle.innerHTML = content.classList.contains('collapsed') ? '<span>▶</span>' : '<span>▼</span>';
}

// Renderizar lista de exercícios
function renderMobilityExerciseList() {
  const container = document.getElementById('mobilityExerciseListContent');
  if (!container || !mobilityState.routine) return;
  
  container.innerHTML = mobilityState.routine.exercises.map((ex, i) => {
    let stateClass = '';
    if (i < mobilityState.currentIndex) stateClass = 'completed';
    else if (i === mobilityState.currentIndex) stateClass = 'current';
    
    return `
      <div class="mobility-exercise-item ${stateClass}" onclick="jumpToExercise(${i})">
        <div class="exercise-number">${i < mobilityState.currentIndex ? '✓' : i + 1}</div>
        <div class="exercise-icon">${ex.icon}</div>
        <div class="exercise-info">
          <div class="exercise-name">${ex.name}</div>
          <div class="exercise-duration">${ex.duration}s ${ex.bilateral ? '(bilateral)' : ''}</div>
        </div>
      </div>
    `;
  }).join('');
}

// Pular para exercício
function jumpToExercise(index) {
  if (mobilityState.isPlaying) pauseMobility();
  
  // Recalcular tempo decorrido
  mobilityState.elapsedTime = 0;
  for (let i = 0; i < index; i++) {
    mobilityState.elapsedTime += Math.round(mobilityState.routine.exercises[i].duration / mobilityState.speed);
  }
  
  loadMobilityExercise(index);
}

// Indicador de respiração
let breathInterval = null;

function startBreathIndicator() {
  const indicator = document.getElementById('mobilityBreathIndicator');
  const text = document.getElementById('breathText');
  indicator.classList.add('active');
  
  let phase = 'inhale';
  
  breathInterval = setInterval(() => {
    if (phase === 'inhale') {
      text.textContent = 'Inspire...';
      indicator.classList.remove('exhale');
      indicator.classList.add('inhale');
      phase = 'exhale';
    } else {
      text.textContent = 'Expire...';
      indicator.classList.remove('inhale');
      indicator.classList.add('exhale');
      phase = 'inhale';
    }
  }, 4000);
}

function stopBreathIndicator() {
  const indicator = document.getElementById('mobilityBreathIndicator');
  indicator.classList.remove('active', 'inhale', 'exhale');
  if (breathInterval) {
    clearInterval(breathInterval);
    breathInterval = null;
  }
}

// Rotina personalizada
function renderCustomExercises() {
  const container = document.getElementById('customExercisesGrid');
  if (!container) return;
  
  container.innerHTML = allMobilityExercises.map(ex => `
    <div class="custom-exercise-item" onclick="toggleCustomExercise(${ex.id})" id="customEx${ex.id}">
      <span class="custom-ex-icon">${ex.icon}</span>
      <span class="custom-ex-name">${ex.name}</span>
    </div>
  `).join('');
}

function toggleCustomExercise(id) {
  const el = document.getElementById('customEx' + id);
  const index = mobilityState.selectedExercises.indexOf(id);
  
  if (index > -1) {
    mobilityState.selectedExercises.splice(index, 1);
    el.classList.remove('selected');
  } else {
    mobilityState.selectedExercises.push(id);
    el.classList.add('selected');
  }
  
  document.getElementById('customSelectedCount').textContent = 
    `${mobilityState.selectedExercises.length} exercícios selecionados`;
}

function setCustomDuration(duration) {
  mobilityState.customDuration = duration;
  document.querySelectorAll('.duration-btn').forEach(btn => btn.classList.remove('active'));
  event.target.classList.add('active');
}

function startCustomRoutine() {
  if (mobilityState.selectedExercises.length < 2) {
    showToast('⚠️ Selecione pelo menos 2 exercícios');
    return;
  }
  
  const customName = document.getElementById('customRoutineName').value || 'Rotina Personalizada';
  
  const customRoutine = {
    id: 'custom_' + Date.now(),
    name: customName,
    icon: '✨',
    description: 'Sua rotina personalizada',
    color: '#6366f1',
    category: 'custom',
    exercises: mobilityState.selectedExercises.map(id => {
      const ex = allMobilityExercises.find(e => e.id === id);
      return {
        name: ex.name,
        duration: mobilityState.customDuration,
        icon: ex.icon,
        tip: ex.tip,
        bilateral: false
      };
    })
  };
  
  mobilityState.routine = customRoutine;
  mobilityState.currentIndex = 0;
  mobilityState.isPlaying = false;
  mobilityState.totalTime = customRoutine.exercises.length * mobilityState.customDuration;
  mobilityState.elapsedTime = 0;
  mobilityState.startTime = null;
  
  document.getElementById('mobilityPlayerCard').style.display = 'block';
  document.getElementById('mobilityExerciseList').style.display = 'block';
  document.getElementById('mobilityCompleteCard').style.display = 'none';
  
  document.getElementById('mobilityRoutineName').textContent = customRoutine.name;
  
  loadMobilityExercise(0);
  renderMobilityExerciseList();
  updateMobilityTimeRemaining();
  
  document.getElementById('mobilityPlayerCard').scrollIntoView({ behavior: 'smooth' });
}

// Compartilhar
function shareMobilityComplete() {
  const text = `🧘 Acabei de completar "${mobilityState.routine.name}" no meu app de treino! ${mobilityState.routine.exercises.length} exercícios de mobilidade 💪`;
  
  if (navigator.share) {
    navigator.share({ text: text });
  } else {
    navigator.clipboard.writeText(text);
    showToast('📋 Copiado para a área de transferência!');
  }
}

// Sons
function playMobilityBeep() {
  try {
    const ctx = new (window.AudioContext || window.webkitAudioContext)();
    const osc = ctx.createOscillator();
    const gain = ctx.createGain();
    osc.connect(gain);
    gain.connect(ctx.destination);
    osc.frequency.value = 880;
    gain.gain.value = 0.3;
    osc.start();
    osc.stop(ctx.currentTime + 0.1);
  } catch(e) {}
}

function playMobilityTransition() {
  try {
    const ctx = new (window.AudioContext || window.webkitAudioContext)();
    const osc = ctx.createOscillator();
    const gain = ctx.createGain();
    osc.connect(gain);
    gain.connect(ctx.destination);
    osc.frequency.value = 523.25;
    gain.gain.value = 0.4;
    osc.start();
    setTimeout(() => { osc.frequency.value = 659.25; }, 100);
    osc.stop(ctx.currentTime + 0.2);
  } catch(e) {}
}

function playMobilityComplete() {
  try {
    const ctx = new (window.AudioContext || window.webkitAudioContext)();
    const notes = [523.25, 659.25, 783.99, 1046.50];
    notes.forEach((freq, i) => {
      setTimeout(() => {
        const osc = ctx.createOscillator();
        const gain = ctx.createGain();
        osc.connect(gain);
        gain.connect(ctx.destination);
        osc.frequency.value = freq;
        gain.gain.value = 0.3;
        osc.start();
        osc.stop(ctx.currentTime + 0.15);
      }, i * 150);
    });
  } catch(e) {}
}

// Voz
function speakMobility(text) {
  if ('speechSynthesis' in window) {
    speechSynthesis.cancel();
    const utter = new SpeechSynthesisUtterance(text);
    utter.lang = 'pt-BR';
    utter.rate = 0.9;
    speechSynthesis.speak(utter);
  }
}

