// Puxar peso do histórico para a data selecionada
function loadWeightForDate(type) {
  const dateInput = document.getElementById(type + 'Date');
  const weightInput = document.getElementById(type + 'Weight');
  
  if (!dateInput.value) {
    showToast('Selecione uma data primeiro');
    return;
  }
  
  // Tentar puxar do histórico de peso
  try {
    const weightHistory = JSON.parse(localStorage.getItem('weightHistory') || '[]');
    const targetDate = dateInput.value;
    
    // Procurar peso exato ou mais próximo
    let foundWeight = null;
    let closestDiff = Infinity;
    
    weightHistory.forEach(entry => {
      const entryDate = entry.date.split('T')[0];
      if (entryDate === targetDate) {
        foundWeight = entry.weight;
      } else {
        const diff = Math.abs(new Date(entryDate) - new Date(targetDate));
        if (diff < closestDiff && diff < 7 * 24 * 60 * 60 * 1000) { // Dentro de 7 dias
          closestDiff = diff;
          foundWeight = entry.weight;
        }
      }
    });
    
    if (foundWeight) {
      weightInput.value = foundWeight;
      shapeData[type].weight = foundWeight;
      updateShapePreview();
      showToast('Peso carregado: ' + foundWeight + ' kg');
    } else {
      showToast('Nenhum registro de peso encontrado para essa data');
    }
  } catch (e) {
    showToast('Erro ao buscar peso');
  }
}

