function saveQuickWeight() {
  // Pega os valores do Modal
  const wVal = document.getElementById('quickWeightInput').value.replace(',', '.');
  const weight = parseFloat(wVal);
  
  const chest = parseFloat(document.getElementById('quickChestInput').value);
  const abs = parseFloat(document.getElementById('quickAbsInput').value);
  const thigh = parseFloat(document.getElementById('quickThighInput').value);
  const age = parseInt(localStorage.getItem('userAge')) || 25;

  if (!weight || weight <= 0) {
    showToast('❌ Digite um peso válido!');
    return;
  }

  // Cria o registro
  const record = {
    id: Date.now(),
    date: new Date().toISOString(),
    weight: weight,
    bf: null,
    folds: null
  };

  // Calcula BF se tiver as 3 dobras
  if (chest && abs && thigh) {
    // Reutiliza sua função calculateBF existente
    if (typeof calculateBF === 'function') {
        const bfResult = calculateBF(chest, abs, thigh, age);
        record.bf = bfResult;
        record.folds = { chest: chest, abs: abs, thigh: thigh, age: age };
        
        // Atualiza memória
        localStorage.setItem('lastChest', chest);
        localStorage.setItem('lastAbs', abs);
        localStorage.setItem('lastThigh', thigh);
    }
  }

  // Atualiza peso na memória
  localStorage.setItem('lastWeight', weight);

  // Salva no histórico
  weightHistory.unshift(record);
  saveData();

  // Atualiza a UI da aba principal de Peso (para quando você for lá, já estar atualizado)
  renderWeightHistory();
  if(typeof renderWeightChart === 'function') renderWeightChart();
  updateWeightGoalProgress();
  
  // Sincroniza os inputs da aba principal
  const mainWeightInput = document.getElementById('weightInput');
  if(mainWeightInput) mainWeightInput.value = weight;

  closeQuickNavModal();
  
  let msg = '✅ Peso registrado!';
  if (record.bf) msg += ` (BF: ${record.bf}%)`;
  showToast(msg);
}


