window.addEventListener('popstate', function(event) {
    // Cenário 1: Timer rodando + Tentativa de sair do app (hash #treino-ativo sumiu)
    // Ocorre quando estamos na aba Treino e apertamos voltar
    if (isWorkoutActive && !location.hash.includes('treino-ativo')) {
        
        // REARMA A ARMADILHA IMEDIATAMENTE (Impede a saída)
        window.history.pushState({ workoutRunning: true }, '', '#treino-ativo');

        // Lógica dos Modais de Alerta (Amarelo -> Vermelho)
        const modal1 = document.getElementById('exitWarningModal1');
        const modal2 = document.getElementById('exitWarningModal2');
        const is1Open = modal1.classList.contains('active');
        const is2Open = modal2.classList.contains('active');

        if (!is1Open && !is2Open) {
            modal1.classList.add('active');
            if (navigator.vibrate) navigator.vibrate(50);
        } else if (is1Open) {
            modal1.classList.remove('active');
            modal2.classList.add('active');
            if (navigator.vibrate) navigator.vibrate([100, 50, 100, 50, 100]);
        } else if (is2Open) {
            modal2.style.animation = 'none';
            modal2.offsetHeight; 
            modal2.style.animation = 'shake 0.4s ease';
            if (navigator.vibrate) navigator.vibrate(500);
        }
        return; // Interrompe aqui para não mudar de aba visualmente
    }

    // Cenário 2: Navegação normal entre abas
    if (event.state && event.state.tab) {
        // Se tem um estado salvo (ex: clicou em voltar estando em 'Peso')
        switchVisualTab(event.state.tab);
    } else {
        // Se voltou para o início (Root), ativa Treino
        switchVisualTab('treino');
    }
});

