function updateNutritionBars() {
    // 1. Pegar os valores atuais (que já estão na tela nos elementos dayProt, dayKcal, etc)
    // Nota: parseInt remove o "g" se houver (ex: "150g" vira 150)
    let curProt = parseInt(document.getElementById('dayProt').innerText) || 0;
    let curFat  = parseInt(document.getElementById('dayFat').innerText) || 0;
    let curCarb = parseInt(document.getElementById('dayCarbs').innerText) || 0;
    let curKcal = parseInt(document.getElementById('dayKcal').innerText) || 0;

    // 2. Pegar as metas (inputs da config)
    let metaProt = parseInt(document.getElementById('metaProt').value) || 1; // Evita divisão por zero
    let metaFat  = parseInt(document.getElementById('metaFat').value) || 1;
    let metaCarb = parseInt(document.getElementById('metaCarb').value) || 1;
    let metaKcal = parseInt(document.getElementById('metaKcal').value) || 1;

    // 3. Função auxiliar para atualizar cada barra
    const setBar = (elementId, current, max) => {
        let pct = (current / max) * 100;
        let bar = document.getElementById(elementId);
        
        // Limita visualmente a 100% (mas pode mudar cor se passar)
        bar.style.width = (pct > 100 ? 100 : pct) + "%";
        
        // SUGESTÃO: Muda cor se ultrapassar a meta (+10% de tolerância)
        if(pct > 110) {
            bar.style.filter = "brightness(0.7)"; // Escurece indicando excesso
        } else {
            bar.style.filter = "none";
        }
    };

    setBar('progBarProt', curProt, metaProt);
    setBar('progBarFat', curFat, metaFat);
    setBar('progBarCarb', curCarb, metaCarb);
    setBar('progBarKcal', curKcal, metaKcal);

    // 4. Atualizar texto de Calorias Restantes
    let remaining = metaKcal - curKcal;
    let elRemain = document.getElementById('valRemaining');
    elRemain.innerText = remaining;
    // Muda cor se negativo
    elRemain.style.color = remaining < 0 ? "var(--danger)" : "var(--success)";
}



