// Copia lista de compras
function copyShoppingList() {
  if (!selectedDietPreset) return;
  
  const diet = DIET_PRESETS[selectedDietPreset];
  
  let text = `🛒 LISTA DE COMPRAS SEMANAL\n`;
  text += `📋 ${diet.name}\n`;
  text += `━━━━━━━━━━━━━━━━━\n\n`;
  
  const sections = {};
  diet.shopping.forEach(item => {
    if (!sections[item.section]) sections[item.section] = [];
    sections[item.section].push(item);
  });
  
  Object.entries(sections).forEach(([section, items]) => {
    text += `${section}\n`;
    items.forEach(item => {
      text += `☐ ${item.item} - ${item.qty}\n`;
    });
    text += `\n`;
  });
  
  text += `━━━━━━━━━━━━━━━━━\n`;
  text += `📱 Exercise Tracker`;
  
  copyToClipboard(text);
}

// Compartilha dieta
function shareDietPreset() {
  if (!selectedDietPreset) return;
  
  const diet = DIET_PRESETS[selectedDietPreset];
  
  let text = `${diet.icon} *${diet.name}*\n`;
  text += `━━━━━━━━━━━━━━━━━\n\n`;
  text += `🎯 *${diet.kcal} kcal/dia*\n`;
  text += `🥩 Proteína: ${diet.protein}g\n`;
  text += `🍚 Carboidrato: ${diet.carbs}g\n`;
  text += `🥑 Gordura: ${diet.fat}g\n\n`;
  
  text += `📋 *Refeições:*\n`;
  diet.meals.forEach(meal => {
    text += `${meal.icon} ${meal.name} (${meal.macros.kcal} kcal)\n`;
  });
  
  text += `\n━━━━━━━━━━━━━━━━━\n`;
  text += `📱 _Exercise Tracker_`;
  
  if (navigator.share) {
    navigator.share({ title: diet.name, text: text })
      .catch(() => copyToClipboard(text));
  } else {
    copyToClipboard(text);
  }
}

