function toggleQuietHours(enabled) {
  waterQuietHours.enabled = enabled;
  saveWaterData();
  renderQuietHoursSettings();
}

function setQuietHours(start, end) {
  waterQuietHours.start = start;
  waterQuietHours.end = end;
  saveWaterData();
  showToast(`🌙 Modo silencioso: ${start} - ${end}`);
}

function isQuietHoursActive() {
  if (!waterQuietHours.enabled) return false;
  
  const now = new Date();
  const currentTime = now.getHours() * 60 + now.getMinutes();
  
  const [startH, startM] = waterQuietHours.start.split(':').map(Number);
  const [endH, endM] = waterQuietHours.end.split(':').map(Number);
  
  const startMinutes = startH * 60 + startM;
  const endMinutes = endH * 60 + endM;
  
  if (startMinutes > endMinutes) {
    return currentTime >= startMinutes || currentTime <= endMinutes;
  }
  
  return currentTime >= startMinutes && currentTime <= endMinutes;
}

