// ==================== LEMBRETES ====================

function addWaterReminder() {
  const time = document.getElementById('reminderTime').value;
  const amount = parseInt(document.getElementById('reminderAmount').value);
  
  if (!time) {
    showToast('❌ Selecione um horário');
    return;
  }
  
  if (waterReminders.some(r => r.time === time)) {
    showToast('⚠️ Já existe um lembrete neste horário');
    return;
  }
  
  waterReminders.push({
    id: Date.now().toString(),
    time: time,
    amount: amount,
    enabled: true
  });
  
  waterReminders.sort((a, b) => a.time.localeCompare(b.time));
  saveWaterData();
  renderWaterReminders();
  startWaterReminderChecker();
  
  showToast(`⏰ Lembrete às ${time} adicionado!`);
}

function removeWaterReminder(id) {
  waterReminders = waterReminders.filter(r => r.id !== id);
  saveWaterData();
  renderWaterReminders();
  showToast('🗑️ Lembrete removido');
}

function toggleWaterReminder(id) {
  const reminder = waterReminders.find(r => r.id === id);
  if (reminder) {
    reminder.enabled = !reminder.enabled;
    saveWaterData();
    renderWaterReminders();
  }
}

function setReminderPreset(preset) {
  let times = [];
  
  switch(preset) {
    case 'work':
      times = ['08:00', '10:00', '12:00', '14:00', '16:00', '18:00'];
      break;
    case 'full':
      times = ['07:00', '09:00', '11:00', '13:00', '15:00', '17:00', '19:00', '21:00'];
      break;
    case 'workout':
      times = ['06:00', '08:00', '10:00', '12:00', '15:00', '17:00', '19:00', '21:00'];
      break;
  }
  
  times.forEach(time => {
    if (!waterReminders.some(r => r.time === time)) {
      waterReminders.push({
        id: Date.now().toString() + Math.random(),
        time: time,
        amount: 250,
        enabled: true
      });
    }
  });
  
  waterReminders.sort((a, b) => a.time.localeCompare(b.time));
  saveWaterData();
  renderWaterReminders();
  startWaterReminderChecker();
  showToast('⏰ Lembretes configurados!');
}

function clearAllReminders() {
  if (!confirm('Remover todos os lembretes?')) return;
  waterReminders = [];
  saveWaterData();
  renderWaterReminders();
  showToast('🗑️ Todos os lembretes removidos');
}

function renderWaterReminders() {
  const container = document.getElementById('remindersList');
  if (!container) return;
  
  if (waterReminders.length === 0) {
    container.innerHTML = '<div style="text-align:center; padding:20px; color:var(--text-muted); font-size:12px;">Nenhum lembrete configurado</div>';
    return;
  }
  
  const now = new Date();
  const currentTime = now.getHours().toString().padStart(2, '0') + ':' + now.getMinutes().toString().padStart(2, '0');
  
  container.innerHTML = waterReminders.map(r => {
    const isActive = r.enabled && r.time > currentTime;
    return `
      <div class="water-reminder-item ${isActive ? 'active' : ''}" style="${!r.enabled ? 'opacity:0.5;' : ''}">
        <span class="reminder-time">${r.time}</span>
        <span class="reminder-amount">${r.amount}ml</span>
        <label style="display:flex; align-items:center; gap:5px; cursor:pointer; margin-left:auto;">
          <input type="checkbox" ${r.enabled ? 'checked' : ''} onchange="toggleWaterReminder('${r.id}')" style="width:18px; height:18px; accent-color:var(--success);"/>
        </label>
        <button class="reminder-delete" onclick="removeWaterReminder('${r.id}')">✕</button>
      </div>
    `;
  }).join('');
}

function startWaterReminderChecker() {
  if (waterReminderInterval) clearInterval(waterReminderInterval);
  waterReminderInterval = setInterval(checkWaterReminders, 60000);
}

function checkWaterReminders() {
  if (!('Notification' in window)) return;
  if (Notification.permission !== 'granted') return;
  if (isQuietHoursActive()) return;
  
  const now = new Date();
  const currentTime = now.getHours().toString().padStart(2, '0') + ':' + now.getMinutes().toString().padStart(2, '0');
  
  waterReminders.forEach(reminder => {
    if (reminder.enabled && reminder.time === currentTime) {
      const todayKey = `waterReminder_${reminder.id}_${getLocalDateString()}`; // ← ALTERADO
      if (!localStorage.getItem(todayKey)) {
        showWaterNotification(reminder);
        localStorage.setItem(todayKey, 'true');
      }
    }
  });
}

function showWaterNotification(reminder) {
  if (Notification.permission === 'granted') {
    const total = getTodayWaterTotal();
    const remaining = Math.max(0, waterGoal - total);
    
    new Notification('💧 Hora de beber água!', {
      body: `Beba ${reminder.amount}ml agora.\nHoje: ${total}ml / ${waterGoal}ml\nFaltam: ${remaining}ml`,
      icon: '💧',
      tag: 'water-reminder',
      requireInteraction: true
    });
  }
}

function requestWaterNotificationPermission() {
  if (!('Notification' in window)) {
    showToast('❌ Notificações não suportadas');
    return;
  }
  
  Notification.requestPermission().then(permission => {
    renderWaterNotificationStatus();
    if (permission === 'granted') {
      showToast('✅ Notificações ativadas!');
      startWaterReminderChecker();
    }
  });
}

