function updateAbaultSummary() {
  let maxStreak = 0;
  let totalItems = 0;
  let cleanItems = 0;
  
  ABAULT_DEFAULT_ITEMS.forEach(item => {
    const data = abaultData[item.id] || { history: [] };
    
    // Só conta se tiver nome (para customizados) ou for padrão
    if (!item.isCustom || data.customName) {
      if (data.history.length > 0) {
        totalItems++;
        const days = getDaysSinceAbault(data.history[0].date);
        if (days > maxStreak) maxStreak = days;
        if (days >= 7) cleanItems++;
      }
    }
  });
  
  const el1 = document.getElementById('abaultTotalDays');
  const el2 = document.getElementById('abaultTotalItems');
  const el3 = document.getElementById('abaultCleanItems');
  
  if (el1) el1.textContent = maxStreak;
  if (el2) el2.textContent = totalItems;
  if (el3) el3.textContent = cleanItems;
}

