let abaultCurrentFilter = 'all';
let abaultCurrentSort = 'default';

// Renderiza os destaques
function renderAbaultHighlights() {
  const container = document.getElementById('abaultHighlights');
  if (!container) return;
  
  let bestStreak = { item: null, days: 0 };
  let worstItem = { item: null, days: Infinity };
  let mostConsumed = { item: null, count: 0 };
  let totalCleanDays = 0;
  let neverCount = 0;
  
  ABAULT_DEFAULT_ITEMS.forEach(item => {
    if (item.isCustom) return;
    
    const data = abaultData[item.id] || { history: [], neverConsumed: false };
    
    if (data.neverConsumed) {
      neverCount++;
      return;
    }
    
    if (data.history && data.history.length > 0) {
      const days = getDaysSinceAbault(data.history[0].date);
      totalCleanDays += days;
      
      if (days > bestStreak.days) {
        bestStreak = { item: item, days: days };
      }
      
      if (days < worstItem.days) {
        worstItem = { item: item, days: days };
      }
      
      if (data.history.length > mostConsumed.count) {
        mostConsumed = { item: item, count: data.history.length };
      }
    }
  });
  
  let html = '<div class="abault-highlights-grid">';
  
  // Melhor Streak
  if (bestStreak.item) {
    html += `
      <div class="abault-highlight-card best">
        <div class="highlight-icon">${bestStreak.item.icon}</div>
        <div class="highlight-info">
          <div class="highlight-value">${bestStreak.days} dias</div>
          <div class="highlight-label">🏆 Melhor Streak</div>
          <div class="highlight-name">${bestStreak.item.name}</div>
        </div>
      </div>
    `;
  }
  
  // Precisa de Atenção
  if (worstItem.item && worstItem.days < 7) {
    html += `
      <div class="abault-highlight-card danger">
        <div class="highlight-icon">${worstItem.item.icon}</div>
        <div class="highlight-info">
          <div class="highlight-value">${worstItem.days} dias</div>
          <div class="highlight-label">⚠️ Atenção</div>
          <div class="highlight-name">${worstItem.item.name}</div>
        </div>
      </div>
    `;
  }
  
  // Mais Consumido
  if (mostConsumed.item && mostConsumed.count > 1) {
    html += `
      <div class="abault-highlight-card warning">
        <div class="highlight-icon">${mostConsumed.item.icon}</div>
        <div class="highlight-info">
          <div class="highlight-value">${mostConsumed.count}x</div>
          <div class="highlight-label">📊 Mais Registrado</div>
          <div class="highlight-name">${mostConsumed.item.name}</div>
        </div>
      </div>
    `;
  }
  
  // Nunca Consumidos
  if (neverCount > 0) {
    html += `
      <div class="abault-highlight-card gold">
        <div class="highlight-icon">🏅</div>
        <div class="highlight-info">
          <div class="highlight-value">${neverCount}</div>
          <div class="highlight-label">✨ Nunca Consumidos</div>
          <div class="highlight-name">Itens zerados</div>
        </div>
      </div>
    `;
  }
  
  html += '</div>';
  
  // Verificar marcos e mostrar celebração
  if (bestStreak.days >= 7) {
    const milestone = getMilestone(bestStreak.days);
    if (milestone) {
      html += `
        <div class="abault-milestone">
          <span class="milestone-icon">${milestone.icon}</span>
          <span class="milestone-text">${milestone.text} sem ${bestStreak.item.name}!</span>
        </div>
      `;
    }
  }
  
  container.innerHTML = html;
}

function getMilestone(days) {
  if (days >= 365) return { icon: '👑', text: '1 ANO' };
  if (days >= 180) return { icon: '💎', text: '6 MESES' };
  if (days >= 90) return { icon: '🏆', text: '3 MESES' };
  if (days >= 60) return { icon: '🥇', text: '2 MESES' };
  if (days >= 30) return { icon: '🎖️', text: '1 MÊS' };
  if (days >= 14) return { icon: '⭐', text: '2 SEMANAS' };
  if (days >= 7) return { icon: '✨', text: '1 SEMANA' };
  return null;
}

// Filtrar itens
function filterAbaultItems(filter) {
  abaultCurrentFilter = filter;
  
  // Atualiza botões
  document.querySelectorAll('.abault-filter-btn').forEach(btn => {
    btn.classList.toggle('active', btn.dataset.filter === filter);
  });
  
  // Aplica filtro visual
  document.querySelectorAll('.abault-item').forEach(item => {
    const itemId = item.dataset.id;
    const data = abaultData[itemId] || { history: [], neverConsumed: false };
    const isNever = data.neverConsumed === true;
    const daysSince = data.history && data.history.length > 0 
      ? getDaysSinceAbault(data.history[0].date) 
      : null;
    
    let show = true;
    
    switch(filter) {
      case 'danger':
        show = !isNever && daysSince !== null && daysSince < 7;
        break;
      case 'clean':
        show = !isNever && daysSince !== null && daysSince >= 7;
        break;
      case 'never':
        show = isNever;
        break;
      case 'all':
      default:
        show = true;
    }
    
    item.style.display = show ? '' : 'none';
  });
  
  // Mostra mensagem se não tiver itens
  showEmptyFilterMessage(filter);
}

function showEmptyFilterMessage(filter) {
  const grids = ['abaultFoodGrid', 'abaultOtherGrid', 'abaultCustomGrid'];
  
  grids.forEach(gridId => {
    const grid = document.getElementById(gridId);
    if (!grid) return;
    
    const visibleItems = grid.querySelectorAll('.abault-item:not([style*="display: none"])');
    let emptyMsg = grid.querySelector('.abault-filter-empty');
    
    if (visibleItems.length === 0) {
      if (!emptyMsg) {
        emptyMsg = document.createElement('div');
        emptyMsg.className = 'abault-filter-empty';
        grid.appendChild(emptyMsg);
      }
      
      const messages = {
        danger: '✅ Nenhum item em risco!',
        clean: '😅 Nenhum item limpo há 7+ dias',
        never: '📝 Nenhum item marcado como "nunca"'
      };
      
      emptyMsg.textContent = messages[filter] || '';
      emptyMsg.style.display = 'block';
    } else if (emptyMsg) {
      emptyMsg.style.display = 'none';
    }
  });
}

// Ordenar itens
function sortAbaultItems(sortBy) {
  abaultCurrentSort = sortBy;
  
  const grids = ['abaultFoodGrid', 'abaultOtherGrid', 'abaultCustomGrid'];
  
  grids.forEach(gridId => {
    const grid = document.getElementById(gridId);
    if (!grid) return;
    
    const items = Array.from(grid.querySelectorAll('.abault-item'));
    
    items.sort((a, b) => {
      const idA = a.dataset.id;
      const idB = b.dataset.id;
      const dataA = abaultData[idA] || { history: [] };
      const dataB = abaultData[idB] || { history: [] };
      const itemA = ABAULT_DEFAULT_ITEMS.find(i => i.id === idA);
      const itemB = ABAULT_DEFAULT_ITEMS.find(i => i.id === idB);
      
      const daysA = dataA.history && dataA.history.length > 0 
        ? getDaysSinceAbault(dataA.history[0].date) : -1;
      const daysB = dataB.history && dataB.history.length > 0 
        ? getDaysSinceAbault(dataB.history[0].date) : -1;
      
      switch(sortBy) {
        case 'streak-desc':
          return daysB - daysA;
        case 'streak-asc':
          return daysA - daysB;
        case 'count-desc':
          return (dataB.history?.length || 0) - (dataA.history?.length || 0);
        case 'count-asc':
          return (dataA.history?.length || 0) - (dataB.history?.length || 0);
        case 'alpha':
          const nameA = itemA?.name || '';
          const nameB = itemB?.name || '';
          return nameA.localeCompare(nameB);
        default:
          return 0;
      }
    });
    
    items.forEach(item => grid.appendChild(item));
  });
}

// Compartilhar conquista
function shareAbaultAchievement(itemId) {
  const item = ABAULT_DEFAULT_ITEMS.find(i => i.id === itemId);
  const data = abaultData[itemId] || { history: [] };
  
  if (!item || !data.history || data.history.length === 0) return;
  
  const days = getDaysSinceAbault(data.history[0].date);
  const name = item.isCustom ? (data.customName || 'Item') : item.name;
  const milestone = getMilestone(days);
  
  let text = `🎯 CONQUISTA DESBLOQUEADA!\n\n`;
  text += `${item.icon} ${name}\n`;
  text += `✅ ${days} dias sem consumir!\n`;
  if (milestone) {
    text += `${milestone.icon} ${milestone.text} limpo!\n`;
  }
  text += `\n💪 Acompanhando meus hábitos com Exercise Tracker`;
  
  if (navigator.share) {
    navigator.share({
      title: 'Minha Conquista',
      text: text
    }).catch(() => {});
  } else {
    copyToClipboard(text);
  }
}

// Atualizar renderAbaultItem para incluir botão de compartilhar
function renderAbaultItem(item, data) {
  const history = data.history || [];
  const lastEntry = history.length > 0 ? history[0] : null;
  const daysSince = lastEntry ? getDaysSinceAbault(lastEntry.date) : null;
  const isNeverConsumed = data.neverConsumed === true;
  
  let statusClass = '';
  if (isNeverConsumed) {
    statusClass = 'never';
  } else if (daysSince !== null) {
    if (daysSince < 3) statusClass = 'recent';
    else if (daysSince >= 7) statusClass = 'good';
  }
  
  const total = history.length;
  const avgText = calcAbaultAverage(history);
  const displayName = item.isCustom ? (data.customName || 'Clique para nomear') : item.name;
  
  let streakHtml = '';
  if (isNeverConsumed) {
    streakHtml = '<span class="abault-streak never">🏆 Nunca!</span>';
  } else if (daysSince !== null) {
    streakHtml = getAbaultStreakBadge(daysSince);
  }
  
  let lastText = 'Nunca registrado';
  if (isNeverConsumed) {
    lastText = '<strong style="color:var(--success);">Nunca consumi isso!</strong>';
  } else if (lastEntry) {
    const date = new Date(lastEntry.date);
    lastText = `<strong>${daysSince}</strong> dias atrás (${date.toLocaleDateString('pt-BR')})`;
  }
  
  // Botão de compartilhar (só aparece se tiver streak >= 7)
  const showShare = !isNeverConsumed && daysSince !== null && daysSince >= 7;
  
  return `
    <div class="abault-item ${statusClass}" data-id="${item.id}">
      <div class="abault-header">
        <div class="abault-icon">${item.icon}</div>
        <div class="abault-info">
          ${item.isCustom ? 
            `<input type="text" class="abault-name-input" placeholder="Nome do item..." 
              value="${data.customName || ''}" 
              onchange="updateAbaultCustomName('${item.id}', this.value)">` :
            `<div class="abault-name">${displayName}</div>`
          }
          <div class="abault-last">${lastText}</div>
        </div>
        ${streakHtml}
      </div>
      
      <div class="abault-stats">
        <div class="abault-stat">Total: <strong>${total}x</strong></div>
        <div class="abault-stat">Média: <strong>${avgText}</strong></div>
        ${showShare ? `
          <button class="abault-share-btn" onclick="shareAbaultAchievement('${item.id}')" title="Compartilhar conquista">
            📤
          </button>
        ` : ''}
      </div>
      
      <div class="abault-actions">
        <button class="abault-mark-btn" onclick="markAbaultNow('${item.id}')" ${isNeverConsumed ? 'disabled style="opacity:0.5;"' : ''}>
          ⚡ Marcar Hoje
        </button>
        <button class="abault-history-btn" onclick="openAbaultHistory('${item.id}')" title="Ver histórico">
          📜 ${total}
        </button>
      </div>
      
      <div class="abault-extra-actions">
        <button class="abault-extra-btn" onclick="openAbaultPastModal('${item.id}')" ${isNeverConsumed ? 'disabled' : ''}>
          📅 Marcar Data
        </button>
        <button class="abault-extra-btn ${isNeverConsumed ? 'active' : ''}" onclick="toggleAbaultNever('${item.id}')">
          ${isNeverConsumed ? '✅ Nunca Consumi' : '🚫 Nunca Consumi'}
        </button>
      </div>
    </div>
  `;
}

// Atualizar renderAbaultTab para incluir os destaques
function renderAbaultTab() {
  const foodGrid = document.getElementById('abaultFoodGrid');
  const otherGrid = document.getElementById('abaultOtherGrid');
  const customGrid = document.getElementById('abaultCustomGrid');
  
  if (!foodGrid) return;
  
  foodGrid.innerHTML = '';
  otherGrid.innerHTML = '';
  customGrid.innerHTML = '';
  
  ABAULT_DEFAULT_ITEMS.forEach(item => {
    const data = abaultData[item.id] || { history: [], customName: '' };
    const html = renderAbaultItem(item, data);
    
    if (item.category === 'food') {
      foodGrid.innerHTML += html;
    } else if (item.category === 'other') {
      otherGrid.innerHTML += html;
    } else {
      customGrid.innerHTML += html;
    }
  });
  
  updateAbaultSummary();
  renderAbaultQuickGrid();
  renderAbaultHighlights();
  
  // Reaplicar filtro e ordenação atuais
  if (abaultCurrentFilter !== 'all') {
    filterAbaultItems(abaultCurrentFilter);
  }
  if (abaultCurrentSort !== 'default') {
    sortAbaultItems(abaultCurrentSort);
  }
}


